/**
 * AgentCard Component
 *
 * Specialized card component for displaying AI agent information with avatar,
 * title, description, and optional actions. Supports drag & drop functionality.
 *
 * @example
 * ```tsx
 * <AgentCard
 *   id="agent-1"
 *   avatar="/agent-avatar.png"
 *   title="Customer Support Agent"
 *   description="Handles customer inquiries and support tickets"
 *   timestamp="Updated 2 hours ago"
 *   onClick={() => console.log('Agent clicked')}
 *   onEdit={() => console.log('Edit agent')}
 *   onDelete={() => console.log('Delete agent')}
 * />
 * ```
 */

import React, { useState } from "react";
import { Edit, Trash2, MoreVertical, Folder } from "lucide-react";
import type { AgentCardProps } from "./AgentCard.types";
import { Card } from "../Card";
import { Avatar } from "../Avatar";
import { Button } from "../Button";
import { Dropdown } from "../Dropdown";
import styles from "./AgentCard.module.css";

export const AgentCard: React.FC<AgentCardProps> = ({
  id,
  avatar,
  title,
  description,
  timestamp,
  isMultiAgent = false,
  onClick,
  onEdit,
  onDelete,
  availableFolders,
  onMoveToFolder,
  draggable = false,
  isDragging = false,
  className,
  ...rest
}) => {
  const isImageAvatar = typeof avatar === "string";
  const [isDraggingState, setIsDraggingState] = useState(isDragging);

  const classNames = [
    styles.agentCard,
    onClick && styles.clickable,
    draggable && styles.draggable,
    isDraggingState && styles.dragging,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  const handleClick = () => {
    onClick?.();
  };

  // Drag and drop handlers
  const handleDragStart = (e: React.DragEvent<HTMLDivElement>) => {
    if (!draggable) return;
    setIsDraggingState(true);
    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", id);
    e.dataTransfer.setData("application/json", JSON.stringify({ id, title }));
  };

  const handleDragEnd = () => {
    setIsDraggingState(false);
  };

  const handleDragOver = (e: React.DragEvent<HTMLDivElement>) => {
    if (!draggable) return;
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  };

  const dropdownItems = [
    ...(onEdit
      ? [
          {
            id: "edit",
            label: "Edit",
            icon: <Edit size={16} />,
            onClick: onEdit,
          },
        ]
      : []),
    ...(onDelete
      ? [
          {
            id: "delete",
            label: "Delete",
            icon: <Trash2 size={16} />,
            danger: true,
            onClick: onDelete,
          },
        ]
      : []),
    ...(onMoveToFolder && availableFolders && availableFolders.length > 0
      ? [
          { id: "_move-sep", label: "", separator: true },
          ...availableFolders.map((f) => ({
            id: `move-to-${f.id}`,
            label: f.title,
            icon: <Folder size={16} />,
            onClick: () => onMoveToFolder(f.id),
          })),
        ]
      : []),
  ];

  return (
    <Card
      variant="base"
      interactive={!!onClick}
      className={classNames}
      onClick={handleClick}
      draggable={draggable}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      onDragOver={handleDragOver}
      data-agent-id={id}
      data-dragging={isDraggingState}
      {...rest}
    >
      {/* Header: Avatar + Badge + Actions Menu */}
      <Card.Header>
        <div className={styles.headerContent}>
          {isImageAvatar ? (
            <Avatar src={avatar} alt={title} size="lg" />
          ) : (
            <div className={styles.iconAvatar}>{avatar}</div>
          )}

          <div className={styles.headerRight}>
            {/* Multi badge — only shown for multi-agents */}
            {isMultiAgent && <span className={styles.multiBadge}>Multi</span>}

            {/* Actions Menu via Dropdown component */}
            {dropdownItems.length > 0 && (
              <Dropdown
                trigger={
                  <Button
                    variant="ghost"
                    iconOnly
                    size="sm"
                    icon={<MoreVertical size={20} />}
                    aria-label="Actions"
                    onDragStart={(e) => e.stopPropagation()}
                  />
                }
                items={dropdownItems}
                placement="bottom-end"
                minWidth={140}
              />
            )}
          </div>
        </div>
      </Card.Header>

      {/* Body: Title + Description */}
      <Card.Body>
        <h3 className={styles.title}>{title}</h3>
        <p className={styles.description}>{description ?? ""}</p>
      </Card.Body>

      {/* Footer: Timestamp only */}
      {timestamp && (
        <Card.Footer>
          <time className={styles.timestamp}>{timestamp}</time>
        </Card.Footer>
      )}
    </Card>
  );
};

AgentCard.displayName = "AgentCard";

/**
 * AgentCard Component Types
 *
 * Type definitions for the AgentCard component - specialized card for AI agents.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * @deprecated Status badges are no longer displayed. Use `isMultiAgent` instead.
 */
export type AgentStatus = "draft" | "published" | "archived";

/**
 * AgentCard component props
 *
 * @example
 * ```tsx
 * <AgentCard
 *   id="agent-1"
 *   avatar="/agent-avatar.png"
 *   title="UVM Agent"
 *   description="Agent for Universidad Virtual de México"
 *   timestamp="Hace 2 horas"
 *   isMultiAgent
 * />
 * ```
 */
export interface AgentCardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Unique agent identifier
   */
  id: string;

  /**
   * Agent avatar (image URL or ReactNode icon)
   */
  avatar: string | ReactNode;

  /**
   * Agent title/name
   */
  title: string;

  /**
   * Agent description (max 2 lines with ellipsis). Space is always reserved
   * for 2 lines even when empty, ensuring consistent card height in grids.
   */
  description?: string;

  /**
   * Timestamp text (e.g., "Hace 2 horas")
   */
  timestamp?: string;

  /**
   * When true, shows a "Multi" badge indicating this is a multi-agent.
   * Only multi-agents show a badge; single agents show nothing.
   */
  isMultiAgent?: boolean;

  /**
   * Card click handler
   */
  onClick?: () => void;

  /**
   * Edit action handler
   */
  onEdit?: () => void;

  /**
   * Delete action handler
   */
  onDelete?: () => void;

  /**
   * Enable drag & drop functionality
   * @default false
   */
  draggable?: boolean;

  /**
   * Currently being dragged
   * @internal
   */
  isDragging?: boolean;

  /**
   * Available folders the agent can be moved to
   */
  availableFolders?: Array<{ id: string; title: string }>;

  /**
   * Called when user selects a folder to move this agent
   */
  onMoveToFolder?: (folderId: string) => void;
}

/**
 * AgentCard Component Styles
 *
 * Responsive card with CSS Grid layout for agent information.
 * Uses semantic tokens for AI-first compliance (100%).
 */

.agentCard {
  position: relative;
  height: 100%; /* Fill grid cell so all cards in a row share the same height */
  transition:
    transform var(--transition-normal),
    box-shadow var(--transition-normal);
  will-change: transform, box-shadow;
}

.agentCard.clickable {
  cursor: pointer;
}

.agentCard.draggable {
  cursor: grab;
  transition:
    transform var(--transition-normal),
    box-shadow var(--transition-normal),
    opacity var(--transition-fast);
}

.agentCard.draggable:active {
  cursor: grabbing;
}

.agentCard.dragging {
  opacity: 0.5;
  cursor: grabbing;
  transform: scale(0.98);
  box-shadow: var(--shadow-xl);
}

/* Hover state — mode-aware lift with neutral shadow */
.agentCard.clickable:hover,
.agentCard.draggable:hover {
  transform: translateY(var(--mode-hover-lift));
  box-shadow: var(--shadow-lg);
}

.agentCard.dragging:hover {
  transform: scale(0.98);
  box-shadow: var(--shadow-xl);
}

/* Header Content - Avatar + Badge/Status + Actions — always horizontal */
.headerContent {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: space-between;
  gap: var(--spacing-3);
}

/* Icon Avatar (non-image avatar) - Circular, fixed 48px */
.iconAvatar {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--spacing-12); /* 48px */
  height: var(--spacing-12); /* 48px */
  border-radius: var(--radius-full);
  background-color: var(--surface-subtle);
  color: var(--text-secondary);
  flex-shrink: 0;
}

/* Header Right - Badge/Status + Actions Menu */
.headerRight {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
}

/* Multi Badge — shown only for multi-agent cards */
.multiBadge {
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--radius-full);
  font-family: var(--font-secondary);
  font-size: var(--font-size-10);
  font-weight: var(--font-weight-semibold);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var(--interactive-primary);
  border: 1px solid var(--interactive-primary);
  background-color: var(--surface-base);
  white-space: nowrap;
  flex-shrink: 0;
}

/* Title in Body — DM Sans (font-secondary) */
.title {
  font-family: var(--font-secondary);
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  margin: 0 0 var(--spacing-2) 0;
  line-height: 1.4;
  text-align: left;
}

/* Description in Card.Body */
.description {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  color: var(--text-secondary);
  line-height: 1.5;
  margin: 0;

  /* Always reserve exactly 2 lines — keeps card height uniform in grids */
  min-height: calc(1.5em * 2);

  /* Ellipsis after 2 lines */
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
}

/* Timestamp in Footer */
.timestamp {
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  display: block;
}

/* Focus states for accessibility */
.agentCard.clickable:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  .agentCard {
    transition: none;
  }

  .agentCard:hover {
    transform: none;
  }
}

/**
 * AgentCard Component Exports
 *
 * Exports for the AgentCard component and its types.
 */

export { AgentCard } from "./AgentCard";
export type { AgentCardProps, AgentStatus } from "./AgentCard.types";
