/**
 * AgentFolder Section
 *
 * Collapsible folder section for organizing AI agents in a grid layout.
 * Built as a thin wrapper over the generic CollapsibleFolder component.
 *
 * Supports native HTML5 drag & drop with visual feedback (drop target highlighting and insertion indicators),
 * sorting, and folder management actions.
 *
 * ## Drag & Drop Features
 * - **Drop target visual state**: Folder highlights with background color and border when dragging agents over it
 * - **Insertion indicator**: Dynamic blue line shows exactly where the agent will be inserted
 * - **Automatic state cleanup**: Visual feedback clears when drag ends or user leaves folder
 * - **Empty folder support**: Shows centered insertion line for empty folders
 * - **Collapsed folder support**: Accepts drops even when folder is collapsed
 *
 * ## Synchronization
 * AgentFolder integrates with parent container (@dnd-kit in AgentWorkspace) via callbacks:
 * - `onDragEnter`: Notifies parent when drag enters this folder
 * - `onDragLeaveFolder`: Notifies parent when drag leaves this folder
 *
 * ## Extending
 * If you need to organize items other than agents, use the generic `CollapsibleFolder` component directly:
 * ```tsx
 * // For organizing files, documents, or custom item types
 * <CollapsibleFolder
 *   items={files}
 *   itemLabel="File"
 *   itemLabelPlural="Files"
 *   renderItem={(file) => <FileCard {...file} />}
 *   {...otherProps}
 * />
 * ```
 *
 * @example
 * ```tsx
 * <AgentFolder
 *   id="folder-1"
 *   title="Customer Support Agents"
 *   agentCount={5}
 *   agents={agentsList}
 *   defaultExpanded={true}
 *   sortOptions={[
 *     { label: 'Most Recent', value: 'recent' },
 *     { label: 'Alphabetical', value: 'alpha' }
 *   ]}
 *   selectedSort="recent"
 *   onSortChange={(value) => console.log('Sort:', value)}
 *   onDrop={(agentId, folderId, index) => {
 *     moveAgent(agentId, folderId, index);
 *   }}
 *   onDragEnter={() => setActiveDropTarget(id)}
 *   onDragLeaveFolder={() => setActiveDropTarget(null)}
 * />
 * ```
 */

import React from "react";
import { CollapsibleFolder } from "../CollapsibleFolder";
import { AgentCard } from "../../components/AgentCard";
import type { AgentFolderProps } from "./AgentFolder.types";

export const AgentFolder: React.FC<AgentFolderProps> = ({
  agents,
  agentCount,
  ...rest
}) => (
  <CollapsibleFolder
    {...rest}
    items={agents}
    itemCount={agentCount}
    itemLabel="Agente"
    itemLabelPlural="Agentes"
    emptyText="No hay agentes en esta carpeta"
    sortLabel="Ordenar"
    renderItem={(agent) => <AgentCard {...agent} />}
  />
);

AgentFolder.displayName = "AgentFolder";

/**
 * AgentFolder Section Types
 *
 * Type definitions for the AgentFolder section - collapsible folder for organizing AI agents.
 */

import type { HTMLAttributes } from "react";
import type { AgentCardProps } from "../../components/AgentCard";

/**
 * Sort option for agent ordering
 */
export interface SortOption {
  /**
   * Display label
   */
  label: string;

  /**
   * Internal value
   */
  value: string;
}

/**
 * AgentFolder component props
 *
 * @example
 * ```tsx
 * <AgentFolder
 *   id="folder-1"
 *   title="Agentes postgrado"
 *   agentCount={2}
 *   agents={[...]}
 *   defaultExpanded={true}
 *   sortOptions={[
 *     { label: 'Más reciente', value: 'recent' },
 *     { label: 'Alfabético', value: 'alpha' }
 *   ]}
 * />
 * ```
 */
export interface AgentFolderProps extends Omit<
  HTMLAttributes<HTMLDivElement>,
  "onDrop"
> {
  /**
   * Unique folder identifier
   */
  id: string;

  /**
   * Folder title/name
   */
  title: string;

  /**
   * Number of agents in folder
   */
  agentCount: number;

  /**
   * Array of agents to display
   */
  agents: AgentCardProps[];

  /**
   * Default expanded state
   * @default true
   */
  defaultExpanded?: boolean;

  /**
   * Available sort options
   */
  sortOptions?: SortOption[];

  /**
   * Currently selected sort value
   */
  selectedSort?: string;

  /**
   * Sort option change handler
   */
  onSortChange?: (value: string) => void;

  /**
   * Drop handler for drag & drop
   * @param agentId - ID of the agent being dropped
   * @param folderId - ID of the target folder
   * @param index - Position index where the agent will be inserted (optional, defaults to end)
   */
  onDrop?: (agentId: string, folderId: string, index?: number) => void;

  /**
   * Rename folder handler
   */
  onFolderEdit?: () => void;

  /**
   * Delete folder handler
   */
  onFolderDelete?: () => void;

  /**
   * Invite participants to folder handler
   */
  onFolderInvite?: () => void;

  /**
   * Is this folder currently a valid drop target
   * @internal
   */
  isDropTarget?: boolean;

  /**
   * Briefly set to true after a successful drop for micro-feedback
   * @internal
   */
  isDropCompleted?: boolean;

  /**
   * Callback when drag enters folder (for syncing @dnd-kit state)
   * @internal
   */
  onDragEnter?: () => void;

  /**
   * Callback when drag leaves folder (for syncing @dnd-kit state)
   * @internal
   */
  onDragLeaveFolder?: () => void;
}

/**
 * AgentFolder Section Exports
 *
 * Exports for the AgentFolder section and its types.
 */

export { AgentFolder } from "./AgentFolder";
export type { AgentFolderProps, SortOption } from "./AgentFolder.types";
