/**
 * AgentFolder Section
 *
 * Collapsible folder section for organizing AI agents in a grid layout.
 * Supports drag & drop, sorting, and folder management actions.
 *
 * @example
 * ```tsx
 * <AgentFolder
 *   id="folder-1"
 *   title="Customer Support Agents"
 *   agentCount={5}
 *   agents={agentsList}
 *   defaultExpanded={true}
 *   sortOptions={[
 *     { label: 'Most Recent', value: 'recent' },
 *     { label: 'Alphabetical', value: 'alpha' }
 *   ]}
 *   selectedSort="recent"
 *   onSortChange={(value) => console.log('Sort:', value)}
 * />
 * ```
 */

import React, { useState } from 'react';
import type { AgentFolderProps } from './AgentFolder.types';
import { Collapsible } from '../../components/Collapsible';
import { Dropdown } from '../../components/Dropdown';
import { Badge } from '../../components/Badge';
import { AgentCard } from '../../components/AgentCard';
import { ChevronDown, MoreVertical } from 'lucide-react';
import styles from './AgentFolder.module.css';

export const AgentFolder: React.FC<AgentFolderProps> = ({
  id,
  title,
  agentCount,
  agents,
  defaultExpanded = true,
  sortOptions,
  selectedSort,
  onSortChange,
  onDrop,
  onFolderEdit,
  onFolderDelete,
  isDropTarget = false,
  className,
  ...rest
}) => {
  const [isExpanded, setIsExpanded] = useState(defaultExpanded);

  const handleDragOver = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    const agentId = e.dataTransfer.getData('agentId');
    if (agentId && onDrop) {
      onDrop(agentId, id);
    }
  };

  const handleDragStart = (e: React.DragEvent<HTMLDivElement>, agentId: string) => {
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('agentId', agentId);
  };

  // Build sort dropdown items
  const sortDropdownItems = sortOptions?.map((option) => ({
    id: option.value,
    label: option.label,
    onClick: () => onSortChange?.(option.value),
  }));

  // Build folder actions dropdown
  const folderActionsItems = [
    ...(onFolderEdit
      ? [
          {
            id: 'edit',
            label: 'Editar carpeta',
            onClick: onFolderEdit,
          },
        ]
      : []),
    ...(onFolderDelete
      ? [
          {
            id: 'delete',
            label: 'Eliminar carpeta',
            danger: true,
            onClick: onFolderDelete,
          },
        ]
      : []),
  ];

  const classNames = [
    styles.folder,
    isDropTarget && styles.dropTarget,
    className,
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <div
      className={classNames}
      data-folder-id={id}
      data-drop-active={isDropTarget}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
      {...rest}
    >
      <Collapsible open={isExpanded} onOpenChange={setIsExpanded}>
        {/* Folder Header */}
        <div className={styles.header}>
          <Collapsible.Trigger className={styles.trigger}>
            <ChevronDown
              size={20}
              className={`${styles.chevron} ${isExpanded ? styles.expanded : ''}`}
            />
            <h2 className={styles.title}>{title}</h2>
            <Badge variant="neutral" className={styles.badge}>
              {agentCount} {agentCount === 1 ? 'Agente' : 'Agentes'}
            </Badge>
          </Collapsible.Trigger>

          <div className={styles.actions}>
            {/* Sort dropdown */}
            {sortOptions && sortOptions.length > 0 && (
              <Dropdown
                trigger={
                  <button className={styles.sortButton} aria-label="Sort agents">
                    {sortOptions.find((opt) => opt.value === selectedSort)?.label ||
                      'Ordenar'}
                    <ChevronDown size={16} />
                  </button>
                }
                items={sortDropdownItems}
                placement="bottom-end"
              />
            )}

            {/* Folder actions menu */}
            {folderActionsItems.length > 0 && (
              <Dropdown
                trigger={
                  <button
                    className={styles.menuButton}
                    aria-label="Folder actions"
                  >
                    <MoreVertical size={20} />
                  </button>
                }
                items={folderActionsItems}
                placement="bottom-end"
              />
            )}
          </div>
        </div>

        {/* Agent Cards Grid */}
        <Collapsible.Content>
          <div className={styles.content}>
            {agents.length === 0 ? (
              <div className={styles.emptyState}>
                <p className={styles.emptyText}>
                  No hay agentes en esta carpeta
                </p>
              </div>
            ) : (
              <div className={styles.grid}>
                {agents.map((agent) => (
                  <div
                    key={agent.id}
                    draggable={agent.draggable}
                    onDragStart={(e) => handleDragStart(e, agent.id)}
                  >
                    <AgentCard {...agent} />
                  </div>
                ))}
              </div>
            )}
          </div>
        </Collapsible.Content>
      </Collapsible>
    </div>
  );
};

AgentFolder.displayName = 'AgentFolder';

/**
 * AgentFolder Section Types
 *
 * Type definitions for the AgentFolder section - collapsible folder for organizing AI agents.
 */

import type { HTMLAttributes } from 'react';
import type { AgentCardProps } from '../../components/AgentCard';

/**
 * Sort option for agent ordering
 */
export interface SortOption {
  /**
   * Display label
   */
  label: string;

  /**
   * Internal value
   */
  value: string;
}

/**
 * AgentFolder component props
 *
 * @example
 * ```tsx
 * <AgentFolder
 *   id="folder-1"
 *   title="Agentes postgrado"
 *   agentCount={2}
 *   agents={[...]}
 *   defaultExpanded={true}
 *   sortOptions={[
 *     { label: 'Más reciente', value: 'recent' },
 *     { label: 'Alfabético', value: 'alpha' }
 *   ]}
 * />
 * ```
 */
export interface AgentFolderProps extends Omit<HTMLAttributes<HTMLDivElement>, 'onDrop'> {
  /**
   * Unique folder identifier
   */
  id: string;

  /**
   * Folder title/name
   */
  title: string;

  /**
   * Number of agents in folder
   */
  agentCount: number;

  /**
   * Array of agents to display
   */
  agents: AgentCardProps[];

  /**
   * Default expanded state
   * @default true
   */
  defaultExpanded?: boolean;

  /**
   * Available sort options
   */
  sortOptions?: SortOption[];

  /**
   * Currently selected sort value
   */
  selectedSort?: string;

  /**
   * Sort option change handler
   */
  onSortChange?: (value: string) => void;

  /**
   * Drop handler for drag & drop
   */
  onDrop?: (agentId: string, folderId: string) => void;

  /**
   * Edit folder handler
   */
  onFolderEdit?: () => void;

  /**
   * Delete folder handler
   */
  onFolderDelete?: () => void;

  /**
   * Is this folder currently a valid drop target
   * @internal
   */
  isDropTarget?: boolean;
}

/**
 * AgentFolder Section Exports
 *
 * Exports for the AgentFolder section and its types.
 */

export { AgentFolder } from './AgentFolder';
export type { AgentFolderProps, SortOption } from './AgentFolder.types';
