/**
 * AlertDialog Component
 *
 * A confirmation dialog that forces a user response.
 * Unlike Modal, it does NOT close on backdrop click or Escape by default.
 *
 * @example
 * ```tsx
 * <AlertDialog open={open} onClose={handleClose}>
 *   <AlertDialog.Icon variant="danger" />
 *   <AlertDialog.Title>Delete account?</AlertDialog.Title>
 *   <AlertDialog.Description>This action cannot be undone.</AlertDialog.Description>
 *   <AlertDialog.Actions>
 *     <Button variant="ghost" onClick={handleClose}>Cancel</Button>
 *     <Button variant="primary" onClick={handleConfirm}>Delete</Button>
 *   </AlertDialog.Actions>
 * </AlertDialog>
 * ```
 */

import React, { createContext, useContext, useEffect, useId } from "react";
import { createPortal } from "react-dom";
import { AlertCircle, AlertTriangle, Info } from "lucide-react";
import type {
  AlertDialogProps,
  AlertDialogIconProps,
  AlertDialogTitleProps,
  AlertDialogDescriptionProps,
  AlertDialogActionsProps,
} from "./AlertDialog.types";
import styles from "./AlertDialog.module.css";

// Internal context for sharing generated IDs between compound components
interface AlertDialogContextValue {
  titleId: string;
  descriptionId: string;
}

const AlertDialogContext = createContext<AlertDialogContextValue | null>(null);

function useAlertDialogContext(): AlertDialogContextValue | null {
  return useContext(AlertDialogContext);
}

const DEFAULT_ICONS = {
  info: <Info size={24} />,
  warning: <AlertTriangle size={24} />,
  danger: <AlertCircle size={24} />,
};

// Main AlertDialog component
export const AlertDialog: React.FC<AlertDialogProps> & {
  Icon: React.FC<AlertDialogIconProps>;
  Title: React.FC<AlertDialogTitleProps>;
  Description: React.FC<AlertDialogDescriptionProps>;
  Actions: React.FC<AlertDialogActionsProps>;
} = ({
  open,
  onClose,
  closeOnBackdrop = false,
  closeOnEscape = false,
  children,
  className,
}) => {
  const titleId = useId();
  const descriptionId = useId();

  // Handle Escape key
  useEffect(() => {
    if (!open || !closeOnEscape) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [open, closeOnEscape, onClose]);

  // Prevent body scroll when dialog is open
  useEffect(() => {
    if (open) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }

    return () => {
      document.body.style.overflow = "";
    };
  }, [open]);

  if (!open) return null;

  const dialogClasses = [styles.dialog, className].filter(Boolean).join(" ");

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (closeOnBackdrop && e.target === e.currentTarget) {
      onClose();
    }
  };

  return createPortal(
    <AlertDialogContext.Provider value={{ titleId, descriptionId }}>
      <div
        className={styles.backdrop}
        onClick={handleBackdropClick}
        role="alertdialog"
        aria-modal="true"
        aria-labelledby={titleId}
        aria-describedby={descriptionId}
      >
        <div className={dialogClasses}>{children}</div>
      </div>
    </AlertDialogContext.Provider>,
    document.body,
  );
};

// AlertDialog.Icon subcomponent
const AlertDialogIcon: React.FC<AlertDialogIconProps> = ({
  variant = "info",
  icon,
  className,
}) => {
  const variantClass = {
    info: styles.iconInfo,
    warning: styles.iconWarning,
    danger: styles.iconDanger,
  }[variant];

  const classNames = [styles.icon, variantClass, className]
    .filter(Boolean)
    .join(" ");

  return (
    <div className={classNames} aria-hidden="true">
      {icon || DEFAULT_ICONS[variant]}
    </div>
  );
};

// AlertDialog.Title subcomponent
const AlertDialogTitle: React.FC<AlertDialogTitleProps> = ({
  className,
  children,
}) => {
  const ctx = useAlertDialogContext();
  const classNames = [styles.title, className].filter(Boolean).join(" ");
  return (
    <h2 id={ctx?.titleId} className={classNames}>
      {children}
    </h2>
  );
};

// AlertDialog.Description subcomponent
const AlertDialogDescription: React.FC<AlertDialogDescriptionProps> = ({
  className,
  children,
}) => {
  const ctx = useAlertDialogContext();
  const classNames = [styles.description, className].filter(Boolean).join(" ");
  return (
    <p id={ctx?.descriptionId} className={classNames}>
      {children}
    </p>
  );
};

// AlertDialog.Actions subcomponent
const AlertDialogActions: React.FC<AlertDialogActionsProps> = ({
  className,
  children,
}) => {
  const classNames = [styles.actions, className].filter(Boolean).join(" ");
  return <div className={classNames}>{children}</div>;
};

// Attach subcomponents
AlertDialog.Icon = AlertDialogIcon;
AlertDialog.Title = AlertDialogTitle;
AlertDialog.Description = AlertDialogDescription;
AlertDialog.Actions = AlertDialogActions;

// Display names
AlertDialog.displayName = "AlertDialog";
AlertDialogIcon.displayName = "AlertDialog.Icon";
AlertDialogTitle.displayName = "AlertDialog.Title";
AlertDialogDescription.displayName = "AlertDialog.Description";
AlertDialogActions.displayName = "AlertDialog.Actions";

/**
 * AlertDialog Component Types
 *
 * Type definitions for the Orion AlertDialog component.
 * Unlike Modal, AlertDialog forces a user response (no close on backdrop/escape by default).
 */

import type { ReactNode } from "react";

/**
 * AlertDialog variant — controls accent color of icon
 */
export type AlertDialogVariant = "info" | "warning" | "danger";

/**
 * AlertDialog component props
 *
 * @example
 * ```tsx
 * <AlertDialog open={open} onClose={handleClose}>
 *   <AlertDialog.Icon variant="danger" />
 *   <AlertDialog.Title>Delete account?</AlertDialog.Title>
 *   <AlertDialog.Description>This action cannot be undone.</AlertDialog.Description>
 *   <AlertDialog.Actions>
 *     <Button variant="ghost" onClick={handleClose}>Cancel</Button>
 *     <Button variant="primary" onClick={handleConfirm}>Delete</Button>
 *   </AlertDialog.Actions>
 * </AlertDialog>
 * ```
 */
export interface AlertDialogProps {
  /**
   * Whether the dialog is open
   */
  open: boolean;

  /**
   * Callback when dialog should close
   */
  onClose: () => void;

  /**
   * Close on backdrop click
   * @default false
   */
  closeOnBackdrop?: boolean;

  /**
   * Close on Escape key
   * @default false
   */
  closeOnEscape?: boolean;

  /**
   * Dialog content (use compound components)
   */
  children?: ReactNode;

  /**
   * Additional class name
   */
  className?: string;
}

/**
 * AlertDialog.Icon props
 */
export interface AlertDialogIconProps {
  /**
   * Visual variant — controls icon and color
   * @default 'info'
   */
  variant?: AlertDialogVariant;

  /**
   * Custom icon (overrides default)
   */
  icon?: ReactNode;

  /**
   * Additional class name
   */
  className?: string;
}

/**
 * AlertDialog.Title props
 */
export interface AlertDialogTitleProps {
  children?: ReactNode;
  className?: string;
}

/**
 * AlertDialog.Description props
 */
export interface AlertDialogDescriptionProps {
  children?: ReactNode;
  className?: string;
}

/**
 * AlertDialog.Actions props
 */
export interface AlertDialogActionsProps {
  children?: ReactNode;
  className?: string;
}

/**
 * AlertDialog Component Styles
 *
 * Compact dialog that forces user response.
 * All values reference semantic tokens.
 */

/* Backdrop overlay */
.backdrop {
  position: fixed;
  inset: 0;
  z-index: var(--z-modal-backdrop);
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--surface-overlay);
  backdrop-filter: blur(var(--blur-sm));
  animation: fadeIn 200ms ease;
  padding: var(--spacing-4);
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* Dialog container */
.dialog {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  width: 100%;
  max-width: 400px;
  background: var(--surface-base);
  border-radius: var(--radius-container);
  box-shadow: var(--shadow-xl);
  padding: var(--spacing-8) var(--spacing-6) var(--spacing-6);
  animation: scaleIn 200ms ease;
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  .backdrop,
  .dialog {
    animation: none;
  }
}

/* ============================================================================
 * ICON
 * ============================================================================ */

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 48px;
  border-radius: var(--radius-full);
  margin-bottom: var(--spacing-4);
  flex-shrink: 0;
}

.iconInfo {
  background: var(--soft-info);
  color: var(--status-info);
}

.iconWarning {
  background: var(--soft-warning);
  color: var(--status-warning);
}

.iconDanger {
  background: var(--soft-error);
  color: var(--status-error);
}

/* ============================================================================
 * CONTENT
 * ============================================================================ */

.title {
  font-family: var(--font-secondary);
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  margin: 0 0 var(--spacing-2) 0;
  line-height: var(--line-height-tight);
}

.description {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  color: var(--text-secondary);
  margin: 0 0 var(--spacing-6) 0;
  line-height: var(--line-height-normal);
}

/* ============================================================================
 * ACTIONS
 * ============================================================================ */

.actions {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--spacing-3);
  width: 100%;
}
