/**
 * Alert Component
 *
 * Notification and message alert component with status variants.
 * Uses Lucide icons for consistent iconography across the design system.
 *
 * @example
 * ```tsx
 * <Alert variant="success" title="Success!">
 *   Your changes have been saved successfully.
 * </Alert>
 *
 * <Alert variant="error" dismissible onClose={() => console.log('closed')}>
 *   An error occurred while processing your request.
 * </Alert>
 *
 * <Alert variant="warning" title="Warning">
 *   This action cannot be undone.
 * </Alert>
 *
 * <Alert variant="info" icon={<InfoIcon />}>
 *   Custom icon example
 * </Alert>
 * ```
 */

import React, { useState, useMemo } from 'react';
import { CheckCircle, XCircle, AlertTriangle, Info, X } from 'lucide-react';
import type { AlertProps, AlertVariant } from './Alert.types';
import styles from './Alert.module.css';

/**
 * Default Lucide icons mapped to alert variants
 * These provide consistent iconography across the design system
 */
const DEFAULT_ICONS: Record<AlertVariant, React.ReactNode> = {
  success: <CheckCircle size={20} />,
  error: <XCircle size={20} />,
  warning: <AlertTriangle size={20} />,
  info: <Info size={20} />,
};

export const Alert: React.FC<AlertProps> = ({
  variant = 'info',
  title,
  dismissible = false,
  onClose,
  icon,
  className,
  children,
  ...rest
}) => {
  const [isVisible, setIsVisible] = useState(true);

  const handleClose = () => {
    setIsVisible(false);
    onClose?.();
  };

  // Memoize class names for performance
  const classNames = useMemo(
    () => [styles.alert, styles[variant], className].filter(Boolean).join(' '),
    [variant, className],
  );

  // Use custom icon or default Lucide icon for variant
  const displayIcon = useMemo(
    () => (icon !== undefined ? icon : DEFAULT_ICONS[variant]),
    [icon, variant],
  );

  if (!isVisible) {
    return null;
  }

  return (
    <div className={classNames} role="alert" {...rest}>
      {/* Icon */}
      {displayIcon && (
        <div className={styles.icon} aria-hidden="true">
          {displayIcon}
        </div>
      )}

      {/* Content */}
      <div className={styles.content}>
        {title && <div className={styles.title}>{title}</div>}
        {children && <div className={styles.message}>{children}</div>}
      </div>

      {/* Close button */}
      {dismissible && (
        <button
          type="button"
          className={styles.closeButton}
          onClick={handleClose}
          aria-label="Close alert"
        >
          <X size={18} aria-hidden="true" />
        </button>
      )}
    </div>
  );
};

Alert.displayName = 'Alert';

/**
 * Alert Component Types
 *
 * Type definitions for the Orion Alert component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Alert variants (status types)
 */
export type AlertVariant = 'success' | 'error' | 'warning' | 'info';

/**
 * Alert component props
 *
 * @example
 * ```tsx
 * <Alert variant="success" title="Success!">
 *   Your changes have been saved.
 * </Alert>
 *
 * <Alert variant="error" onClose={() => {}}>
 *   An error occurred.
 * </Alert>
 * ```
 */
export interface AlertProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Alert variant (status type)
   * @default 'info'
   */
  variant?: AlertVariant;

  /**
   * Alert title (optional)
   */
  title?: string;

  /**
   * Show close button
   * @default false
   */
  dismissible?: boolean;

  /**
   * Close button callback
   */
  onClose?: () => void;

  /**
   * Custom icon (overrides default status icon)
   */
  icon?: ReactNode;

  /**
   * Alert content
   */
  children?: ReactNode;
}

/**
 * Alert Component Styles
 *
 * Notification and message alerts.
 */

/* Base alert */
.alert {
  /* Layout */
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-3);
  padding: var(--spacing-4);

  /* Styling */
  border-radius: var(--radius-control);

  /* Typography */
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  line-height: var(--line-height-normal);
}

/* Icon */
.icon {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 20px;
  height: 20px;
}

/* Content wrapper */
.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

/* Title */
.title {
  font-weight: var(--font-weight-medium);
  font-size: var(--font-size-16);
  line-height: var(--line-height-tight);
}

/* Message */
.message {
  color: inherit;
  opacity: 0.9;
}

/* Close button */
.closeButton {
  /* Layout */
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 24px;
  height: 24px;

  /* Styling */
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  cursor: pointer;

  /* Typography */
  color: inherit;
  opacity: 0.6;

  /* Interaction */
  transition: all var(--transition-fast);

  &:hover {
    opacity: 1;
    background: var(--interactive-ghost-hover);
  }

  &:active {
    transform: scale(0.95);
  }

  &:focus-visible {
    outline: 2px solid currentColor;
    outline-offset: 2px;
    opacity: 1;
  }
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

/* Success variant */
.success {
  background: var(--soft-success);
  border: 1px solid var(--status-success);
  color: var(--status-success);
}

/* Error variant */
.error {
  background: var(--soft-error);
  border: 1px solid var(--status-error);
  color: var(--status-error);
}

/* Warning variant */
.warning {
  background: var(--soft-warning);
  border: 1px solid var(--status-warning);
  color: var(--status-warning);
}

/* Info variant */
.info {
  background: var(--soft-info);
  border: 1px solid var(--status-info);
  color: var(--status-info);
}
