/**
 * Avatar Component
 *
 * User profile picture or initials display with status indicators.
 *
 * @example
 * ```tsx
 * <Avatar src="/user.jpg" alt="John Doe" />
 * <Avatar initials="JD" size="lg" />
 * <Avatar icon={<UserIcon />} status="online" />
 * <Avatar src="/user.jpg" status="away" interactive />
 * ```
 */

import React, { useState } from "react";
import { User } from "lucide-react";
import type { AvatarProps } from "./Avatar.types";
import styles from "./Avatar.module.css";

// Map size prop to CSS class names
const sizeClassMap: Record<string, string> = {
  xs: "xs",
  sm: "sm",
  md: "md",
  lg: "lg",
  xl: "xl",
  "2xl": "xxl",
  "3xl": "xxxl",
  "4xl": "xxxxl",
  "5xl": "xxxxxl",
  profile: "profile",
};

export const Avatar: React.FC<AvatarProps> = ({
  src,
  alt = "",
  initials,
  icon,
  size = "md",
  status,
  interactive = false,
  className,
  ...rest
}) => {
  const [imageError, setImageError] = useState(false);

  const sizeClass = sizeClassMap[size] || size;

  const classNames = [
    styles.avatar,
    styles[sizeClass],
    interactive && styles.interactive,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  // Determine what to display
  const showImage = src && !imageError;
  const showInitials = !showImage && initials;
  const showIcon = !showImage && !showInitials && icon;

  return (
    <div className={classNames} {...rest}>
      {/* Image */}
      {showImage && (
        <img
          src={src}
          alt={alt}
          className={styles.image}
          onError={() => setImageError(true)}
        />
      )}

      {/* Initials */}
      {showInitials && <span className={styles.initials}>{initials}</span>}

      {/* Icon */}
      {showIcon && <span className={styles.icon}>{icon}</span>}

      {/* Default fallback icon */}
      {!showImage && !showInitials && !showIcon && (
        <span className={styles.icon}>
          <User />
        </span>
      )}

      {/* Status indicator */}
      {status && (
        <span
          className={`${styles.statusIndicator} ${styles[status]}`}
          aria-label={`Status: ${status}`}
        />
      )}
    </div>
  );
};

Avatar.displayName = "Avatar";

/**
 * Avatar Component Types
 *
 * Type definitions for the Orion Avatar component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * Avatar sizes
 */
export type AvatarSize =
  | "xs"
  | "sm"
  | "md"
  | "lg"
  | "xl"
  | "2xl"
  | "3xl"
  | "4xl"
  | "5xl"
  | "profile";

/**
 * Avatar component props
 *
 * @example
 * ```tsx
 * <Avatar src="/user.jpg" alt="John Doe" />
 * <Avatar initials="JD" />
 * <Avatar icon={<UserIcon />} />
 * ```
 */
export interface AvatarProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Avatar image source
   */
  src?: string;

  /**
   * Alt text for image
   */
  alt?: string;

  /**
   * User initials (fallback if no image)
   */
  initials?: string;

  /**
   * Icon element (fallback if no image or initials)
   */
  icon?: ReactNode;

  /**
   * Avatar size
   * @default 'md'
   */
  size?: AvatarSize;

  /**
   * Show online status indicator
   * @default false
   */
  status?: "online" | "offline" | "away" | "busy";

  /**
   * Make avatar clickable
   * @default false
   */
  interactive?: boolean;
}

/**
 * Avatar Component Styles
 *
 * User profile picture or initials display.
 */

/* Base avatar */
.avatar {
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;

  /* Styling */
  background: var(--surface-layer);
  border-radius: 50%;
  user-select: none;

  /* Typography */
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);

  /* Interaction */
  transition: all var(--transition-fast);
}

/* Avatar image */
.image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 50%;
}

/* Initials */
.initials {
  text-transform: uppercase;
  line-height: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
}

/* Icon */
.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--text-tertiary);
}

.icon svg {
  width: 100%;
  height: 100%;
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.xs {
  width: 24px;
  height: 24px;
  font-size: var(--font-size-10);
}

.xs .icon {
  width: 12px;
  height: 12px;
}

.sm {
  width: 32px;
  height: 32px;
  font-size: var(--font-size-12);
}

.sm .icon {
  width: 16px;
  height: 16px;
}

.md {
  width: 40px;
  height: 40px;
  font-size: var(--font-size-14);
}

.md .icon {
  width: 20px;
  height: 20px;
}

.lg {
  width: 48px;
  height: 48px;
  font-size: var(--font-size-16);
}

.lg .icon {
  width: 24px;
  height: 24px;
}

.xl {
  width: 64px;
  height: 64px;
  font-size: var(--font-size-20);
}

.xl .icon {
  width: 32px;
  height: 32px;
}

.xxl {
  width: 96px;
  height: 96px;
  font-size: var(--font-size-32);
}

.xxl .icon {
  width: 48px;
  height: 48px;
}

.profile {
  width: 120px;
  height: 120px;
  font-size: var(--font-size-48);
}

.profile .icon {
  width: 56px;
  height: 56px;
}

.xxxl {
  width: 120px;
  height: 120px;
  font-size: var(--font-size-48);
}

.xxxl .icon {
  width: 56px;
  height: 56px;
}

.xxxxl {
  width: 160px;
  height: 160px;
  font-size: var(--font-size-64);
}

.xxxxl .icon {
  width: 72px;
  height: 72px;
}

.xxxxxl {
  width: 200px;
  height: 200px;
  font-size: var(--font-size-80);
}

.xxxxxl .icon {
  width: 96px;
  height: 96px;
}

/* ============================================================================
 * INTERACTIVE
 * ============================================================================ */

.interactive {
  cursor: pointer;

  &:hover {
    transform: scale(1.05);
    box-shadow: var(--shadow-md);
  }

  &:active {
    transform: scale(0.98);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }
}

/* ============================================================================
 * STATUS INDICATOR
 * ============================================================================ */

.statusIndicator {
  position: absolute;
  bottom: 0;
  right: 0;
  width: 25%;
  height: 25%;
  min-width: 8px;
  min-height: 8px;
  border-radius: 50%;
  border: 2px solid var(--surface-base);
}

.online {
  background: var(--status-success);
}

.offline {
  background: var(--text-tertiary);
}

.away {
  background: var(--status-warning);
}

.busy {
  background: var(--status-error);
}
