/**
 * Badge Component
 *
 * Status indicator and label component.
 *
 * @example
 * ```tsx
 * <Badge variant="success">Active</Badge>
 * <Badge variant="error" size="sm">Error</Badge>
 * <Badge variant="brand" dot>New Feature</Badge>
 * <Badge variant="info">3 pending</Badge>
 * ```
 */

import React from 'react';
import type { BadgeProps } from './Badge.types';
import styles from './Badge.module.css';

export const Badge: React.FC<BadgeProps> = ({
  variant = 'neutral',
  size = 'md',
  dot = false,
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.badge, styles[variant], styles[size], className]
    .filter(Boolean)
    .join(' ');

  return (
    <span className={classNames} {...rest}>
      {dot && <span className={styles.dot} aria-hidden="true" />}
      {children}
    </span>
  );
};

Badge.displayName = 'Badge';

/**
 * Badge Component Types
 *
 * Type definitions for the Orion Badge component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Badge variants (status colors)
 */
export type BadgeVariant =
  | 'primary'
  | 'secondary'
  | 'neutral'
  | 'success'
  | 'error'
  | 'warning'
  | 'info'
  | 'brand';

/**
 * Badge sizes
 */
export type BadgeSize = 'sm' | 'md' | 'lg';

/**
 * Badge component props
 *
 * @example
 * ```tsx
 * <Badge variant="success">Active</Badge>
 * <Badge variant="error" size="sm">Error</Badge>
 * <Badge variant="brand" dot>New</Badge>
 * ```
 */
export interface BadgeProps extends HTMLAttributes<HTMLSpanElement> {
  /**
   * Badge visual variant
   * @default 'neutral'
   */
  variant?: BadgeVariant;

  /**
   * Badge size
   * @default 'md'
   */
  size?: BadgeSize;

  /**
   * Show dot indicator
   * @default false
   */
  dot?: boolean;

  /**
   * Badge content
   */
  children?: ReactNode;
}

/**
 * Badge Component Styles
 *
 * Status indicators and labels.
 */

/* Base badge */
.badge {
  /* Layout */
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-1);

  /* Typography */
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  line-height: var(--line-height-none);
  white-space: nowrap;

  /* Styling */
  border-radius: var(--radius-full);

  /* Interaction */
  user-select: none;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

/* Primary variant */
.primary {
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
}

/* Secondary variant */
.secondary {
  background: var(--interactive-secondary);
  color: var(--text-primary);
}

/* Neutral variant */
.neutral {
  background: var(--surface-subtle);
  color: var(--text-primary);
}

/* Success variant */
.success {
  background: var(--soft-success);
  color: var(--status-success);
}

/* Error variant */
.error {
  background: var(--soft-error);
  color: var(--status-error);
}

/* Warning variant */
.warning {
  background: var(--soft-warning);
  color: var(--status-warning);
}

/* Info variant */
.info {
  background: var(--soft-info);
  color: var(--status-info);
}

/* Brand variant */
.brand {
  background: var(--soft-brand);
  color: var(--text-brand);
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm {
  height: 20px;
  padding: 0 var(--spacing-2);
  font-size: var(--font-size-10);
}

.md {
  height: 24px;
  padding: 0 var(--spacing-3);
  font-size: var(--font-size-12);
}

.lg {
  height: 28px;
  padding: 0 var(--spacing-4);
  font-size: var(--font-size-14);
}

/* ============================================================================
 * DOT INDICATOR
 * ============================================================================ */

.dot {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  flex-shrink: 0;
  background: currentColor;
}
