/**
 * Banner Section Component
 *
 * A promotional banner section with multiple style variants.
 */

import React from 'react';
import { X } from 'lucide-react';
import { Button } from '../../components/Button';
import type { BannerProps } from './Banner.types';
import styles from './Banner.module.css';

/**
 * Banner section for promotions and announcements
 */
export const Banner: React.FC<BannerProps> = ({
  eyebrow,
  title,
  description,
  ctaLabel,
  ctaHref,
  secondaryCtaLabel,
  secondaryCtaHref,
  variant = 'default',
  backgroundImage,
  sideImage,
  imagePosition = 'right',
  backgroundColor,
  gradient,
  dismissible = false,
  onDismiss,
  fullWidth = false,
  compact = false,
  centered = true,
  className,
  style,
  ...rest
}) => {
  const customStyle: React.CSSProperties = {
    ...style,
    ...(backgroundImage &&
      variant === 'image' && {
        backgroundImage: `url(${backgroundImage})`,
      }),
    ...(backgroundColor && { backgroundColor }),
    ...(gradient &&
      variant === 'gradient' && {
        background: gradient,
      }),
  };

  const renderContent = () => (
    <div className={styles.content}>
      {eyebrow && <span className={styles.eyebrow}>{eyebrow}</span>}
      <h2 className={styles.title}>{title}</h2>
      {description && <p className={styles.description}>{description}</p>}
      {(ctaLabel || secondaryCtaLabel) && (
        <div className={styles.actions}>
          {ctaLabel && ctaHref && (
            <Button
              variant={variant === 'split' ? 'primary' : 'inverse'}
              onClick={() => window.location.href = ctaHref}
            >
              {ctaLabel}
            </Button>
          )}
          {secondaryCtaLabel && secondaryCtaHref && (
            <Button
              variant={variant === 'split' ? 'secondary' : 'ghost'}
              onClick={() => window.location.href = secondaryCtaHref}
            >
              {secondaryCtaLabel}
            </Button>
          )}
        </div>
      )}
    </div>
  );

  return (
    <section
      className={`${styles.banner} ${className || ''}`}
      data-variant={variant}
      data-compact={compact}
      data-centered={centered && variant !== 'split'}
      data-full-width={fullWidth}
      style={customStyle}
      {...rest}
    >
      <div className={styles.container}>
        {variant === 'split' && sideImage ? (
          <div className={styles.splitLayout} data-image-position={imagePosition}>
            <div className={styles.splitContent}>{renderContent()}</div>
            <img src={sideImage} alt="" className={styles.splitImage} aria-hidden="true" />
          </div>
        ) : (
          renderContent()
        )}
      </div>

      {dismissible && (
        <div className={styles.dismissButton}>
          <Button
            iconOnly
            icon={<X size={20} />}
            variant="ghost"
            onClick={onDismiss}
            aria-label="Dismiss banner"
          />
        </div>
      )}
    </section>
  );
};

Banner.displayName = 'Banner';

/**
 * Banner Section Types
 *
 * Type definitions for the Orion Banner section component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Banner variant styles
 */
export type BannerVariant = 'default' | 'gradient' | 'image' | 'split';

/**
 * Banner section props
 *
 * @example
 * ```tsx
 * <Banner
 *   title="Summer Sale"
 *   description="Up to 50% off on selected items"
 *   ctaLabel="Shop Now"
 *   ctaHref="/sale"
 *   variant="gradient"
 * />
 * ```
 */
export interface BannerProps extends Omit<HTMLAttributes<HTMLElement>, 'title'> {
  /**
   * Optional eyebrow/badge text
   */
  eyebrow?: ReactNode;

  /**
   * Banner title
   */
  title: ReactNode;

  /**
   * Banner description
   */
  description?: ReactNode;

  /**
   * Primary CTA button label
   */
  ctaLabel?: string;

  /**
   * Primary CTA button href
   */
  ctaHref?: string;

  /**
   * Secondary CTA label
   */
  secondaryCtaLabel?: string;

  /**
   * Secondary CTA href
   */
  secondaryCtaHref?: string;

  /**
   * Banner variant
   * @default 'default'
   */
  variant?: BannerVariant;

  /**
   * Background image URL (for 'image' and 'split' variants)
   */
  backgroundImage?: string;

  /**
   * Side image URL (for 'split' variant)
   */
  sideImage?: string;

  /**
   * Image position for split variant
   * @default 'right'
   */
  imagePosition?: 'left' | 'right';

  /**
   * Custom background color
   */
  backgroundColor?: string;

  /**
   * Custom gradient (overrides default)
   */
  gradient?: string;

  /**
   * Dismissible banner
   * @default false
   */
  dismissible?: boolean;

  /**
   * On dismiss callback
   */
  onDismiss?: () => void;

  /**
   * Full width (no max-width constraint)
   * @default false
   */
  fullWidth?: boolean;

  /**
   * Compact mode
   * @default false
   */
  compact?: boolean;

  /**
   * Center content
   * @default true
   */
  centered?: boolean;
}
