/**
 * Breadcrumb Component
 *
 * Navigation breadcrumbs showing current page location in site hierarchy.
 *
 * @example
 * ```tsx
 * <Breadcrumb
 *   items={[
 *     { label: 'Home', href: '/' },
 *     { label: 'Products', href: '/products' },
 *     { label: 'Laptop' }
 *   ]}
 * />
 *
 * <Breadcrumb
 *   items={breadcrumbs}
 *   separator="slash"
 *   showHomeIcon
 *   maxItems={3}
 *   size="lg"
 * />
 *
 * <Breadcrumb
 *   items={items}
 *   separator="custom"
 *   customSeparator={<Icon name="arrow-right" />}
 * />
 * ```
 */

import React from "react";
import { Home } from "lucide-react";
import type { BreadcrumbProps, BreadcrumbItem } from "./Breadcrumb.types";
import styles from "./Breadcrumb.module.css";

export const Breadcrumb: React.FC<BreadcrumbProps> = ({
  items,
  separator = "chevron",
  customSeparator,
  showHomeIcon = false,
  maxItems,
  size = "md",
  className,
  linkProps,
}) => {
  // Collapse items if maxItems is set and exceeded
  const processedItems = React.useMemo(() => {
    if (!maxItems || items.length <= maxItems) {
      return items;
    }

    // Always show first and last items, collapse middle
    const firstItem = items[0];
    const lastItems = items.slice(-(maxItems - 1));

    return [
      ...(firstItem ? [firstItem] : []),
      { label: "...", href: undefined } as BreadcrumbItem, // Ellipsis
      ...lastItems,
    ];
  }, [items, maxItems]);

  // Render separator
  const renderSeparator = (index: number) => {
    const separatorClasses = [
      styles.separator,
      separator === "chevron" && styles.chevron,
      separator === "slash" && styles.slash,
    ]
      .filter(Boolean)
      .join(" ");

    if (separator === "custom" && customSeparator) {
      return (
        <span
          key={`sep-${index}`}
          className={styles.separator}
          aria-hidden="true"
        >
          {customSeparator}
        </span>
      );
    }

    return (
      <span
        key={`sep-${index}`}
        className={separatorClasses}
        aria-hidden="true"
      />
    );
  };

  // Get icon size based on breadcrumb size
  const getIconSize = () => {
    switch (size) {
      case "sm":
        return 14;
      case "lg":
        return 18;
      default:
        return 16;
    }
  };

  // Render home icon
  const renderHomeIcon = () => {
    if (!showHomeIcon) return null;
    return (
      <span className={styles.homeIcon} aria-label="Home">
        <Home size={getIconSize()} />
      </span>
    );
  };

  // Render individual breadcrumb item
  const renderItem = (item: BreadcrumbItem, index: number) => {
    const isLast = index === processedItems.length - 1;
    const isEllipsis = item.label === "...";

    // Ellipsis (collapsed items indicator)
    if (isEllipsis) {
      return (
        <li key={`ellipsis-${index}`} className={styles.item}>
          <span className={styles.ellipsis} aria-label="More items">
            â€¦
          </span>
        </li>
      );
    }

    // Current page (last item, non-clickable)
    if (isLast || !item.href) {
      return (
        <li
          key={index}
          className={styles.item}
          aria-current={isLast ? "page" : undefined}
        >
          <span className={styles.current}>
            {index === 0 && renderHomeIcon()}
            {item.icon && <span className={styles.icon}>{item.icon}</span>}
            {item.label}
          </span>
        </li>
      );
    }

    // Clickable breadcrumb link
    return (
      <li key={index} className={styles.item}>
        <a href={item.href} className={styles.link} {...linkProps}>
          {index === 0 && renderHomeIcon()}
          {item.icon && <span className={styles.icon}>{item.icon}</span>}
          {item.label}
        </a>
      </li>
    );
  };

  // Container classes
  const breadcrumbClasses = [styles.breadcrumb, styles[size], className]
    .filter(Boolean)
    .join(" ");

  return (
    <nav aria-label="Breadcrumb">
      <ol className={breadcrumbClasses}>
        {processedItems.map((item, index) => (
          <React.Fragment key={index}>
            {renderItem(item, index)}
            {index < processedItems.length - 1 && renderSeparator(index)}
          </React.Fragment>
        ))}
      </ol>
    </nav>
  );
};

Breadcrumb.displayName = "Breadcrumb";

/**
 * Breadcrumb Component Types
 *
 * Type definitions for the Orion Breadcrumb component.
 */

import type { ReactNode, AnchorHTMLAttributes } from "react";

/**
 * Breadcrumb sizes
 */
export type BreadcrumbSize = "sm" | "md" | "lg";

/**
 * Breadcrumb separator type
 */
export type BreadcrumbSeparator = "chevron" | "slash" | "custom";

/**
 * Individual breadcrumb item
 */
export interface BreadcrumbItem {
  /**
   * Item label
   */
  label: string;

  /**
   * Link href (if undefined, renders as span)
   */
  href?: string;

  /**
   * Optional icon
   */
  icon?: ReactNode;
}

/**
 * Breadcrumb component props
 *
 * @example
 * ```tsx
 * <Breadcrumb
 *   items={[
 *     { label: 'Home', href: '/' },
 *     { label: 'Products', href: '/products' },
 *     { label: 'Laptop' }
 *   ]}
 * />
 *
 * <Breadcrumb
 *   items={items}
 *   separator="slash"
 *   showHomeIcon
 *   size="lg"
 * />
 * ```
 */
export interface BreadcrumbProps {
  /**
   * Breadcrumb items
   */
  items: BreadcrumbItem[];

  /**
   * Separator type
   * @default 'chevron'
   */
  separator?: BreadcrumbSeparator;

  /**
   * Custom separator element
   * (only used if separator is 'custom')
   */
  customSeparator?: ReactNode;

  /**
   * Show home icon for first item
   * @default false
   */
  showHomeIcon?: boolean;

  /**
   * Maximum number of items to display
   * (items in middle will be collapsed if exceeded)
   */
  maxItems?: number;

  /**
   * Breadcrumb size
   * @default 'md'
   */
  size?: BreadcrumbSize;

  /**
   * Additional CSS class
   */
  className?: string;

  /**
   * Link props (applied to all anchor elements)
   */
  linkProps?: Omit<AnchorHTMLAttributes<HTMLAnchorElement>, "href">;
}

/**
 * Breadcrumb Component Styles
 *
 * Navigation breadcrumbs showing current page location.
 */

/* Container */
.breadcrumb {
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap: var(--spacing-2);
  list-style: none;
  margin: 0;
  padding: 0;
  font-family: var(--font-secondary);
}

/* Item */
.item {
  display: flex;
  align-items: center;
}

/* Link */
.link {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-2);
  font-weight: var(--font-weight-regular);
  color: var(--text-secondary);
  text-decoration: none;
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--radius-sm);
  transition: all var(--transition-fast);
}

.link:hover {
  color: var(--text-primary);
  background: var(--surface-subtle);
}

.link:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
}

/* Current page (non-clickable) */
.current {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-2);
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);
  padding: var(--spacing-1) var(--spacing-2);
}

/* Separator */
.separator {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--text-tertiary);
  user-select: none;
  flex-shrink: 0;
}

/* Chevron separator - using SVG for crisp rendering */
.chevron {
  width: 16px;
  height: 16px;
}

.chevron::after {
  content: "";
  display: block;
  width: 6px;
  height: 6px;
  border-right: 1.5px solid currentColor;
  border-bottom: 1.5px solid currentColor;
  transform: rotate(-45deg);
  margin-left: 2px;
}

/* Slash separator */
.slash::after {
  content: "/";
  font-weight: var(--font-weight-regular);
  font-size: 0.875em;
  opacity: 0.5;
}

/* Icon wrapper */
.icon {
  flex-shrink: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: currentColor;
  opacity: 0.7;
}

.link:hover .icon {
  opacity: 1;
}

.current .icon {
  opacity: 1;
}

/* Home icon */
.homeIcon {
  flex-shrink: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

/* Ellipsis (collapsed items indicator) */
.ellipsis {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: var(--text-tertiary);
  font-weight: var(--font-weight-medium);
  user-select: none;
  padding: var(--spacing-1) var(--spacing-2);
  letter-spacing: 2px;
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm .link,
.sm .current,
.sm .ellipsis {
  font-size: var(--font-size-12);
  padding: 2px var(--spacing-1);
}

.sm .icon,
.sm .homeIcon {
  width: 14px;
  height: 14px;
}

.sm .separator {
  width: 14px;
  height: 14px;
}

.sm .chevron::after {
  width: 5px;
  height: 5px;
}

.md .link,
.md .current,
.md .ellipsis {
  font-size: var(--font-size-14);
}

.md .icon,
.md .homeIcon {
  width: 16px;
  height: 16px;
}

.md .separator {
  width: 16px;
  height: 16px;
}

.lg .link,
.lg .current,
.lg .ellipsis {
  font-size: var(--font-size-16);
  padding: var(--spacing-1) var(--spacing-2);
}

.lg .icon,
.lg .homeIcon {
  width: 18px;
  height: 18px;
}

.lg .separator {
  width: 18px;
  height: 18px;
}

.lg .chevron::after {
  width: 7px;
  height: 7px;
}
