/**
 * Card Component
 *
 * A versatile container card with header, body, and footer sections.
 *
 * @example
 * ```tsx
 * <Card variant="base">
 *   <Card.Header>Card Title</Card.Header>
 *   <Card.Body>
 *     Card content goes here
 *   </Card.Body>
 *   <Card.Footer>Footer content</Card.Footer>
 * </Card>
 *
 * <Card variant="glass" interactive>
 *   <Card.Body>Clickable glass card</Card.Body>
 * </Card>
 * ```
 */

import React from "react";
import type {
  CardProps,
  CardHeaderProps,
  CardBodyProps,
  CardFooterProps,
  ImageCardPosition,
  ImageContentProps,
  ImageTitleProps,
  ImageDescriptionProps,
  ImageMetaProps,
} from "./Card.types";
import styles from "./Card.module.css";

// Gradient class mapping
const getGradientClass = (position: ImageCardPosition): string => {
  const map: Record<ImageCardPosition, string | undefined> = {
    top: styles.gradientTop,
    center: styles.gradientCenter,
    bottom: styles.gradientBottom,
  };
  return map[position] || styles.gradientBottom || "";
};

// Main Card component
export const Card: React.FC<CardProps> & {
  Header: React.FC<CardHeaderProps>;
  Body: React.FC<CardBodyProps>;
  Footer: React.FC<CardFooterProps>;
  ImageContent: React.FC<ImageContentProps>;
  ImageTitle: React.FC<ImageTitleProps>;
  ImageDescription: React.FC<ImageDescriptionProps>;
  ImageMeta: React.FC<ImageMetaProps>;
} = ({
  variant = "base",
  interactive = false,
  imageUrl,
  imagePosition = "bottom",
  aspectRatio = "16/9",
  className,
  children,
  style,
  ...rest
}) => {
  const isImageVariant = variant === "image";

  const classNames = [
    styles.card,
    styles[variant],
    interactive && styles.interactive,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  const cardStyle = isImageVariant
    ? {
        ...style,
        backgroundImage: imageUrl ? `url(${imageUrl})` : undefined,
        aspectRatio,
      }
    : style;

  if (isImageVariant) {
    return (
      <div className={classNames} style={cardStyle} {...rest}>
        <div
          className={`${styles.imageOverlay} ${getGradientClass(imagePosition)}`}
        >
          {children}
        </div>
      </div>
    );
  }

  return (
    <div className={classNames} style={style} {...rest}>
      {children}
    </div>
  );
};

// Card Header subcomponent
const CardHeader: React.FC<CardHeaderProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.header, className].filter(Boolean).join(" ");

  return (
    <div className={classNames} {...rest}>
      {children}
    </div>
  );
};

// Card Body subcomponent
const CardBody: React.FC<CardBodyProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.body, className].filter(Boolean).join(" ");

  return (
    <div className={classNames} {...rest}>
      {children}
    </div>
  );
};

// Card Footer subcomponent
const CardFooter: React.FC<CardFooterProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.footer, className].filter(Boolean).join(" ");

  return (
    <div className={classNames} {...rest}>
      {children}
    </div>
  );
};

// Image Card subcomponents
const ImageContent: React.FC<ImageContentProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.imageContent, className].filter(Boolean).join(" ");
  return (
    <div className={classNames} {...rest}>
      {children}
    </div>
  );
};

const ImageTitle: React.FC<ImageTitleProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.imageTitle, className].filter(Boolean).join(" ");
  return (
    <h3 className={classNames} {...rest}>
      {children}
    </h3>
  );
};

const ImageDescription: React.FC<ImageDescriptionProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.imageDescription, className]
    .filter(Boolean)
    .join(" ");
  return (
    <p className={classNames} {...rest}>
      {children}
    </p>
  );
};

const ImageMeta: React.FC<ImageMetaProps> = ({
  className,
  children,
  ...rest
}) => {
  const classNames = [styles.imageMeta, className].filter(Boolean).join(" ");
  return (
    <span className={classNames} {...rest}>
      {children}
    </span>
  );
};

// Attach subcomponents
Card.Header = CardHeader;
Card.Body = CardBody;
Card.Footer = CardFooter;
Card.ImageContent = ImageContent;
Card.ImageTitle = ImageTitle;
Card.ImageDescription = ImageDescription;
Card.ImageMeta = ImageMeta;

// Display names for debugging
Card.displayName = "Card";
CardHeader.displayName = "Card.Header";
CardBody.displayName = "Card.Body";
CardFooter.displayName = "Card.Footer";
ImageContent.displayName = "Card.ImageContent";
ImageTitle.displayName = "Card.ImageTitle";
ImageDescription.displayName = "Card.ImageDescription";
ImageMeta.displayName = "Card.ImageMeta";

/**
 * Card Component Types
 *
 * Type definitions for the Orion Card component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * Card visual variants
 */
export type CardVariant = "base" | "glass" | "elevated" | "outlined" | "image";

/**
 * Image card content position
 */
export type ImageCardPosition = "top" | "center" | "bottom";

/**
 * Card component props
 *
 * @example
 * ```tsx
 * <Card variant="base">
 *   <Card.Header>Title</Card.Header>
 *   <Card.Body>Content</Card.Body>
 *   <Card.Footer>Footer</Card.Footer>
 * </Card>
 * ```
 */
export interface CardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Visual variant
   * @default 'base'
   */
  variant?: CardVariant;

  /**
   * Interactive card (adds hover effect)
   * @default false
   */
  interactive?: boolean;

  /**
   * Background image URL (only for 'image' variant)
   */
  imageUrl?: string;

  /**
   * Content position for image variant
   * @default 'bottom'
   */
  imagePosition?: ImageCardPosition;

  /**
   * Aspect ratio for image card (e.g., '16/9', '4/3', '1/1')
   * @default '16/9'
   */
  aspectRatio?: string;

  /**
   * Card content
   */
  children?: ReactNode;
}

/**
 * Card Header props
 */
export interface CardHeaderProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Header content
   */
  children?: ReactNode;
}

/**
 * Card Body props
 */
export interface CardBodyProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Body content
   */
  children?: ReactNode;
}

/**
 * Card Footer props
 */
export interface CardFooterProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Footer content
   */
  children?: ReactNode;
}

/**
 * Image Card Content wrapper props
 */
export interface ImageContentProps extends HTMLAttributes<HTMLDivElement> {
  children?: ReactNode;
}

/**
 * Image Card Title props
 */
export interface ImageTitleProps extends HTMLAttributes<HTMLHeadingElement> {
  children?: ReactNode;
}

/**
 * Image Card Description props
 */
export interface ImageDescriptionProps extends HTMLAttributes<HTMLParagraphElement> {
  children?: ReactNode;
}

/**
 * Image Card Meta props
 */
export interface ImageMetaProps extends HTMLAttributes<HTMLSpanElement> {
  children?: ReactNode;
}

/**
 * Card Component Styles
 *
 * Container card with header, body, and footer sections.
 */

/* Base card */
.card {
  /* Layout */
  display: flex;
  flex-direction: column;
  overflow: hidden;

  /* Styling */
  background: var(--surface-base);
  border-radius: var(--radius-container);

  /* Interaction */
  transition: all var(--transition-fast);
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

/* Base variant (default) */
.base {
  border: 1px solid var(--border-subtle);
}

/* Glass variant (frosted glass effect) */
.glass {
  background: var(--surface-glass);
  backdrop-filter: blur(var(--blur-md));
  border: 1px solid var(--border-subtle);
}

/* Elevated variant (shadow) */
.elevated {
  box-shadow: var(--shadow-md);
}

/* Outlined variant (strong border) */
.outlined {
  border: 2px solid var(--border-strong);
}

/* Image variant (full background image with gradient overlay) */
.image {
  position: relative;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  aspect-ratio: 16 / 9;
}

/* Gradient overlay for readability */
.imageOverlay {
  position: absolute;
  inset: 0;
  display: flex;
  flex-direction: column;
  padding: var(--card-padding);
  border-radius: var(--radius-container);
}

/* Gradient positions */
.gradientBottom {
  background: linear-gradient(
    to top,
    var(--overlay-dark-heavy) 0%,
    var(--overlay-dark-light) 50%,
    transparent 100%
  );
  justify-content: flex-end;
}

.gradientTop {
  background: linear-gradient(
    to bottom,
    var(--overlay-dark-heavy) 0%,
    var(--overlay-dark-light) 50%,
    transparent 100%
  );
  justify-content: flex-start;
}

.gradientCenter {
  background: linear-gradient(
    to bottom,
    var(--overlay-dark-light) 0%,
    var(--overlay-dark) 50%,
    var(--overlay-dark-light) 100%
  );
  justify-content: center;
  align-items: center;
  text-align: center;
}

/* Image card text styles (always light on dark) */
.imageContent {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
  color: var(--color-neutral-0);
}

.imageTitle {
  font-size: var(--font-size-24);
  font-weight: var(--font-weight-semibold);
  color: var(--color-neutral-0);
  line-height: var(--line-height-tight);
  letter-spacing: -0.02em;
  text-shadow:
    0 2px 4px var(--overlay-dark-heavy),
    0 4px 12px var(--overlay-dark);
}

.imageDescription {
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-regular);
  color: var(--color-neutral-0);
  line-height: var(--line-height-relaxed);
  opacity: 0.9;
  text-shadow:
    0 1px 2px var(--overlay-dark-heavy),
    0 2px 8px var(--overlay-dark);
}

.imageMeta {
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-medium);
  color: var(--color-neutral-0);
  opacity: 0.75;
  letter-spacing: 0.02em;
  text-transform: uppercase;
  text-shadow: 0 1px 2px var(--overlay-dark-heavy);
}

/* ============================================================================
 * INTERACTIVE STATE
 * ============================================================================ */

.interactive {
  cursor: pointer;

  &:hover {
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
}

.interactive.elevated:hover {
  box-shadow: var(--shadow-lg);
}

.interactive.image:hover {
  transform: scale(1.02);
}

.interactive.image:active {
  transform: scale(1);
}

/* ============================================================================
 * CARD SECTIONS
 * ============================================================================ */

/* Card Header */
.header {
  padding: var(--card-padding) var(--card-padding) 0;
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);
}

/* Card Body */
.body {
  padding: var(--card-padding);
  flex: 1;
  color: var(--text-primary);
}

/* Body padding adjustment when preceded by header */
.header + .body {
  padding-top: var(--card-padding-gap);
}

/* Card Footer */
.footer {
  padding: 0 var(--card-padding) var(--card-padding);
  color: var(--text-secondary);
  font-size: var(--font-size-14);
}
