/**
 * Carousel Component
 *
 * An atomic carousel with horizontal scrolling, snap behavior, and peek effect.
 * This is a low-level component for use within cards, modals, or custom layouts.
 * For full-page carousel sections with title/description, use CarouselSection.
 *
 * @example
 * ```tsx
 * // Inside a Card
 * <Card>
 *   <Carousel
 *     items={[
 *       { image: <img src="/1.jpg" />, title: "Slide 1" },
 *       { image: <img src="/2.jpg" />, title: "Slide 2" },
 *     ]}
 *     variant="gallery"
 *     aspectRatio="16/9"
 *   />
 * </Card>
 *
 * // Inside a Modal
 * <Modal>
 *   <Modal.Body>
 *     <Carousel items={items} showNavigation showPagination />
 *   </Modal.Body>
 * </Modal>
 * ```
 */

import { forwardRef, useRef, useState, useEffect, useCallback } from "react";
import type { CarouselProps } from "./Carousel.types";
import { CarouselCard } from "./CarouselCard";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { spacing } from "../../tokens/primitives";
import styles from "./Carousel.module.css";

// Parse spacing token values (e.g., "24px" -> 24)
const TRACK_PADDING = parseInt(spacing[6], 10);
const GAP_SM = parseInt(spacing[4], 10); // 16px
const GAP_MD = parseInt(spacing[6], 10); // 24px
const GAP_LG = parseInt(spacing[8], 10); // 32px

export const Carousel = forwardRef<HTMLDivElement, CarouselProps>(
  (
    {
      items,
      variant = "editorial",
      aspectRatio = "16/9",
      peek = true,
      autoScroll = false,
      autoScrollInterval = 5000,
      gap = "md",
      align = "edge",
      alignOffset,
      showNavigation = true,
      showPagination = false,
      onSlideChange,
      renderNavigation,
      highlightActive = false,
      loop = false,
      className,
      ...rest
    },
    ref,
  ) => {
    const trackRef = useRef<HTMLDivElement>(null);
    const [canScrollLeft, setCanScrollLeft] = useState(false);
    const [canScrollRight, setCanScrollRight] = useState(true);
    const [activeIndex, setActiveIndex] = useState(0);
    const activeIndexRef = useRef(0);

    // Get gap width based on gap prop
    const getGapWidth = useCallback(() => {
      return gap === "sm" ? GAP_SM : gap === "lg" ? GAP_LG : GAP_MD;
    }, [gap]);

    // Get the card's layout width (without transforms that affect getBoundingClientRect)
    // When highlightActive is true, cards have scale transforms that change their visual size
    const getCardLayoutWidth = useCallback((track: HTMLDivElement): number => {
      const firstCard = track.children[0] as HTMLElement | undefined;
      if (!firstCard) return 0;

      // Use offsetWidth which gives layout width, not transformed width
      return firstCard.offsetWidth;
    }, []);

    const updateScrollState = useCallback(() => {
      if (!trackRef.current) return;

      const { scrollLeft, scrollWidth, clientWidth } = trackRef.current;
      const cardWidth = getCardLayoutWidth(trackRef.current);
      const gapWidth = getGapWidth();

      // Calculate end/start states
      const isAtEnd = scrollLeft >= scrollWidth - clientWidth - 1;
      const isAtStart = scrollLeft <= 1;

      setCanScrollLeft(!isAtStart);
      setCanScrollRight(!isAtEnd);

      // Calculate active index based on scroll position
      let newIndex: number;

      // If at the end of scroll, the last card should be active
      if (isAtEnd) {
        newIndex = items.length - 1;
      } else {
        const index = Math.round(scrollLeft / (cardWidth + gapWidth));
        newIndex = Math.min(Math.max(0, index), items.length - 1);
      }

      if (newIndex !== activeIndexRef.current) {
        activeIndexRef.current = newIndex;
        setActiveIndex(newIndex);
        onSlideChange?.(newIndex);
      }
    }, [gap, items.length, onSlideChange, getGapWidth, getCardLayoutWidth]);

    const scroll = useCallback(
      (direction: "left" | "right") => {
        if (!trackRef.current) return;

        const { scrollLeft, scrollWidth, clientWidth } = trackRef.current;
        const cardWidth = getCardLayoutWidth(trackRef.current);
        const gapWidth = getGapWidth();
        const scrollAmount = cardWidth + gapWidth;

        // Calculate current scroll state directly (not from stale state)
        const isAtStart = scrollLeft <= 1;
        const isAtEnd = scrollLeft >= scrollWidth - clientWidth - 1;

        // Handle loop behavior (simple jump to start/end)
        if (loop) {
          if (direction === "right" && isAtEnd) {
            // At end, loop to beginning
            trackRef.current.scrollTo({ left: 0, behavior: "smooth" });
            return;
          }
          if (direction === "left" && isAtStart) {
            // At beginning, loop to end
            trackRef.current.scrollTo({
              left: scrollWidth - clientWidth,
              behavior: "smooth",
            });
            return;
          }
        }

        trackRef.current.scrollBy({
          left: direction === "left" ? -scrollAmount : scrollAmount,
          behavior: "smooth",
        });
      },
      [gap, loop, getGapWidth, getCardLayoutWidth],
    );

    const scrollToIndex = useCallback(
      (index: number) => {
        if (!trackRef.current) return;

        const cardWidth = getCardLayoutWidth(trackRef.current);
        const gapWidth = getGapWidth();

        trackRef.current.scrollTo({
          left: index * (cardWidth + gapWidth),
          behavior: "smooth",
        });
      },
      [getGapWidth, getCardLayoutWidth],
    );

    useEffect(() => {
      const track = trackRef.current;
      if (!track) return;

      updateScrollState();
      track.addEventListener("scroll", updateScrollState);

      return () => {
        track.removeEventListener("scroll", updateScrollState);
      };
    }, [updateScrollState]);

    // Auto-scroll
    useEffect(() => {
      if (!autoScroll || items.length <= 1) return;

      const interval = setInterval(() => {
        const nextIndex = (activeIndex + 1) % items.length;
        scrollToIndex(nextIndex);
      }, autoScrollInterval);

      return () => clearInterval(interval);
    }, [
      autoScroll,
      autoScrollInterval,
      activeIndex,
      items.length,
      scrollToIndex,
    ]);

    // For container alignment, calculate extra margin for first card
    const hasCustomAlign = align === "container" && alignOffset !== undefined;
    // Extra margin = alignOffset - default track padding (from spacing token)
    const firstCardMargin = hasCustomAlign
      ? Math.max(0, alignOffset - TRACK_PADDING)
      : 0;

    // Map props to camelCase class names
    const gapClass =
      gap === "sm" ? styles.gapSm : gap === "lg" ? styles.gapLg : styles.gapMd;
    const trackAlignClass =
      align === "container" ? styles.trackContainer : styles.trackEdge;

    const classNames = [
      styles.carousel,
      peek && styles.peek,
      highlightActive && styles.highlightActive,
      gapClass,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    const trackClassNames = [styles.track, trackAlignClass]
      .filter(Boolean)
      .join(" ");

    // When loop is enabled, navigation is always available (if more than 1 item)
    const canLoopScroll = loop && items.length > 1;

    const navigationProps = {
      canScrollLeft: canLoopScroll || canScrollLeft,
      canScrollRight: canLoopScroll || canScrollRight,
      scrollLeft: () => scroll("left"),
      scrollRight: () => scroll("right"),
      activeIndex,
      totalItems: items.length,
    };

    return (
      <div ref={ref} className={classNames} {...rest}>
        <div
          ref={trackRef}
          className={trackClassNames}
          style={
            hasCustomAlign
              ? { scrollPaddingInlineStart: alignOffset }
              : undefined
          }
          role="region"
          aria-label="Carousel"
        >
          {items.map((item, index) => {
            const isFirst = index === 0;
            const isActive = index === activeIndex;
            const isHighlighted = isActive && highlightActive;

            return (
              <CarouselCard
                key={item.title || index}
                item={item}
                aspectRatio={aspectRatio}
                variant={variant}
                className={isHighlighted ? styles.cardHighlighted : undefined}
                style={
                  // First card gets extra margin for container alignment
                  isFirst && firstCardMargin > 0
                    ? { marginLeft: firstCardMargin }
                    : undefined
                }
              />
            );
          })}
        </div>

        {showNavigation &&
          items.length > 1 &&
          (renderNavigation ? (
            renderNavigation(navigationProps)
          ) : (
            <div className={styles.navigation}>
              <button
                type="button"
                className={styles.navButton}
                onClick={() => scroll("left")}
                disabled={!navigationProps.canScrollLeft}
                aria-label="Previous"
              >
                <ChevronLeft size={24} />
              </button>
              <button
                type="button"
                className={styles.navButton}
                onClick={() => scroll("right")}
                disabled={!navigationProps.canScrollRight}
                aria-label="Next"
              >
                <ChevronRight size={24} />
              </button>
            </div>
          ))}

        {showPagination && items.length > 1 && (
          <div className={styles.pagination}>
            {items.map((_, index) => (
              <button
                key={index}
                type="button"
                className={`${styles.dot} ${index === activeIndex ? styles.dotActive : ""}`}
                onClick={() => scrollToIndex(index)}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        )}
      </div>
    );
  },
);

Carousel.displayName = "Carousel";

/**
 * Carousel Component Types
 *
 * Type definitions for the atomic Carousel component.
 * This is a low-level component for use within cards, modals, or custom layouts.
 * For full-page carousel sections, use CarouselSection from sections.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * Carousel visual variant
 */
export type CarouselVariant = "editorial" | "product" | "gallery";

/**
 * Carousel aspect ratio
 */
export type CarouselAspectRatio = "16/9" | "4/3" | "1/1" | "3/4";

/**
 * Carousel gap size
 */
export type CarouselGap = "sm" | "md" | "lg";

/**
 * Overlay style for carousel cards
 */
export type CarouselOverlay = "none" | "gradient" | "dark";

/**
 * Track alignment relative to container
 */
export type CarouselAlign = "container" | "edge";

/**
 * Navigation render props
 */
export interface CarouselNavigationProps {
  canScrollLeft: boolean;
  canScrollRight: boolean;
  scrollLeft: () => void;
  scrollRight: () => void;
  activeIndex: number;
  totalItems: number;
}

/**
 * Carousel item data structure
 */
export interface CarouselItem {
  /**
   * Background image element (typically an img)
   */
  image: ReactNode;

  /**
   * Small category/tag text above title
   */
  eyebrow?: string;

  /**
   * Main headline for the card
   */
  title: string;

  /**
   * Short description/subtitle
   */
  description?: string;

  /**
   * Optional action element (link or button)
   */
  action?: ReactNode;

  /**
   * Overlay style for text legibility
   * @default 'gradient'
   */
  overlay?: CarouselOverlay;
}

/**
 * Carousel card props (internal component)
 */
export interface CarouselCardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Carousel item data
   */
  item: CarouselItem;

  /**
   * Aspect ratio of the card
   * @default '16/9'
   */
  aspectRatio?: CarouselAspectRatio;

  /**
   * Visual variant
   * @default 'editorial'
   */
  variant?: CarouselVariant;
}

/**
 * Atomic Carousel component props
 *
 * This is the low-level carousel component. For page sections, use CarouselSection.
 *
 * @example
 * ```tsx
 * // Inside a Card or Modal
 * <Carousel
 *   items={[
 *     { image: <img src="/1.jpg" />, title: "Slide 1" },
 *     { image: <img src="/2.jpg" />, title: "Slide 2" },
 *   ]}
 *   variant="gallery"
 *   aspectRatio="16/9"
 * />
 * ```
 */
export interface CarouselProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Array of carousel items
   */
  items: CarouselItem[];

  /**
   * Visual variant
   * - editorial: Magazine-style with large images and text overlays
   * - product: Product showcase cards
   * - gallery: Simple image gallery
   * @default 'editorial'
   */
  variant?: CarouselVariant;

  /**
   * Aspect ratio for cards
   * @default '16/9'
   */
  aspectRatio?: CarouselAspectRatio;

  /**
   * Show cards cut at edge (Apple style peek effect)
   * @default true
   */
  peek?: boolean;

  /**
   * Enable auto-scroll
   * @default false
   */
  autoScroll?: boolean;

  /**
   * Auto-scroll interval in milliseconds
   * @default 5000
   */
  autoScrollInterval?: number;

  /**
   * Gap between cards
   * @default 'md'
   */
  gap?: CarouselGap;

  /**
   * Track alignment
   * - 'container': First card aligns with Container (matches section title)
   * - 'edge': Cards start from screen edge (Apple style)
   * @default 'edge'
   */
  align?: CarouselAlign;

  /**
   * Show navigation arrows
   * @default true
   */
  showNavigation?: boolean;

  /**
   * Show pagination dots
   * @default false
   */
  showPagination?: boolean;

  /**
   * Callback when active slide changes
   */
  onSlideChange?: (index: number) => void;

  /**
   * Custom navigation renderer
   */
  renderNavigation?: (props: CarouselNavigationProps) => ReactNode;

  /**
   * Pixel offset for container alignment.
   * Used by CarouselSection to align first card snap point with title.
   * When set, scroll-padding-inline-start aligns snap points to this offset.
   * @internal
   */
  alignOffset?: number;

  /**
   * Make the active (focused) card 10% larger than the rest.
   * Creates a "spotlight" effect that follows the scroll position.
   * @default false
   */
  highlightActive?: boolean;

  /**
   * Enable infinite loop scrolling.
   * When reaching the end, navigation continues from the beginning.
   * @default false
   */
  loop?: boolean;
}

/**
 * Carousel Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 * This is the atomic carousel - no section/container wrappers.
 */

.carousel {
  position: relative;
  overflow: hidden;
  width: 100%;
}

/* ============================================================================
 * CAROUSEL TRACK
 * ============================================================================ */

.track {
  display: flex;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  scroll-behavior: smooth;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none;
}

.track::-webkit-scrollbar {
  display: none;
}

/* Alignment: Cards start from edge (default, Apple style) */
.trackEdge {
  padding-left: var(--spacing-6);
  padding-right: var(--spacing-6);
}

/* Alignment: Cards aligned with Container (matches section title)
 * First card gets extra margin-left via inline style.
 * scroll-padding ensures snap works correctly.
 */
.trackContainer {
  padding-left: var(--spacing-6);
  padding-right: var(--spacing-6);
}

/* Peek effect - cards cut at edge */
.peek .trackEdge,
.peek .trackContainer {
  padding-right: var(--spacing-16);
}

/* Gap variants */
.gapSm .track {
  gap: var(--spacing-4);
}

.gapMd .track {
  gap: var(--spacing-6);
}

.gapLg .track {
  gap: var(--spacing-8);
}

/* ============================================================================
 * NAVIGATION BUTTONS (Inline)
 * ============================================================================ */

.navigation {
  display: flex;
  gap: var(--spacing-2);
  justify-content: center;
  margin-top: var(--spacing-4);
}

.navButton {
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--surface-subtle);
  border: 1px solid var(--border-subtle);
  border-radius: 50%;
  color: var(--text-primary);
  cursor: pointer;
  transition:
    background var(--transition-fast),
    border-color var(--transition-fast);
}

.navButton:hover:not(:disabled) {
  background: var(--surface-layer);
  border-color: var(--border-default);
}

.navButton:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

/* ============================================================================
 * CAROUSEL CARD
 * ============================================================================ */

.card {
  flex-shrink: 0;
  scroll-snap-align: start;
  position: relative;
  border-radius: var(--radius-container);
  overflow: hidden;
  width: calc(100% - var(--spacing-12));
  max-width: 600px;
}

/* Aspect ratios */
.aspect-16-9 {
  aspect-ratio: 16/9;
}

.aspect-4-3 {
  aspect-ratio: 4/3;
}

.aspect-1-1 {
  aspect-ratio: 1/1;
}

.aspect-3-4 {
  aspect-ratio: 3/4;
}

/* ============================================================================
 * CARD IMAGE
 * ============================================================================ */

.imageWrapper {
  position: absolute;
  inset: 0;
}

.imageWrapper img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* ============================================================================
 * CARD OVERLAY
 * ============================================================================ */

.overlay {
  position: absolute;
  inset: 0;
  pointer-events: none;
}

.overlay-gradient {
  background: linear-gradient(
    to top,
    var(--overlay-dark-heavy) 0%,
    var(--overlay-dark-light) 30%,
    transparent 60%
  );
}

.overlay-dark {
  background: var(--overlay-dark);
}

/* ============================================================================
 * CARD CONTENT
 * ============================================================================ */

.content {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: var(--spacing-6);
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
}

.card .eyebrow {
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-semibold);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--text-on-dark-secondary);
}

.card .cardTitle {
  font-family: var(--font-secondary);
  font-size: var(--font-size-24);
  font-weight: var(--font-weight-bold);
  line-height: var(--line-height-tight);
  color: var(--text-on-dark);
  margin: 0;
}

.card .cardDescription {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  color: var(--text-on-dark-secondary);
  line-height: var(--line-height-relaxed);
  margin: 0;
}

.card .action {
  margin-top: var(--spacing-2);
}

/* ============================================================================
 * VARIANT: EDITORIAL (Apple-style)
 * ============================================================================ */

.variant-editorial {
  max-width: 700px;
}

.variant-editorial .cardTitle {
  font-size: clamp(1.25rem, 2.5vw, 2rem);
}

/* ============================================================================
 * VARIANT: PRODUCT
 * ============================================================================ */

.variant-product {
  max-width: 320px;
  background: var(--surface-layer);
  display: flex;
  flex-direction: column;
  aspect-ratio: auto; /* Override card aspect-ratio */
}

.variant-product .imageWrapper {
  position: relative;
  flex-shrink: 0;
  height: 140px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.variant-product .content {
  position: relative;
  padding: var(--spacing-4);
  background: var(--surface-layer);
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
}

.variant-product .eyebrow {
  color: var(--text-tertiary);
}

.variant-product .cardTitle {
  color: var(--text-primary);
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-semibold);
}

.variant-product .cardDescription {
  color: var(--text-secondary);
  font-size: var(--font-size-14);
  line-height: var(--line-height-relaxed);
}

/* ============================================================================
 * VARIANT: GALLERY
 * ============================================================================ */

.variant-gallery {
  max-width: 500px;
}

.variant-gallery .content {
  opacity: 0;
  transition: opacity var(--transition-fast);
}

.variant-gallery:hover .content {
  opacity: 1;
}

/* ============================================================================
 * PAGINATION
 * ============================================================================ */

.pagination {
  display: flex;
  justify-content: center;
  gap: var(--spacing-2);
  margin-top: var(--spacing-4);
}

.dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: var(--border-subtle);
  border: none;
  cursor: pointer;
  padding: 0;
  transition:
    background var(--transition-fast),
    transform var(--transition-fast);
}

.dot:hover {
  background: var(--border-default);
}

.dotActive {
  background: var(--interactive-primary);
  transform: scale(1.25);
}

/* ============================================================================
 * HIGHLIGHTED ACTIVE CARD (Spotlight Effect)
 * ============================================================================ */

/* Add vertical padding to track to accommodate scaled card overflow */
.highlightActive .track {
  padding-block: var(--spacing-6);
}

.cardHighlighted {
  transform: scale(1.1);
  transform-origin: center center;
  z-index: var(--z-dropdown);
  transition: transform var(--transition-normal);
}

/* Non-highlighted cards get a subtle scale down for contrast */
.highlightActive .card:not(.cardHighlighted) {
  transform: scale(0.95);
  opacity: 0.7;
  transition:
    transform var(--transition-normal),
    opacity var(--transition-normal);
}

/* ============================================================================
 * RESPONSIVE ADJUSTMENTS
 * ============================================================================ */

@media (max-width: 1024px) {
  .card {
    width: calc(100% - var(--spacing-8));
    max-width: 500px;
  }

  .variant-editorial {
    max-width: 500px;
  }
}

@media (max-width: 640px) {
  .trackEdge,
  .trackContainer {
    padding-left: var(--spacing-4);
    padding-right: var(--spacing-4);
  }

  .peek .trackEdge,
  .peek .trackContainer {
    padding-right: var(--spacing-8);
  }

  .card {
    width: calc(100% - var(--spacing-4));
    max-width: none;
  }

  .variant-editorial,
  .variant-product,
  .variant-gallery {
    max-width: none;
  }

  .navButton {
    width: 40px;
    height: 40px;
  }

  .content {
    padding: var(--spacing-4);
  }

  .card .cardTitle {
    font-size: var(--font-size-18);
  }
}
