/**
 * Chart Component
 *
 * Lightweight wrapper around Recharts providing Orion token theming.
 * Does NOT wrap Recharts — user accesses Recharts components directly.
 * Provides: ChartContainer (CSS variable injection), ChartTooltipContent,
 * and ChartLegendContent styled with Orion tokens.
 *
 * @example
 * ```tsx
 * const config: ChartConfig = {
 *   revenue: { label: 'Revenue', color: 'var(--color-brand-500)' },
 *   expenses: { label: 'Expenses', color: 'var(--status-error)' },
 * };
 *
 * <ChartContainer config={config}>
 *   <BarChart data={data}>
 *     <XAxis dataKey="month" />
 *     <YAxis />
 *     <ChartTooltip content={<ChartTooltipContent />} />
 *     <ChartLegend content={<ChartLegendContent />} />
 *     <Bar dataKey="revenue" fill="var(--color-revenue)" />
 *   </BarChart>
 * </ChartContainer>
 * ```
 */

import React, { createContext, useContext } from 'react';
import { ResponsiveContainer, Tooltip, Legend } from 'recharts';
import type {
  ChartConfig,
  ChartContainerProps,
  ChartTooltipContentProps,
  ChartLegendContentProps,
  ChartGradientProps,
} from './Chart.types';
import { useResolvedChartColors } from './useResolvedChartColors';
import styles from './Chart.module.css';

// Context to share config across tooltip/legend
const ChartContext = createContext<ChartConfig>({});

/**
 * ChartContainer — wraps Recharts with CSS variable injection from config
 */
export const ChartContainer: React.FC<ChartContainerProps> = ({
  config,
  children,
  className,
  style,
  ...rest
}) => {
  // Resolve CSS variable chains to concrete hex values for SVG compatibility
  const { resolvedConfig, resolvedColorVars } = useResolvedChartColors(config);

  const containerClasses = [styles.container, className].filter(Boolean).join(' ');

  return (
    <ChartContext.Provider value={resolvedConfig}>
      <div
        role="figure"
        className={containerClasses}
        style={{ ...resolvedColorVars, ...style } as React.CSSProperties}
        {...rest}
      >
        <ResponsiveContainer width="100%" height="100%">
          {children as React.ReactElement}
        </ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  );
};

ChartContainer.displayName = 'ChartContainer';

/**
 * ChartTooltipContent — Orion-styled tooltip for Recharts
 */
export const ChartTooltipContent: React.FC<ChartTooltipContentProps> = ({
  active,
  payload,
  label,
  indicator = 'dot',
  hideLabel = false,
  hideIndicator = false,
  labelFormatter,
  formatter,
  className,
}) => {
  const config = useContext(ChartContext);

  if (!active || !payload?.length) return null;

  const tooltipClasses = [styles.tooltip, className].filter(Boolean).join(' ');

  const formattedLabel = labelFormatter ? labelFormatter(String(label)) : label;

  const indicatorClass = {
    dot: styles.tooltipDot,
    line: styles.tooltipLine,
    dashed: styles.tooltipDashed,
  }[indicator];

  return (
    <div className={tooltipClasses}>
      {!hideLabel && formattedLabel && <div className={styles.tooltipLabel}>{formattedLabel}</div>}
      <div className={styles.tooltipItems}>
        {payload.map((entry) => {
          const dataKey = String(entry.dataKey || entry.name);
          const configItem = config[dataKey];
          const displayLabel = configItem?.label || dataKey;
          const displayValue = formatter ? formatter(entry.value, dataKey) : String(entry.value);
          const color = entry.color || configItem?.color;

          return (
            <div key={dataKey} className={styles.tooltipItem}>
              {!hideIndicator && (
                <span
                  className={indicatorClass}
                  style={{ backgroundColor: color, borderColor: color }}
                />
              )}
              <span className={styles.tooltipName}>{displayLabel}</span>
              <span className={styles.tooltipValue}>{displayValue}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

ChartTooltipContent.displayName = 'ChartTooltipContent';

/**
 * ChartLegendContent — Orion-styled legend for Recharts
 */
export const ChartLegendContent: React.FC<ChartLegendContentProps> = ({ payload, className }) => {
  const config = useContext(ChartContext);

  if (!payload?.length) return null;

  const legendClasses = [styles.legend, className].filter(Boolean).join(' ');

  return (
    <div className={legendClasses}>
      {payload.map((entry) => {
        const dataKey = entry.dataKey || entry.value;
        const configItem = config[dataKey];
        const displayLabel = configItem?.label || dataKey;
        const color = entry.color || configItem?.color;

        return (
          <div key={dataKey} className={styles.legendItem}>
            <span className={styles.legendDot} style={{ backgroundColor: color }} />
            <span>{displayLabel}</span>
          </div>
        );
      })}
    </div>
  );
};

ChartLegendContent.displayName = 'ChartLegendContent';

/**
 * ChartGradient — SVG linearGradient helper for elegant area chart fills.
 *
 * Place inside `<defs>` within a Recharts chart, then reference via `fill="url(#id)"`.
 *
 * @example
 * ```tsx
 * <AreaChart data={data}>
 *   <defs>
 *     <ChartGradient id="fillRevenue" color="var(--chart-1)" />
 *   </defs>
 *   <Area dataKey="revenue" fill="url(#fillRevenue)" fillOpacity={1} />
 * </AreaChart>
 * ```
 */
export const ChartGradient: React.FC<ChartGradientProps> = ({
  id,
  color,
  startOpacity = 0.4,
  endOpacity = 0.05,
}) => (
  <linearGradient id={id} x1="0" y1="0" x2="0" y2="1">
    <stop offset="5%" stopColor={color} stopOpacity={startOpacity} />
    <stop offset="95%" stopColor={color} stopOpacity={endOpacity} />
  </linearGradient>
);

ChartGradient.displayName = 'ChartGradient';

// Re-export Recharts Tooltip and Legend for convenience
export const ChartTooltip = Tooltip;
export const ChartLegend = Legend;

/**
 * Chart Component Types
 *
 * Type definitions for the Orion Chart wrapper components.
 * Provides themed containers, tooltips, and legends for Recharts.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Configuration for a single data series in a chart
 */
export interface ChartConfigItem {
  /**
   * Display label for the series
   */
  label: string;

  /**
   * CSS color value or token variable (e.g., 'var(--color-brand-500)')
   */
  color: string;

  /**
   * Optional icon for the legend
   */
  icon?: ReactNode;
}

/**
 * Chart configuration object — maps data keys to display settings
 *
 * @example
 * ```ts
 * const config: ChartConfig = {
 *   revenue: { label: 'Revenue', color: 'var(--color-brand-500)' },
 *   expenses: { label: 'Expenses', color: 'var(--status-error)' },
 * };
 * ```
 */
export type ChartConfig = Record<string, ChartConfigItem>;

/**
 * ChartContainer props — wraps Recharts with CSS variable injection
 */
export interface ChartContainerProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Chart configuration mapping data keys to colors/labels
   */
  config: ChartConfig;

  /**
   * The Recharts chart element
   */
  children: ReactNode;

  /**
   * Additional class name
   */
  className?: string;
}

/**
 * ChartTooltipContent props
 */
export interface ChartTooltipContentProps {
  /**
   * Whether to show the color indicator dot
   * @default true
   */
  indicator?: 'dot' | 'line' | 'dashed';

  /**
   * Hide the label text
   * @default false
   */
  hideLabel?: boolean;

  /**
   * Hide the indicator
   * @default false
   */
  hideIndicator?: boolean;

  /**
   * Custom label formatter
   */
  labelFormatter?: (label: string) => string;

  /**
   * Custom value formatter
   */
  formatter?: (value: number, name: string) => string;

  /**
   * Additional class name
   */
  className?: string;

  /**
   * Recharts injected props (passed automatically)
   */
  active?: boolean;
  payload?: Array<{
    name: string;
    value: number;
    dataKey: string;
    color: string;
    payload: Record<string, unknown>;
  }>;
  label?: string;
}

/**
 * ChartGradient props — SVG linearGradient helper for area charts
 */
export interface ChartGradientProps {
  /**
   * Unique ID for the gradient (referenced via `fill="url(#id)"`)
   */
  id: string;

  /**
   * CSS color value or token variable
   */
  color: string;

  /**
   * Opacity at the top of the gradient
   * @default 0.4
   */
  startOpacity?: number;

  /**
   * Opacity at the bottom of the gradient
   * @default 0.05
   */
  endOpacity?: number;
}

/**
 * ChartLegendContent props
 */
export interface ChartLegendContentProps {
  /**
   * Additional class name
   */
  className?: string;

  /**
   * Recharts injected props (passed automatically)
   */
  payload?: Array<{
    value: string;
    type: string;
    id: string;
    color: string;
    dataKey: string;
  }>;
}

/**
 * Chart Component Styles
 *
 * Themed wrapper, tooltip, and legend for Recharts.
 */

/* Container */
.container {
  width: 100%;
  font-family: var(--font-secondary);
}

/* ============================================================================
 * TOOLTIP
 * ============================================================================ */

.tooltip {
  background: var(--surface-base);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-control);
  box-shadow: var(--shadow-lg);
  padding: var(--spacing-3) var(--spacing-4);
  font-family: var(--font-secondary);
  font-size: var(--font-size-13);
}

.tooltipLabel {
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  margin-bottom: var(--spacing-2);
  padding-bottom: var(--spacing-2);
  border-bottom: 1px solid var(--border-subtle);
}

.tooltipItems {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.tooltipItem {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
}

.tooltipDot {
  width: 8px;
  height: 8px;
  border-radius: var(--radius-full);
  flex-shrink: 0;
}

.tooltipLine {
  width: 12px;
  height: 2px;
  flex-shrink: 0;
}

.tooltipDashed {
  width: 12px;
  height: 2px;
  flex-shrink: 0;
  border-top: 2px dashed;
}

.tooltipName {
  color: var(--text-secondary);
}

.tooltipValue {
  color: var(--text-primary);
  font-weight: var(--font-weight-medium);
  margin-left: auto;
  padding-left: var(--spacing-3);
}

/* ============================================================================
 * LEGEND
 * ============================================================================ */

.legend {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--spacing-4);
  padding-top: var(--spacing-3);
  font-family: var(--font-secondary);
  font-size: var(--font-size-13);
}

.legendItem {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  color: var(--text-secondary);
}

.legendDot {
  width: 8px;
  height: 8px;
  border-radius: var(--radius-full);
  flex-shrink: 0;
}
