/**
 * Chat Component
 *
 * A flexible compound component for building AI chat interfaces.
 * Supports multimodal content including text, images, audio, files, and code.
 *
 * @example
 * ```tsx
 * <Chat>
 *   <Chat.Header title="AI Assistant" />
 *   <Chat.Messages>
 *     {messages.map(msg => (
 *       <Chat.Message
 *         key={msg.id}
 *         role={msg.role}
 *         content={msg.content}
 *       />
 *     ))}
 *     {isTyping && <Chat.TypingIndicator />}
 *   </Chat.Messages>
 *   <Chat.Input onSend={handleSend} />
 * </Chat>
 * ```
 */

import React from 'react';
import type { ChatProps } from './Chat.types';
import {
  ChatMessage,
  ChatMessages,
  ChatInput,
  ChatHeader,
  ChatTypingIndicator,
  ChatCodeBlock,
  ChatMarkdown,
  ChatImagePreview,
  ChatAudioPlayer,
  ChatVoiceRecorder,
  ChatFileUpload,
  ChatAttachment,
  ChatSidebar,
} from './components';
import styles from './Chat.module.css';

// Main Chat container
const ChatRoot: React.FC<ChatProps> = ({ children, className, ...rest }) => {
  return (
    <div
      className={[styles.chat, className].filter(Boolean).join(' ')}
      role="region"
      aria-label="Chat interface"
      {...rest}
    >
      {children}
    </div>
  );
};

ChatRoot.displayName = 'Chat';

// Type for the compound component
type ChatComponent = React.FC<ChatProps> & {
  Header: typeof ChatHeader;
  Messages: typeof ChatMessages;
  Message: typeof ChatMessage;
  Input: typeof ChatInput;
  TypingIndicator: typeof ChatTypingIndicator;
  CodeBlock: typeof ChatCodeBlock;
  Markdown: typeof ChatMarkdown;
  ImagePreview: typeof ChatImagePreview;
  AudioPlayer: typeof ChatAudioPlayer;
  VoiceRecorder: typeof ChatVoiceRecorder;
  FileUpload: typeof ChatFileUpload;
  Attachment: typeof ChatAttachment;
  Sidebar: typeof ChatSidebar;
};

// Attach sub-components
export const Chat = ChatRoot as ChatComponent;
Chat.Header = ChatHeader;
Chat.Messages = ChatMessages;
Chat.Message = ChatMessage;
Chat.Input = ChatInput;
Chat.TypingIndicator = ChatTypingIndicator;
Chat.CodeBlock = ChatCodeBlock;
Chat.Markdown = ChatMarkdown;
Chat.ImagePreview = ChatImagePreview;
Chat.AudioPlayer = ChatAudioPlayer;
Chat.VoiceRecorder = ChatVoiceRecorder;
Chat.FileUpload = ChatFileUpload;
Chat.Attachment = ChatAttachment;
Chat.Sidebar = ChatSidebar;

// Sub-component display names
Chat.Header.displayName = 'Chat.Header';
Chat.Messages.displayName = 'Chat.Messages';
Chat.Message.displayName = 'Chat.Message';
Chat.Input.displayName = 'Chat.Input';
Chat.TypingIndicator.displayName = 'Chat.TypingIndicator';
Chat.CodeBlock.displayName = 'Chat.CodeBlock';
Chat.Markdown.displayName = 'Chat.Markdown';
Chat.ImagePreview.displayName = 'Chat.ImagePreview';
Chat.AudioPlayer.displayName = 'Chat.AudioPlayer';
Chat.VoiceRecorder.displayName = 'Chat.VoiceRecorder';
Chat.FileUpload.displayName = 'Chat.FileUpload';
Chat.Attachment.displayName = 'Chat.Attachment';
Chat.Sidebar.displayName = 'Chat.Sidebar';

/**
 * Chat Component Types
 *
 * Type definitions for the Orion Chat component - an AI-first chat interface
 * with multimodal support (text, images, audio, files, code).
 */

import type { HTMLAttributes, ReactNode, TextareaHTMLAttributes, RefObject } from 'react';

// ============================================================================
// CORE ENUMS & TYPES
// ============================================================================

/**
 * Message role types
 */
export type ChatRole = 'user' | 'assistant' | 'system';

/**
 * Message delivery status
 */
export type MessageStatus = 'sending' | 'sent' | 'error' | 'streaming';

/**
 * Attachment types for multimodal support
 */
export type AttachmentType = 'image' | 'audio' | 'file' | 'code';

/**
 * Message reaction types (feedback)
 */
export type MessageReaction = 'like' | 'dislike';

// ============================================================================
// ATTACHMENT INTERFACES
// ============================================================================

/**
 * Base attachment interface
 */
export interface ChatAttachment {
  /** Unique identifier for the attachment */
  id: string;
  /** Type of attachment */
  type: AttachmentType;
  /** Display name for the attachment */
  name: string;
  /** URL for media files (images, audio, etc.) */
  url?: string;
  /** Content for code blocks */
  content?: string;
  /** Programming language for syntax highlighting */
  language?: string;
  /** MIME type of the file */
  mimeType?: string;
  /** File size in bytes */
  size?: number;
  /** Audio duration in seconds */
  duration?: number;
  /** Thumbnail URL for images */
  thumbnail?: string;
}

// ============================================================================
// MESSAGE INTERFACES
// ============================================================================

/**
 * Chat message structure
 */
export interface ChatMessage {
  /** Unique identifier for the message */
  id: string;
  /** Message sender role */
  role: ChatRole;
  /** Message content (supports Markdown) */
  content: string;
  /** Timestamp of the message */
  timestamp?: Date;
  /** Message delivery status */
  status?: MessageStatus;
  /** Attached files, images, audio, or code */
  attachments?: ChatAttachment[];
  /** Whether the message is currently streaming */
  isStreaming?: boolean;
}

/**
 * Conversation for sidebar listing
 */
export interface ChatConversation {
  /** Unique identifier for the conversation */
  id: string;
  /** Conversation title */
  title: string;
  /** Preview of the last message */
  preview?: string;
  /** Last update timestamp */
  updatedAt: Date;
  /** Total number of messages */
  messageCount?: number;
}

// ============================================================================
// VOICE RECORDER STATE
// ============================================================================

/**
 * Voice recorder state interface
 */
export interface VoiceRecorderState {
  /** Whether currently recording */
  isRecording: boolean;
  /** Recording duration in seconds */
  duration: number;
  /** Recorded audio blob */
  audioBlob?: Blob;
  /** Recording error */
  error?: string;
}

// ============================================================================
// COMPONENT PROPS
// ============================================================================

/**
 * Main Chat container props
 */
export interface ChatProps extends HTMLAttributes<HTMLDivElement> {
  /** Chat content (compound components) */
  children?: ReactNode;
}

/**
 * Chat Header props
 */
export interface ChatHeaderProps extends HTMLAttributes<HTMLDivElement> {
  /** Title displayed in the header */
  title?: string;
  /** Subtitle or description */
  subtitle?: string;
  /** Avatar or icon to display */
  avatar?: ReactNode;
  /** Actions (buttons, menu) on the right side */
  actions?: ReactNode;
  /** Custom content */
  children?: ReactNode;
}

/**
 * Chat Messages container props
 */
export interface ChatMessagesProps extends HTMLAttributes<HTMLDivElement> {
  /** Message content */
  children?: ReactNode;
  /** Reference for scroll control */
  scrollRef?: RefObject<HTMLDivElement>;
  /** Enable auto-scroll to latest message */
  autoScroll?: boolean;
}

/**
 * Individual chat message props
 */
export interface ChatMessageProps extends HTMLAttributes<HTMLDivElement> {
  /** Message sender role */
  role: ChatRole;
  /** Message content (supports Markdown) */
  content: string;
  /** Timestamp of the message */
  timestamp?: Date;
  /** Message delivery status */
  status?: MessageStatus;
  /** Attached files, images, audio, or code */
  attachments?: ChatAttachment[];
  /** Custom avatar element */
  avatar?: ReactNode;
  /** Whether to show the avatar (defaults to false) */
  showAvatar?: boolean;
  /** Action buttons (copy, regenerate, etc.) */
  actions?: ReactNode;
  /** Whether the message is streaming */
  isStreaming?: boolean;
  /** Callback when an image attachment is clicked */
  onImageClick?: (attachment: ChatAttachment) => void;
  /** Callback to retry sending a failed message */
  onRetry?: () => void;
  /** Current reaction on this message */
  reaction?: MessageReaction;
  /** Callback when user reacts to a message */
  onReaction?: (reaction: MessageReaction) => void;
  /** Custom content instead of default rendering */
  children?: ReactNode;
}

/**
 * Chat input props
 */
export interface ChatInputProps extends Omit<
  TextareaHTMLAttributes<HTMLTextAreaElement>,
  'onSubmit'
> {
  /** Callback when message is sent */
  onSend?: (message: string, attachments?: File[]) => void;
  /** Allow file attachments (images, files) */
  allowAttachments?: boolean;
  /** Allow voice recording */
  allowVoiceRecording?: boolean;
  /** Allow audio file upload */
  allowAudioUpload?: boolean;
  /** Whether input is disabled */
  disabled?: boolean;
  /** Maximum character count */
  maxLength?: number;
  /** Accepted file types for upload */
  acceptedFileTypes?: string[];
  /** Whether a message is being sent */
  isLoading?: boolean;
  /** Pending attachments to display */
  pendingAttachments?: File[];
  /** Callback to remove a pending attachment */
  onRemoveAttachment?: (index: number) => void;
}

/**
 * Typing indicator props
 */
export interface ChatTypingIndicatorProps extends HTMLAttributes<HTMLDivElement> {
  /** Label text (default: "Assistant is typing") */
  label?: string;
}

/**
 * Code block props
 */
export interface ChatCodeBlockProps extends HTMLAttributes<HTMLDivElement> {
  /** Code content */
  code: string;
  /** Programming language for syntax highlighting */
  language?: string;
  /** Show line numbers */
  showLineNumbers?: boolean;
  /** Show copy button */
  showCopyButton?: boolean;
  /** Maximum height before scrolling */
  maxHeight?: string;
}

/**
 * Markdown renderer props
 */
export interface ChatMarkdownProps extends HTMLAttributes<HTMLDivElement> {
  /** Markdown content to render */
  content: string;
}

// ============================================================================
// ATTACHMENT COMPONENT PROPS
// ============================================================================

/**
 * Image preview props
 */
export interface ChatImagePreviewProps extends HTMLAttributes<HTMLDivElement> {
  /** Image source URL */
  src: string;
  /** Alt text for accessibility */
  alt?: string;
  /** Thumbnail URL (optional, uses src if not provided) */
  thumbnail?: string;
  /** Callback when image is clicked */
  onClick?: () => void;
  /** Enable lightbox on click */
  enableLightbox?: boolean;
}

/**
 * Audio player props
 */
export interface ChatAudioPlayerProps extends HTMLAttributes<HTMLDivElement> {
  /** Audio source URL */
  src: string;
  /** Duration in seconds (optional, auto-detected if not provided) */
  duration?: number;
  /** Audio title/name */
  title?: string;
}

/**
 * Voice recorder props
 */
export interface ChatVoiceRecorderProps extends HTMLAttributes<HTMLDivElement> {
  /** Callback when recording is complete */
  onRecordingComplete?: (blob: Blob) => void;
  /** Callback when recording is cancelled */
  onCancel?: () => void;
  /** Maximum recording duration in seconds */
  maxDuration?: number;
}

/**
 * File upload zone props
 */
export interface ChatFileUploadProps extends HTMLAttributes<HTMLDivElement> {
  /** Callback when files are selected */
  onFilesSelected?: (files: File[]) => void;
  /** Accepted file types */
  accept?: string[];
  /** Allow multiple files */
  multiple?: boolean;
  /** Maximum file size in bytes */
  maxSize?: number;
  /** Custom upload button content */
  children?: ReactNode;
}

/**
 * Attachment preview props (unified)
 */
export interface ChatAttachmentPreviewProps extends HTMLAttributes<HTMLDivElement> {
  /** Attachment data */
  attachment: ChatAttachment;
  /** Show remove button */
  removable?: boolean;
  /** Callback when remove is clicked */
  onRemove?: () => void;
  /** Callback when attachment is clicked */
  onClick?: () => void;
}

// ============================================================================
// SIDEBAR PROPS
// ============================================================================

/**
 * Chat sidebar props
 */
export interface ChatSidebarProps extends HTMLAttributes<HTMLDivElement> {
  /** List of conversations */
  conversations?: ChatConversation[];
  /** Currently active conversation ID */
  activeConversationId?: string;
  /** Callback when a conversation is selected */
  onSelectConversation?: (id: string) => void;
  /** Callback when new conversation is created */
  onNewConversation?: () => void;
  /** Callback when a conversation is deleted */
  onDeleteConversation?: (id: string) => void;
  /** Callback when search query changes */
  onSearch?: (query: string) => void;
  /** Custom header content */
  header?: ReactNode;
  /** Custom footer content */
  footer?: ReactNode;
  /** Whether sidebar is collapsed (mobile) */
  collapsed?: boolean;
  /** Callback when collapse state changes */
  onCollapsedChange?: (collapsed: boolean) => void;
}

// ============================================================================
// SECTION & TEMPLATE PROPS
// ============================================================================

/**
 * ChatSection props (sidebar + chat)
 */
export interface ChatSectionProps extends HTMLAttributes<HTMLDivElement> {
  /** List of conversations for sidebar */
  conversations?: ChatConversation[];
  /** Currently active conversation ID */
  activeConversationId?: string;
  /** Messages for the active conversation */
  messages?: ChatMessage[];
  /** Callback when a message is sent */
  onSendMessage?: (message: string, attachments?: File[]) => void;
  /** Callback when a conversation is selected */
  onSelectConversation?: (id: string) => void;
  /** Callback when new conversation is created */
  onNewConversation?: () => void;
  /** Callback when a conversation is deleted */
  onDeleteConversation?: (id: string) => void;
  /** Whether assistant is typing */
  isTyping?: boolean;
  /** Whether message is being sent */
  isLoading?: boolean;
  /** Chat header configuration */
  header?: {
    title?: string;
    subtitle?: string;
    avatar?: ReactNode;
    actions?: ReactNode;
  };
  /** Input configuration */
  inputConfig?: {
    placeholder?: string;
    allowAttachments?: boolean;
    allowVoiceRecording?: boolean;
    maxLength?: number;
  };
  /** Hide sidebar (full-width chat) */
  hideSidebar?: boolean;
}

/**
 * ChatPageTemplate props (full page layout)
 */
export interface ChatPageTemplateProps extends HTMLAttributes<HTMLDivElement> {
  // Sidebar props
  /** List of conversations */
  conversations?: ChatConversation[];
  /** Currently active conversation ID */
  activeConversationId?: string;
  /** Callback when a conversation is selected */
  onSelectConversation?: (id: string) => void;
  /** Callback when new conversation is created */
  onNewConversation?: () => void;
  /** Callback when a conversation is deleted */
  onDeleteConversation?: (id: string) => void;

  // Chat props
  /** Messages for the active conversation */
  messages?: ChatMessage[];
  /** Callback when a message is sent */
  onSendMessage?: (message: string, attachments?: File[]) => void;
  /** Whether assistant is typing */
  isTyping?: boolean;
  /** Whether message is being sent */
  isLoading?: boolean;

  // Header props
  /** User information for header */
  user?: {
    name: string;
    avatar?: string;
  };
  /** Callback when user logs out */
  onLogout?: () => void;

  // Customization
  /** Custom logo element */
  logo?: ReactNode;
  /** Chat input configuration */
  inputConfig?: ChatSectionProps['inputConfig'];
}

// ============================================================================
// LIGHTBOX PROPS
// ============================================================================

/**
 * Image lightbox props
 */
export interface ChatLightboxProps extends HTMLAttributes<HTMLDivElement> {
  /** Image source URL */
  src: string;
  /** Alt text for accessibility */
  alt?: string;
  /** Whether lightbox is open */
  isOpen: boolean;
  /** Callback when lightbox is closed */
  onClose: () => void;
}

/**
 * Chat Component Styles
 *
 * AI-first chat interface with multimodal support.
 * Uses Orion semantic tokens exclusively.
 */

/* ============================================================================
 * MAIN CONTAINER
 * ============================================================================ */

.chat {
  display: flex;
  flex-direction: column;
  height: 100%;
  flex: 1;
  min-height: 0;
  background: var(--surface-base);
  border-radius: var(--radius-container);
  overflow: hidden;
  position: relative;
}

/* ============================================================================
 * HEADER
 * ============================================================================ */

.header {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  padding: var(--spacing-3) var(--spacing-4);
  background: var(--surface-base);
  border-bottom: 1px solid var(--border-subtle);
  flex-shrink: 0;
}

.headerAvatar {
  flex-shrink: 0;
}

.headerContent {
  flex: 1;
  min-width: 0;
}

.headerTitle {
  font-size: var(--font-size-16);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  line-height: var(--line-height-tight);
  margin: 0;
}

.headerSubtitle {
  font-size: var(--font-size-14);
  color: var(--text-secondary);
  line-height: var(--line-height-normal);
  margin: 0;
}

.headerActions {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  flex-shrink: 0;
}

/* ============================================================================
 * MESSAGES CONTAINER
 * ============================================================================ */

.messages {
  flex: 1;
  min-height: 0;
  overflow-y: auto;
  overflow-x: hidden;
  padding: var(--spacing-4);
  padding-bottom: var(--spacing-32);
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
  scroll-behavior: smooth;
}

.messagesInner {
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
  gap: var(--spacing-4);
  max-width: var(--chat-max-width);
  width: 100%;
  margin: 0 auto;
}

/* ============================================================================
 * MESSAGE BUBBLE
 * ============================================================================ */

.message {
  display: flex;
  gap: var(--spacing-3);
  max-width: 100%;
  animation: messageIn var(--transition-spring);
}

@keyframes messageIn {
  from {
    opacity: 0;
    transform: translateY(8px) scale(0.98);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

@media (prefers-reduced-motion: reduce) {
  .message {
    animation: none;
  }
}

.messageUser {
  flex-direction: row-reverse;
}

.messageAssistant {
  /* Selector for assistant messages â€” width handled via .messageContent */
}

/* Differential width by conversational role */
.messageUser .messageContent {
  flex: 0 1 auto;
  max-width: 75%;
  align-items: flex-end;
}

.messageAssistant .messageContent {
  flex: 0 1 auto;
  max-width: 90%;
}

.messageAvatar {
  flex-shrink: 0;
  width: var(--size-avatar-md);
  height: var(--size-avatar-md);
  border-radius: var(--radius-full);
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--surface-subtle);
  color: var(--text-secondary);
  font-size: var(--font-size-14);
}

.messageAvatarUser {
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
}

.messageAvatarAssistant {
  background: var(--surface-layer);
  color: var(--text-brand);
}

.messageContent {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
}

.messageBubble {
  padding: var(--spacing-3) var(--spacing-4);
  border-radius: var(--chat-bubble-radius);
  line-height: var(--line-height-relaxed);
  overflow-wrap: break-word;
  overflow-wrap: break-word;
}

.messageBubbleUser {
  background: var(--interactive-primary);
  color: var(--text-on-brand-primary);
  box-shadow: var(--shadow-sm);
  width: fit-content;
  max-width: 100%;
}

.messageBubbleAssistant {
  background: var(--surface-layer);
  color: var(--text-primary);
  width: fit-content;
  max-width: 100%;
}

.messageBubbleSystem {
  background: var(--surface-layer);
  color: var(--text-secondary);
  font-size: var(--font-size-14);
  text-align: center;
  border-radius: var(--chat-bubble-radius);
  padding: var(--spacing-2) var(--spacing-3);
}

.messageAttachments {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-2);
  margin-top: var(--spacing-2);
}

.messageUser .messageAttachments {
  justify-content: flex-end;
}

.messageFooter {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: 0 var(--spacing-1);
}

.messageUser .messageFooter {
  justify-content: flex-end;
}

.messageTimestamp {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
}

.messageStatus {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
}

.messageStatusError {
  color: var(--status-error);
}

.messageActions {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  opacity: 0;
  transition: opacity var(--transition-fast);
}

.message:hover .messageActions {
  opacity: 1;
}

/* Streaming cursor */
.streamingCursor {
  display: inline-block;
  width: var(--spacing-05);
  height: 1.1em;
  background: currentColor;
  margin-left: var(--spacing-05);
  border-radius: 1px;
  vertical-align: text-bottom;
  animation: cursorPulse 1.2s ease-in-out infinite;
}

@keyframes cursorPulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.15;
  }
}

/* ============================================================================
 * THINKING STATE (Processing Indicator)
 *
 * Circular conic-gradient ring with smooth, continuous rotation.
 * The gradient tail fades from transparent to solid, creating a
 * fluid sweep rather than a hard-cut border spinner.
 * ============================================================================ */

.thinkingBubble {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 40px;
  min-width: 56px;
}

.thinkingSpinner {
  display: block;
  width: 20px;
  height: 20px;
  border-radius: var(--radius-full);
  background: conic-gradient(from 0deg, transparent 0%, transparent 10%, var(--text-tertiary) 100%);
  -webkit-mask: radial-gradient(
    farthest-side,
    transparent calc(100% - 2.5px),
    var(--text-primary) calc(100% - 2px)
  );
  mask: radial-gradient(farthest-side, transparent calc(100% - 2.5px), var(--text-primary) calc(100% - 2px));
  animation: thinkingSpin 1.2s linear infinite;
}

@keyframes thinkingSpin {
  to {
    transform: rotate(360deg);
  }
}

@media (prefers-reduced-motion: reduce) {
  .thinkingSpinner {
    animation: none;
    background: none;
    -webkit-mask: none;
    mask: none;
    border: 2px solid var(--border-subtle);
    border-top-color: var(--text-tertiary);
  }
}

/* ============================================================================
 * INPUT AREA
 * ============================================================================ */

.inputContainer {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: var(--spacing-10) var(--spacing-4) var(--spacing-4);
  background: linear-gradient(to bottom, transparent 0%, var(--surface-base) 50%);
  pointer-events: none;
  z-index: var(--z-dropdown);
}

.inputWrapper {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
  max-width: var(--chat-max-width);
  margin: 0 auto;
  pointer-events: auto;
}

.inputBox {
  display: flex;
  align-items: flex-end;
  gap: var(--spacing-2);
  background: var(--surface-base);
  border: 1px solid var(--border-default);
  border-radius: var(--radius-xl);
  padding: var(--spacing-3) var(--spacing-4);
  box-shadow: var(--shadow-card-sm);
  transition: all var(--transition-fast);
}

.inputBox:focus-within {
  border-color: var(--interactive-primary);
  box-shadow: var(--shadow-md);
}

.inputActions {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  flex-shrink: 0;
}

.inputTextarea {
  flex: 1;
  min-height: var(--chat-textarea-min-height);
  max-height: var(--chat-textarea-max-height);
  padding: var(--spacing-1) 0;
  background: transparent;
  border: none;
  outline: none;
  font-family: var(--font-secondary);
  font-size: var(--font-size-16);
  color: var(--text-primary);
  line-height: var(--line-height-relaxed);
  resize: none;
  overflow-y: auto;
}

.inputTextarea::placeholder {
  color: var(--text-tertiary);
}

.inputSendButton {
  flex-shrink: 0;
}

.inputButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-avatar-md);
  height: var(--size-avatar-md);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-control);
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.inputButton:hover:not(:disabled) {
  background: var(--surface-layer);
  color: var(--text-primary);
}

.inputButton:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.inputButtonPrimary {
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
  border-radius: var(--radius-full);
}

.inputButtonPrimary:hover:not(:disabled) {
  background: var(--interactive-primary-hover);
  color: var(--interactive-primary-text);
}

/* Pending attachments */
.pendingAttachments {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-3);
  background: var(--surface-subtle);
  border-radius: var(--chat-bubble-radius);
}

.pendingAttachment {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-1) var(--spacing-2);
  background: var(--surface-base);
  border: 1px solid var(--border-subtle);
  border-radius: var(--chat-bubble-radius);
  font-size: var(--font-size-14);
  color: var(--text-primary);
}

.pendingAttachmentRemove {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-icon-md);
  height: var(--size-icon-md);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-full);
  color: var(--text-tertiary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.pendingAttachmentRemove:hover {
  background: var(--surface-subtle);
  color: var(--status-error);
}

/* ============================================================================
 * CODE BLOCK
 * ============================================================================ */

.codeBlock {
  position: relative;
  margin: var(--spacing-2) 0;
  border-radius: var(--chat-bubble-radius);
  overflow: hidden;
  background: var(--surface-sunken);
}

.codeBlockHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-2) var(--spacing-3);
  background: var(--surface-layer);
  border-bottom: 1px solid var(--border-subtle);
}

.codeBlockLanguage {
  font-size: var(--font-size-12);
  font-family: var(--font-mono);
  color: var(--text-secondary);
  text-transform: uppercase;
}

.codeBlockCopy {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  padding: var(--spacing-1) var(--spacing-2);
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  font-size: var(--font-size-12);
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.codeBlockCopy:hover {
  background: var(--surface-subtle);
  color: var(--text-primary);
}

.codeBlockCopied {
  color: var(--status-success);
}

.codeBlockContent {
  overflow-x: auto;
  padding: var(--spacing-3);
  font-family: var(--font-mono);
  font-size: var(--font-size-14);
  line-height: var(--line-height-relaxed);
}

/* ============================================================================
 * MARKDOWN STYLES
 * ============================================================================ */

.markdown {
  font-family: var(--font-secondary);
  font-size: var(--font-size-16);
  line-height: var(--line-height-relaxed);
  color: inherit;
}

.markdown p {
  margin: 0 0 var(--spacing-2);
}

.markdown p:last-child {
  margin-bottom: 0;
}

.markdown strong {
  font-weight: var(--font-weight-semibold);
}

.markdown em {
  font-style: italic;
}

.markdown a {
  color: var(--text-brand);
  text-decoration: underline;
  text-decoration-color: transparent;
  transition: text-decoration-color var(--transition-fast);
}

.markdown a:hover {
  text-decoration-color: currentColor;
}

.markdown ul,
.markdown ol {
  margin: var(--spacing-2) 0;
  padding-left: var(--spacing-6);
}

.markdown li {
  margin: var(--spacing-1) 0;
}

.markdown h1,
.markdown h2,
.markdown h3,
.markdown h4,
.markdown h5,
.markdown h6 {
  font-weight: var(--font-weight-semibold);
  line-height: var(--line-height-tight);
  margin: var(--spacing-4) 0 var(--spacing-2);
}

.markdown h1:first-child,
.markdown h2:first-child,
.markdown h3:first-child {
  margin-top: 0;
}

.markdown h1 {
  font-size: var(--font-size-24);
}
.markdown h2 {
  font-size: var(--font-size-20);
}
.markdown h3 {
  font-size: var(--font-size-18);
}
.markdown h4 {
  font-size: var(--font-size-16);
}

.markdown blockquote {
  margin: var(--spacing-3) 0;
  padding: var(--spacing-2) var(--spacing-4);
  border-left: 3px solid var(--border-strong);
  background: var(--surface-layer);
  color: var(--text-secondary);
}

.markdown hr {
  margin: var(--spacing-4) 0;
  border: none;
  border-top: 1px solid var(--border-subtle);
}

.markdown table {
  width: 100%;
  margin: var(--spacing-3) 0;
  border-collapse: collapse;
  font-size: var(--font-size-14);
}

.markdown th,
.markdown td {
  padding: var(--spacing-2) var(--spacing-3);
  border: 1px solid var(--border-subtle);
  text-align: left;
}

.markdown th {
  background: var(--surface-layer);
  font-weight: var(--font-weight-medium);
}

.markdown code {
  padding: var(--spacing-05) var(--spacing-2);
  background: var(--surface-layer);
  border-radius: var(--radius-sm);
  font-family: var(--font-mono);
  font-size: 0.9em;
}

.markdown pre {
  margin: var(--spacing-3) 0;
  padding: 0;
  background: transparent;
  border-radius: var(--radius-sm);
  overflow: hidden;
}

.markdown pre code {
  padding: 0;
  background: transparent;
  border-radius: 0;
}

/* User bubble markdown - lighter colors */
.markdownUser a {
  color: inherit;
  opacity: 0.9;
}

.markdownUser code {
  background: var(--overlay-light);
}

/* ============================================================================
 * ATTACHMENT COMPONENTS
 * ============================================================================ */

/* Image Preview */
.imagePreview {
  position: relative;
  border-radius: var(--chat-bubble-radius);
  overflow: hidden;
  cursor: pointer;
  transition: transform var(--transition-fast);
}

.imagePreview:hover {
  transform: scale(1.02);
}

.imagePreviewImg {
  display: block;
  max-width: var(--chat-image-max-width);
  max-height: var(--chat-image-max-height);
  width: auto;
  height: auto;
  object-fit: cover;
  border-radius: var(--chat-bubble-radius);
}

/* Audio Player */
.audioPlayer {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-3);
  background: var(--surface-layer);
  border-radius: var(--chat-bubble-radius);
  min-width: 200px;
}

.audioPlayerButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-avatar-sm);
  height: var(--size-avatar-sm);
  padding: 0;
  background: var(--interactive-primary);
  border: none;
  border-radius: var(--radius-full);
  color: var(--interactive-primary-text);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.audioPlayerButton:hover {
  background: var(--interactive-primary-hover);
}

.audioPlayerProgress {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.audioPlayerTrack {
  height: 4px;
  background: var(--surface-subtle);
  border-radius: var(--radius-full);
  overflow: hidden;
}

.audioPlayerFill {
  height: 100%;
  background: var(--interactive-primary);
  transition: width 0.1s linear;
}

.audioPlayerTime {
  display: flex;
  justify-content: space-between;
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  font-family: var(--font-mono);
}

/* Voice Recorder */
.voiceRecorder {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  padding: var(--spacing-3);
  background: var(--surface-layer);
  border-radius: var(--chat-bubble-radius);
}

.voiceRecorderButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-avatar-lg);
  height: var(--size-avatar-lg);
  padding: 0;
  background: var(--status-error);
  border: none;
  border-radius: var(--radius-full);
  color: var(--color-neutral-0);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.voiceRecorderButton:hover {
  opacity: 0.9;
}

.voiceRecorderButtonIdle {
  background: var(--interactive-primary);
}

.voiceRecorderInfo {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.voiceRecorderDuration {
  font-size: var(--font-size-18);
  font-family: var(--font-mono);
  color: var(--text-primary);
}

.voiceRecorderLabel {
  font-size: var(--font-size-14);
  color: var(--text-secondary);
}

.voiceRecorderWaveform {
  display: flex;
  align-items: center;
  gap: var(--spacing-05);
  height: 32px;
}

.voiceRecorderBar {
  width: 3px;
  background: var(--status-error);
  border-radius: var(--radius-full);
  animation: waveform 0.5s ease-in-out infinite;
}

.voiceRecorderBar:nth-child(2) {
  animation-delay: 0.1s;
}
.voiceRecorderBar:nth-child(3) {
  animation-delay: 0.2s;
}
.voiceRecorderBar:nth-child(4) {
  animation-delay: 0.3s;
}
.voiceRecorderBar:nth-child(5) {
  animation-delay: 0.4s;
}

@keyframes waveform {
  0%,
  100% {
    height: 8px;
  }
  50% {
    height: 24px;
  }
}

.voiceRecorderActions {
  display: flex;
  gap: var(--spacing-2);
}

/* File Upload */
.fileUpload {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-6);
  border: 2px dashed var(--border-default);
  border-radius: var(--radius-container);
  background: var(--surface-subtle);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.fileUpload:hover {
  border-color: var(--interactive-primary);
  background: var(--surface-layer);
}

.fileUploadDragging {
  border-color: var(--interactive-primary);
  background: var(--interactive-primary-subtle);
}

.fileUploadIcon {
  color: var(--text-tertiary);
  margin-bottom: var(--spacing-2);
}

.fileUploadText {
  font-size: var(--font-size-14);
  color: var(--text-secondary);
  text-align: center;
}

.fileUploadHint {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  margin-top: var(--spacing-1);
}

/* Attachment Preview */
.attachmentPreview {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2);
  background: var(--surface-layer);
  border: 1px solid var(--border-subtle);
  border-radius: var(--chat-bubble-radius);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.attachmentPreview:hover {
  border-color: var(--border-default);
}

.attachmentPreviewIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-avatar-sm);
  height: var(--size-avatar-sm);
  background: var(--surface-subtle);
  border-radius: var(--radius-sm);
  color: var(--text-secondary);
}

.attachmentPreviewInfo {
  flex: 1;
  min-width: 0;
}

.attachmentPreviewName {
  font-size: var(--font-size-14);
  color: var(--text-primary);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.attachmentPreviewMeta {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
}

.attachmentPreviewRemove {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-icon-lg);
  height: var(--size-icon-lg);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-full);
  color: var(--text-tertiary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.attachmentPreviewRemove:hover {
  background: var(--surface-subtle);
  color: var(--status-error);
}

/* ============================================================================
 * LIGHTBOX
 * ============================================================================ */

.lightbox {
  position: fixed;
  inset: 0;
  z-index: var(--z-toast);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-4);
  background: var(--overlay-dark-heavy);
  animation: lightboxIn 0.2s ease-out;
}

@keyframes lightboxIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.lightboxImage {
  max-width: 100%;
  max-height: 100%;
  object-fit: contain;
  border-radius: var(--radius-sm);
}

.lightboxClose {
  position: absolute;
  top: var(--spacing-4);
  right: var(--spacing-4);
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-avatar-md);
  height: var(--size-avatar-md);
  padding: 0;
  background: var(--overlay-light-subtle);
  border: none;
  border-radius: var(--radius-full);
  color: var(--color-neutral-0);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.lightboxClose:hover {
  background: var(--overlay-light);
}

/* ============================================================================
 * SIDEBAR
 * ============================================================================ */

.sidebar {
  display: flex;
  flex-direction: column;
  width: var(--size-sidebar-width);
  flex-shrink: 0;
  height: 100%;
  background: var(--surface-sunken);
  border-right: 1px solid var(--border-subtle);
  overflow: hidden;
}

.sidebarHeader {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-3) var(--spacing-4);
  border-bottom: 1px solid var(--border-subtle);
}

.sidebarNewButton {
  flex: 1;
}

.sidebarSearch {
  padding: var(--spacing-2) var(--spacing-4);
}

.sidebarSearchInput {
  width: 100%;
  padding: var(--spacing-2) var(--spacing-3);
  background: var(--surface-base);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-sm);
  font-size: var(--font-size-14);
  color: var(--text-primary);
  outline: none;
  transition: all var(--transition-fast);
}

.sidebarSearchInput::placeholder {
  color: var(--text-tertiary);
}

.sidebarSearchInput:focus {
  border-color: var(--interactive-primary);
}

.sidebarList {
  flex: 1;
  overflow-y: auto;
  padding: var(--spacing-2);
}

.sidebarItem {
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-3);
  width: 100%;
  padding: var(--spacing-3);
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  text-align: left;
  cursor: pointer;
  transition: all var(--transition-fast);
}

.sidebarItem:hover {
  background: var(--surface-base);
}

.sidebarItemActive {
  background: var(--surface-base);
}

.sidebarItemContent {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.sidebarItemTitle {
  display: block;
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);
  line-height: var(--line-height-tight);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.sidebarItemPreview {
  display: block;
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  line-height: var(--line-height-normal);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.sidebarItemDelete {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  width: var(--size-icon-lg);
  height: var(--size-icon-lg);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  color: var(--text-tertiary);
  cursor: pointer;
  opacity: 0;
  transition: all var(--transition-fast);
}

.sidebarItem:hover .sidebarItemDelete {
  opacity: 1;
}

.sidebarItemDelete:hover {
  background: var(--surface-subtle);
  color: var(--status-error);
}

.sidebarFooter {
  padding: var(--spacing-3) var(--spacing-4);
  border-top: 1px solid var(--border-subtle);
}

.sidebarEmpty {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-8);
  color: var(--text-tertiary);
  text-align: center;
}

.sidebarEmptyIcon {
  margin-bottom: var(--spacing-2);
}

/* Sidebar date group headers */
.sidebarGroupHeader {
  padding: var(--spacing-2) var(--spacing-3);
  margin-top: var(--spacing-3);
  font-size: var(--font-size-10);
  font-weight: var(--font-weight-medium);
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: var(--letter-spacing-wide);
}

.sidebarGroupHeader:first-child {
  margin-top: 0;
}

/* Sidebar collapsed (mobile) */
.sidebarCollapsed {
  position: fixed;
  left: 0;
  top: 0;
  bottom: 0;
  transform: translateX(-100%);
  z-index: var(--z-sticky);
  box-shadow: var(--shadow-lg);
}

.sidebarCollapsed.sidebarOpen {
  transform: translateX(0);
}

/* ============================================================================
 * SECTION LAYOUT (Sidebar + Chat)
 * ============================================================================ */

.section {
  display: flex;
  height: 100%;
  background: var(--surface-base);
  overflow: hidden;
}

.sectionMain {
  flex: 1;
  display: flex;
  flex-direction: column;
  min-width: 0;
  min-height: 0;
  overflow: hidden;
}

/* ============================================================================
 * PAGE TEMPLATE
 * ============================================================================ */

.pageTemplate {
  display: flex;
  height: 100vh;
  background: var(--surface-base);
  overflow: hidden;
}

.pageTemplateHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-3) var(--spacing-4);
  background: var(--surface-base);
  border-bottom: 1px solid var(--border-subtle);
}

.pageTemplateLogo {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
}

.pageTemplateUser {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
}

/* ============================================================================
 * SCROLL TO BOTTOM BUTTON
 * ============================================================================ */

.scrollToBottomWrapper {
  position: sticky;
  bottom: var(--spacing-20);
  display: flex;
  justify-content: center;
  pointer-events: none;
  z-index: var(--z-dropdown);
}

.scrollToBottomButton {
  pointer-events: auto;
  box-shadow: var(--shadow-md);
}

/* ============================================================================
 * INPUT SPINNER
 * ============================================================================ */

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.inputSpinner {
  display: inline-block;
  width: 20px;
  height: 20px;
  border: 2px solid currentColor;
  border-top-color: transparent;
  border-radius: var(--radius-full);
  animation: spin 1s linear infinite;
}

/* ============================================================================
 * PENDING ATTACHMENT SIZE
 * ============================================================================ */

.pendingAttachmentSize {
  color: var(--text-tertiary);
}

/* ============================================================================
 * VOICE RECORDER ERROR
 * ============================================================================ */

.voiceRecorderError {
  color: var(--status-error);
}

/* ============================================================================
 * HEADER AVATAR (assistant default style)
 * ============================================================================ */

.headerAvatarDefault {
  background: var(--surface-layer);
  color: var(--text-brand);
}

/* ============================================================================
 * SIDEBAR INLINE STYLE REPLACEMENTS
 * ============================================================================ */

.sidebarOverlay {
  position: fixed;
  inset: 0;
  background: var(--overlay-dark-heavy);
  z-index: var(--z-dropdown);
  display: none;
}

.sidebarNewButtonInner {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  width: 100%;
  justify-content: center;
}

.sidebarCloseButton {
  display: none;
}

.sidebarSearchWrapper {
  position: relative;
}

.sidebarSearchIcon {
  position: absolute;
  left: var(--spacing-3);
  top: 50%;
  transform: translateY(-50%);
  color: var(--text-tertiary);
  pointer-events: none;
}

.sidebarSearchInputWithIcon {
  padding-left: var(--spacing-8);
}

.sidebarItemIcon {
  flex-shrink: 0;
  color: var(--text-tertiary);
  margin-top: 2px;
}

.sidebarItemDate {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  flex-shrink: 0;
  margin-top: 2px;
  white-space: nowrap;
}

/* Sidebar delete confirmation */
.sidebarItemDeleteConfirm {
  font-size: var(--font-size-12);
  color: var(--status-error);
  font-weight: var(--font-weight-medium);
}

/* ============================================================================
 * MESSAGE RETRY BUTTON
 * ============================================================================ */

.messageRetry {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-1);
  padding: var(--spacing-1) var(--spacing-2);
  margin-left: var(--spacing-2);
  background: transparent;
  border: 1px solid var(--status-error);
  border-radius: var(--radius-sm);
  color: var(--status-error);
  font-size: var(--font-size-12);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.messageRetry:hover {
  background: var(--status-error);
  color: var(--color-neutral-0);
}

/* ============================================================================
 * MESSAGE REACTIONS
 * ============================================================================ */

.messageReactions {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  opacity: 0;
  transition: opacity var(--transition-fast);
}

.message:hover .messageReactions {
  opacity: 1;
}

.messageReactionButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--size-icon-lg);
  height: var(--size-icon-lg);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  color: var(--text-tertiary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.messageReactionButton:hover {
  background: var(--surface-layer);
  color: var(--text-primary);
}

.messageReactionActive {
  color: var(--interactive-primary);
  opacity: 1;
}

/* Keep reactions visible when one is active */
.messageReactionsVisible {
  opacity: 1;
}

/* ============================================================================
 * EMPTY STATE (Shared)
 * ============================================================================ */

.emptyState {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  gap: var(--spacing-4);
  color: var(--text-tertiary);
  text-align: center;
  padding: var(--spacing-8);
}

.emptyStateIcon {
  width: 64px;
  height: 64px;
  border-radius: var(--radius-full);
  background: var(--surface-subtle);
  display: flex;
  align-items: center;
  justify-content: center;
}

.emptyStateTitle {
  font-size: var(--font-size-20);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  margin-bottom: var(--spacing-2);
}

.emptyStateText {
  font-size: var(--font-size-14);
}

/* ============================================================================
 * PAGE TEMPLATE - Sidebar Header
 * ============================================================================ */

.pageTemplateSidebarHeader {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  width: 100%;
}

.pageTemplateSidebarHeaderLogo {
  flex: 1;
}

.pageTemplateSidebarHeaderLogoText {
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  font-size: var(--font-size-18);
}

/* ============================================================================
 * PAGE TEMPLATE - User Footer
 * ============================================================================ */

.pageTemplateUserButton {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  width: 100%;
  padding: var(--spacing-2);
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.pageTemplateUserButtonActive {
  background: var(--surface-base);
}

.pageTemplateUserAvatar {
  width: 32px;
  height: 32px;
  border-radius: var(--radius-full);
  object-fit: cover;
}

.pageTemplateUserAvatarFallback {
  width: 32px;
  height: 32px;
  border-radius: var(--radius-full);
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
  display: flex;
  align-items: center;
  justify-content: center;
}

.pageTemplateUserName {
  flex: 1;
  text-align: left;
  color: var(--text-primary);
  font-weight: var(--font-weight-medium);
}

/* ============================================================================
 * PAGE TEMPLATE - User Menu Dropdown
 * ============================================================================ */

.pageTemplateUserMenu {
  position: absolute;
  bottom: 100%;
  left: 0;
  right: 0;
  margin-bottom: var(--spacing-2);
  background: var(--surface-base);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-sm);
  box-shadow: var(--shadow-lg);
  overflow: hidden;
}

.pageTemplateMenuItem {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  width: 100%;
  padding: var(--spacing-2) var(--spacing-3);
  background: transparent;
  border: none;
  color: var(--text-primary);
  cursor: pointer;
  text-align: left;
  font-size: var(--font-size-14);
  transition: background var(--transition-fast);
}

.pageTemplateMenuItem:hover {
  background: var(--surface-subtle);
}

.pageTemplateMenuItemDanger {
  color: var(--status-error);
}

.pageTemplateMenuItemDanger:hover {
  background: var(--surface-subtle);
}

/* Page template click-outside overlay for user menu */
.pageTemplateMenuOverlay {
  position: fixed;
  inset: 0;
  z-index: var(--z-dropdown);
}

/* ============================================================================
 * RESPONSIVE
 * ============================================================================ */

@media (max-width: 768px) {
  .sidebar {
    position: fixed;
    left: 0;
    top: 0;
    bottom: 0;
    z-index: var(--z-sticky);
    transform: translateX(-100%);
    transition: transform var(--transition-smooth);
    box-shadow: var(--shadow-lg);
  }

  .sidebar.sidebarOpen {
    transform: translateX(0);
  }

  .sidebarOverlay {
    display: block;
  }

  .sidebarCloseButton {
    display: flex;
  }

  .messages {
    padding: var(--spacing-3);
    padding-bottom: var(--spacing-24);
  }

  .inputContainer {
    padding: var(--spacing-8) var(--spacing-3) var(--spacing-3);
  }

  .messageAvatar {
    width: var(--size-avatar-sm);
    height: var(--size-avatar-sm);
  }

  .messageBubble {
    padding: var(--spacing-2) var(--spacing-3);
  }

  .messageUser .messageContent {
    max-width: 85%;
  }

  .messageAssistant .messageContent {
    max-width: 100%;
  }

  .imagePreviewImg {
    max-width: var(--chat-image-max-width-mobile);
    max-height: var(--chat-image-max-height-mobile);
  }

  /* Section: mobile sidebar */
  .section > .sidebar {
    position: fixed;
    left: 0;
    top: 0;
    bottom: 0;
    z-index: var(--z-sticky);
    transform: translateX(-100%);
    transition: transform var(--transition-smooth);
    box-shadow: var(--shadow-lg);
  }

  .section > .sidebar.sidebarOpen {
    transform: translateX(0);
  }

  .sectionMobileMenuButton {
    display: flex !important;
  }

  /* Page template: mobile sidebar */
  .pageTemplate > .sidebar {
    position: fixed;
    left: 0;
    top: 0;
    bottom: 0;
    z-index: var(--z-sticky);
    transform: translateX(-100%);
    transition: transform var(--transition-smooth);
    box-shadow: var(--shadow-lg);
  }

  .pageTemplate > .sidebar:not(.sidebarCollapsed) {
    transform: translateX(0);
  }
}

@media (min-width: 769px) {
  .sectionMobileMenuButton {
    display: none !important;
  }
}

/**
 * Chat Component - AI-first chat interface with multimodal support.
 *
 * @example
 * ```tsx
 * import { Chat } from '@orion-ds/react';
 *
 * <Chat>
 *   <Chat.Header title="AI Assistant" />
 *   <Chat.Messages>
 *     {messages.map(msg => (
 *       <Chat.Message
 *         key={msg.id}
 *         role={msg.role}
 *         content={msg.content}
 *       />
 *     ))}
 *     {isTyping && <Chat.TypingIndicator />}
 *   </Chat.Messages>
 *   <Chat.Input onSend={handleSend} />
 * </Chat>
 * ```
 */

export { Chat } from './Chat';

// Types
export type {
  // Core types
  ChatRole,
  MessageStatus,
