/**
 * Chip Component
 *
 * A compact element for displaying tags, filters, or selections.
 *
 * @example
 * ```tsx
 * <Chip>Default</Chip>
 * <Chip variant="success" icon={<Check size={14} />}>Completed</Chip>
 * <Chip onRemove={() => removeTag(tag)}>{tag}</Chip>
 * ```
 */

import { forwardRef } from "react";
import type { ChipProps } from "./Chip.types";
import styles from "./Chip.module.css";

export const Chip = forwardRef<HTMLDivElement, ChipProps>(
  (
    {
      variant = "default",
      size = "md",
      icon,
      clickable = false,
      onClick,
      onRemove,
      disabled = false,
      selected = false,
      children,
      className,
      ...rest
    },
    ref,
  ) => {
    const isInteractive = clickable || !!onClick;

    const classNames = [
      styles.chip,
      styles[variant],
      styles[size],
      isInteractive && styles.clickable,
      selected && styles.selected,
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    const handleClick = () => {
      if (!disabled && onClick) {
        onClick();
      }
    };

    const handleRemove = (e: React.MouseEvent) => {
      e.stopPropagation();
      if (!disabled && onRemove) {
        onRemove();
      }
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
      if (disabled) return;

      if (e.key === "Enter" || e.key === " ") {
        e.preventDefault();
        onClick?.();
      }

      if (e.key === "Backspace" || e.key === "Delete") {
        e.preventDefault();
        onRemove?.();
      }
    };

    return (
      <div
        ref={ref}
        className={classNames}
        role={isInteractive ? "button" : undefined}
        tabIndex={isInteractive && !disabled ? 0 : undefined}
        aria-disabled={disabled}
        aria-pressed={selected}
        onClick={isInteractive ? handleClick : undefined}
        onKeyDown={isInteractive ? handleKeyDown : undefined}
        {...rest}
      >
        {icon && (
          <span className={styles.icon} aria-hidden="true">
            {icon}
          </span>
        )}

        <span className={styles.label}>{children}</span>

        {onRemove && (
          <button
            type="button"
            className={styles.removeButton}
            onClick={handleRemove}
            disabled={disabled}
            aria-label="Remove"
          >
            <svg
              width="14"
              height="14"
              viewBox="0 0 14 14"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M10.5 3.5L3.5 10.5M3.5 3.5l7 7"
                stroke="currentColor"
                strokeWidth="1.5"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </button>
        )}
      </div>
    );
  },
);

Chip.displayName = "Chip";

/**
 * Chip Component Types
 *
 * Type definitions for the Orion Chip/Tag component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * Chip visual variants
 */
export type ChipVariant =
  | "default"
  | "primary"
  | "success"
  | "warning"
  | "error"
  | "info";

/**
 * Chip sizes
 */
export type ChipSize = "sm" | "md" | "lg";

/**
 * Chip component props
 *
 * @example
 * ```tsx
 * <Chip>Default</Chip>
 * <Chip variant="success">Completed</Chip>
 * <Chip onRemove={() => handleRemove()}>Removable</Chip>
 * ```
 */
export interface ChipProps extends Omit<
  HTMLAttributes<HTMLDivElement>,
  "onClick"
> {
  /**
   * Visual variant of the chip
   * @default 'default'
   */
  variant?: ChipVariant;

  /**
   * Size of the chip
   * @default 'md'
   */
  size?: ChipSize;

  /**
   * Icon to display before the label
   */
  icon?: ReactNode;

  /**
   * Whether the chip is clickable
   * @default false
   */
  clickable?: boolean;

  /**
   * Click handler (when clickable is true)
   */
  onClick?: () => void;

  /**
   * Callback to remove the chip (shows X button)
   */
  onRemove?: () => void;

  /**
   * Whether the chip is disabled
   * @default false
   */
  disabled?: boolean;

  /**
   * Whether the chip is selected (for selectable chips)
   * @default false
   */
  selected?: boolean;

  /**
   * Chip content
   */
  children?: ReactNode;
}

/**
 * Chip Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Base chip styles */
.chip {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-1);
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  border-radius: var(--radius-full);
  white-space: nowrap;
  transition:
    background-color var(--transition-fast),
    border-color var(--transition-fast);
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm {
  height: 24px;
  padding: 0 var(--spacing-2);
  font-size: var(--font-size-12);
}

.md {
  height: 28px;
  padding: 0 var(--spacing-3);
  font-size: var(--font-size-12);
}

.lg {
  height: 32px;
  padding: 0 var(--spacing-3);
  font-size: var(--font-size-14);
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.default {
  background: var(--surface-subtle);
  color: var(--text-primary);
  border: 1px solid var(--border-subtle);
}

.primary {
  background: color-mix(in srgb, var(--interactive-primary) 15%, transparent);
  color: var(--text-brand);
  border: 1px solid
    color-mix(in srgb, var(--interactive-primary) 30%, transparent);
}

.success {
  background: color-mix(in srgb, var(--status-success) 15%, transparent);
  color: var(--status-success);
  border: 1px solid color-mix(in srgb, var(--status-success) 30%, transparent);
}

.warning {
  background: color-mix(in srgb, var(--status-warning) 15%, transparent);
  color: var(--status-warning);
  border: 1px solid color-mix(in srgb, var(--status-warning) 30%, transparent);
}

.error {
  background: color-mix(in srgb, var(--status-error) 15%, transparent);
  color: var(--status-error);
  border: 1px solid color-mix(in srgb, var(--status-error) 30%, transparent);
}

.info {
  background: color-mix(in srgb, var(--status-info) 15%, transparent);
  color: var(--status-info);
  border: 1px solid color-mix(in srgb, var(--status-info) 30%, transparent);
}

/* ============================================================================
 * STATES
 * ============================================================================ */

.clickable {
  cursor: pointer;

  &:hover:not(.disabled) {
    filter: brightness(0.95);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }

  &:active:not(.disabled) {
    transform: scale(0.98);
  }
}

.selected {
  &.default {
    background: var(--interactive-primary);
    color: var(--interactive-primary-text);
    border-color: var(--interactive-primary);
  }

  &.primary {
    background: var(--interactive-primary);
    color: var(--interactive-primary-text);
    border-color: var(--interactive-primary);
  }
}

.disabled {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}

/* ============================================================================
 * ELEMENTS
 * ============================================================================ */

.icon {
  width: 14px;
  height: 14px;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  .sm & {
    width: 12px;
    height: 12px;
  }

  .lg & {
    width: 16px;
    height: 16px;
  }
}

.label {
  line-height: 1;
}

.removeButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 16px;
  height: 16px;
  margin-left: var(--spacing-1);
  margin-right: calc(var(--spacing-1) * -1);
  padding: 0;
  background: transparent;
  border: none;
  border-radius: 50%;
  color: currentColor;
  opacity: 0.6;
  cursor: pointer;
  transition:
    opacity var(--transition-fast),
    background-color var(--transition-fast);

  &:hover:not(:disabled) {
    opacity: 1;
    background: var(--interactive-ghost-hover);
  }

  &:focus-visible {
    outline: 2px solid currentColor;
    outline-offset: 1px;
  }

  &:disabled {
    cursor: not-allowed;
  }

  .sm & {
    width: 14px;
    height: 14px;
  }

  .lg & {
    width: 18px;
    height: 18px;
  }
}
