/**
 * Contact Component
 *
 * A contact section with form and contact information.
 * Supports multiple layouts and custom form fields.
 *
 * @example
 * ```tsx
 * <Contact
 *   eyebrow={<Badge>Contact</Badge>}
 *   title="Get in touch"
 *   description="We'd love to hear from you"
 *   contactInfo={[
 *     { icon: <Mail size={20} />, label: "Email", value: "hello@example.com", href: "mailto:hello@example.com" },
 *     { icon: <Phone size={20} />, label: "Phone", value: "+1 (555) 000-0000", href: "tel:+15550000000" },
 *     { icon: <MapPin size={20} />, label: "Address", value: "123 Main St, City, Country" }
 *   ]}
 *   formFields={[
 *     { name: "name", label: "Name", required: true },
 *     { name: "email", label: "Email", type: "email", required: true },
 *     { name: "message", label: "Message", type: "textarea", required: true }
 *   ]}
 *   onSubmit={(data) => console.log(data)}
 * />
 * ```
 */

import { forwardRef, useCallback, useState } from 'react';
import type { ContactProps, ContactFormField } from './Contact.types';
import { Section } from '../Section';
import { Container } from '../Container';
import { Button, Field, Textarea } from '../../components';
import styles from './Contact.module.css';

const defaultFields: ContactFormField[] = [
  { name: 'name', label: 'Name', type: 'text', required: true, placeholder: 'Your name' },
  { name: 'email', label: 'Email', type: 'email', required: true, placeholder: 'your@email.com' },
  {
    name: 'message',
    label: 'Message',
    type: 'textarea',
    required: true,
    placeholder: 'How can we help?',
    rows: 4,
  },
];

export const Contact = forwardRef<HTMLElement, ContactProps>(
  (
    {
      eyebrow,
      title,
      description,
      contactInfo,
      formFields = defaultFields,
      onSubmit,
      submitButton,
      submitText = 'Send Message',
      layout = 'split',
      background = 'subtle',
      additionalContent,
      className,
      ...rest
    },
    ref,
  ) => {
    const hasHeader = eyebrow || title || description;
    const hasContactInfo = contactInfo && contactInfo.length > 0;
    const [formData, setFormData] = useState<Record<string, string>>({});

    const handleFieldChange = useCallback((name: string, value: string) => {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }, []);

    const handleSubmit = useCallback(
      (e: React.FormEvent) => {
        e.preventDefault();
        if (onSubmit) {
          onSubmit(formData);
        }
      },
      [formData, onSubmit],
    );

    const classNames = [styles.contact, styles[`layout-${layout}`], className]
      .filter(Boolean)
      .join(' ');

    const renderForm = () => (
      <form className={styles.form} onSubmit={handleSubmit}>
        <div className={styles.formFields}>
          {formFields.map((field) => {
            if (field.type === 'textarea') {
              return (
                <Textarea
                  key={field.name}
                  label={field.label}
                  name={field.name}
                  placeholder={field.placeholder}
                  required={field.required}
                  rows={field.rows || 4}
                  value={formData[field.name] || ''}
                  onChange={(e) => handleFieldChange(field.name, e.target.value)}
                  className={styles.formField}
                />
              );
            }

            return (
              <Field
                key={field.name}
                label={field.label}
                name={field.name}
                type={field.type || 'text'}
                placeholder={field.placeholder}
                required={field.required}
                value={formData[field.name] || ''}
                onChange={(e) => handleFieldChange(field.name, e.target.value)}
                className={styles.formField}
              />
            );
          })}
        </div>

        <div className={styles.formAction}>
          {submitButton || (
            <Button type="submit" size="lg">
              {submitText}
            </Button>
          )}
        </div>
      </form>
    );

    const renderContactInfo = () => (
      <div className={styles.contactInfo}>
        {contactInfo?.map((info, index) => (
          <div key={info.label || index} className={styles.infoItem}>
            {info.icon && <div className={styles.infoIcon}>{info.icon}</div>}
            <div className={styles.infoContent}>
              <span className={styles.infoLabel}>{info.label}</span>
              {info.href ? (
                <a href={info.href} className={styles.infoValue}>
                  {info.value}
                </a>
              ) : (
                <span className={styles.infoValue}>{info.value}</span>
              )}
            </div>
          </div>
        ))}

        {additionalContent && <div className={styles.additionalContent}>{additionalContent}</div>}
      </div>
    );

    return (
      <Section ref={ref} spacing="lg" background={background} className={classNames} {...rest}>
        <Container size="lg">
          {hasHeader && (
            <header className={styles.header}>
              {eyebrow && <div className={styles.eyebrow}>{eyebrow}</div>}
              {title && <h2 className={styles.title}>{title}</h2>}
              {description && <p className={styles.description}>{description}</p>}
            </header>
          )}

          <div className={styles.content}>
            {layout !== 'form-only' && hasContactInfo && renderContactInfo()}
            {renderForm()}
          </div>
        </Container>
      </Section>
    );
  },
);

Contact.displayName = 'Contact';

/**
 * Contact Component Types
 *
 * Type definitions for the Orion Contact section component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Contact layout variant
 */
export type ContactLayout = 'split' | 'stacked' | 'form-only';

/**
 * Contact info item (phone, email, address, etc.)
 */
export interface ContactInfo {
  /**
   * Icon element for the contact method
   */
  icon?: ReactNode;

  /**
   * Label (e.g., "Email", "Phone", "Address")
   */
  label: string;

  /**
   * Value (e.g., "hello@example.com")
   */
  value: string;

  /**
   * Optional href for clickable items (mailto:, tel:)
   */
  href?: string;
}

/**
 * Form field configuration
 */
export interface ContactFormField {
  /**
   * Field name (for form data)
   */
  name: string;

  /**
   * Field label
   */
  label: string;

  /**
   * Field type
   * @default 'text'
   */
  type?: 'text' | 'email' | 'tel' | 'textarea';

  /**
   * Placeholder text
   */
  placeholder?: string;

  /**
   * Whether the field is required
   * @default false
   */
  required?: boolean;

  /**
   * Number of rows (for textarea only)
   * @default 4
   */
  rows?: number;
}

/**
 * Contact section props
 *
 * @example
 * ```tsx
 * <Contact
 *   title="Get in touch"
 *   description="We'd love to hear from you"
 *   contactInfo={[
 *     { icon: <Mail />, label: "Email", value: "hello@example.com", href: "mailto:hello@example.com" },
 *     { icon: <Phone />, label: "Phone", value: "+1 (555) 000-0000", href: "tel:+15550000000" }
 *   ]}
 *   formFields={[
 *     { name: "name", label: "Name", required: true },
 *     { name: "email", label: "Email", type: "email", required: true },
 *     { name: "message", label: "Message", type: "textarea", required: true }
 *   ]}
 *   onSubmit={(data) => console.log(data)}
 * />
 * ```
 */
export interface ContactProps extends Omit<HTMLAttributes<HTMLElement>, 'title' | 'onSubmit'> {
  /**
   * Optional eyebrow/badge above title
   */
  eyebrow?: ReactNode;

  /**
   * Section title
   */
  title?: ReactNode;

  /**
   * Section description
   */
  description?: ReactNode;

  /**
   * Contact information items
   */
  contactInfo?: ContactInfo[];

  /**
   * Form fields configuration
   */
  formFields?: ContactFormField[];

  /**
   * Form submit handler - receives form data as an object
   */
  onSubmit?: (data: Record<string, string>) => void;

  /**
   * Custom submit button element
   */
  submitButton?: ReactNode;

  /**
   * Submit button text (if not using custom button)
   * @default "Send Message"
   */
  submitText?: string;

  /**
   * Layout variant
   * - split: Info on left, form on right
   * - stacked: Info above form
   * - form-only: Just the form, no contact info
   * @default 'split'
   */
  layout?: ContactLayout;

  /**
   * Background style
   * @default 'subtle'
   */
  background?: 'base' | 'subtle' | 'none';

  /**
   * Additional content below contact info (e.g., social links, map)
   */
  additionalContent?: ReactNode;
}

/**
 * Contact Component Exports
 */

export { Contact } from './Contact';
export type { ContactProps, ContactInfo, ContactFormField, ContactLayout } from './Contact.types';
