/**
 * Container Component
 *
 * A layout wrapper that constrains content to a maximum width with consistent padding.
 * Part of the Orion Sections library for building complete page layouts.
 *
 * @example
 * ```tsx
 * <Container size="lg">
 *   <h1>Page Title</h1>
 *   <p>Content goes here</p>
 * </Container>
 * ```
 */

import { forwardRef } from "react";
import type { ContainerProps } from "./Container.types";
import styles from "./Container.module.css";

export const Container = forwardRef<HTMLDivElement, ContainerProps>(
  (
    {
      size = "lg",
      centered = true,
      padded = true,
      className,
      children,
      ...rest
    },
    ref,
  ) => {
    const classNames = [
      styles.container,
      styles[size],
      centered && styles.centered,
      padded && styles.padded,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    return (
      <div ref={ref} className={classNames} {...rest}>
        {children}
      </div>
    );
  },
);

Container.displayName = "Container";

/**
 * Container Component Types
 *
 * Type definitions for the Orion Container section component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * Container size variants
 */
export type ContainerSize = "sm" | "md" | "lg" | "xl" | "full";

/**
 * Container component props
 *
 * @example
 * ```tsx
 * <Container size="lg">
 *   <Content />
 * </Container>
 * ```
 */
export interface ContainerProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Maximum width variant
   * - sm: 640px (small content, text-focused)
   * - md: 768px (medium content)
   * - lg: 1024px (standard layouts)
   * - xl: 1280px (wide layouts)
   * - full: 100% (full width)
   * @default 'lg'
   */
  size?: ContainerSize;

  /**
   * Center the container horizontally
   * @default true
   */
  centered?: boolean;

  /**
   * Add horizontal padding
   * @default true
   */
  padded?: boolean;

  /**
   * Container content
   */
  children?: ReactNode;
}

/**
 * Container Component Exports
 */

export { Container } from "./Container";
export type { ContainerProps, ContainerSize } from "./Container.types";
