/**
 * CTA Component
 *
 * A call-to-action section for converting users. Supports various visual styles
 * and can be used as a contained card or full-width section.
 *
 * @example
 * ```tsx
 * <CTA
 *   title="Ready to get started?"
 *   description="Join thousands of developers building with Orion"
 *   actions={
 *     <>
 *       <Button size="lg">Start Free Trial</Button>
 *       <Button size="lg" variant="ghost">Contact Sales</Button>
 *     </>
 *   }
 *   variant="brand"
 * />
 * ```
 */

import { forwardRef, useEffect } from 'react';
import type { CTAProps } from './CTA.types';
import { Section } from '../Section';
import { Container } from '../Container';
import { Card } from '../../components';
import styles from './CTA.module.css';

export const CTA = forwardRef<HTMLElement, CTAProps>(
  (
    {
      title,
      headline,
      description,
      actions,
      footnote,
      variant = 'brand',
      size = 'md',
      align = 'center',
      contained = true,
      className,
      ...rest
    },
    ref,
  ) => {
    // Support deprecated headline prop with warning
    const effectiveTitle = title ?? headline;

    // Deprecation warning
    useEffect(() => {
      if (headline && process.env.NODE_ENV === 'development') {
        console.warn(
          '[CTA] The "headline" prop is deprecated. Use "title" instead. ' +
            'headline will be removed in v3.0.',
        );
      }
    }, [headline]);

    const classNames = [
      styles.cta,
      styles[`variant-${variant}`],
      styles[`size-${size}`],
      styles[`align-${align}`],
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const content = (
      <div className={styles.content}>
        <h2 className={styles.headline}>{effectiveTitle}</h2>

        {description && <p className={styles.description}>{description}</p>}

        {actions && <div className={styles.actions}>{actions}</div>}

        {footnote && <p className={styles.footnote}>{footnote}</p>}
      </div>
    );

    if (contained) {
      return (
        <Section ref={ref} spacing="lg" className={classNames} {...rest}>
          <Container size="lg">
            <Card className={styles.card}>
              <Card.Body className={styles.cardBody}>{content}</Card.Body>
            </Card>
          </Container>
        </Section>
      );
    }

    return (
      <Section ref={ref} spacing="lg" className={classNames} {...rest}>
        <Container size="lg">{content}</Container>
      </Section>
    );
  },
);

CTA.displayName = 'CTA';

/**
 * CTA Component Types
 *
 * Type definitions for the Orion CTA (Call-to-Action) section component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * CTA visual variants
 */
export type CTAVariant = 'default' | 'brand' | 'subtle' | 'outline';

/**
 * CTA size variants
 */
export type CTASize = 'sm' | 'md' | 'lg';

/**
 * CTA section props
 *
 * @example
 * ```tsx
 * <CTA
 *   title="Ready to get started?"
 *   description="Join thousands of developers building with Orion"
 *   actions={
 *     <>
 *       <Button size="lg">Start Free Trial</Button>
 *       <Button size="lg" variant="ghost">Contact Sales</Button>
 *     </>
 *   }
 * />
 * ```
 */
export interface CTAProps extends Omit<HTMLAttributes<HTMLElement>, 'title'> {
  /**
   * Main title text
   */
  title?: ReactNode;

  /**
   * @deprecated Use `title` instead. Will be removed in v3.0.
   */
  headline?: ReactNode;

  /**
   * Supporting description text
   */
  description?: ReactNode;

  /**
   * Action buttons/elements
   */
  actions?: ReactNode;

  /**
   * Additional text below actions (disclaimers, etc.)
   */
  footnote?: ReactNode;

  /**
   * Visual variant
   * - default: Standard background
   * - brand: Brand color background
   * - subtle: Subtle background
   * - outline: Outlined container
   * @default 'brand'
   */
  variant?: CTAVariant;

  /**
   * Size variant
   * @default 'md'
   */
  size?: CTASize;

  /**
   * Text alignment
   * @default 'center'
   */
  align?: 'left' | 'center';

  /**
   * Render as card (contained box) vs full-width section
   * @default true
   */
  contained?: boolean;
}

/**
 * CTA Component Exports
 */

export { CTA } from './CTA';
export type { CTAProps, CTAVariant, CTASize } from './CTA.types';
