/**
 * Divider Component
 *
 * A visual separator for content sections.
 *
 * @example
 * ```tsx
 * <Divider />
 * <Divider orientation="vertical" />
 * <Divider variant="dashed" label="OR" />
 * ```
 */

import { forwardRef } from "react";
import type { DividerProps } from "./Divider.types";
import styles from "./Divider.module.css";

export const Divider = forwardRef<HTMLHRElement, DividerProps>(
  (
    {
      orientation = "horizontal",
      variant = "solid",
      spacing = "md",
      label,
      className,
      ...rest
    },
    ref,
  ) => {
    const classNames = [
      styles.divider,
      styles[orientation],
      styles[variant],
      styles[`spacing-${spacing}`],
      label && styles.withLabel,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    if (label && orientation === "horizontal") {
      return (
        <div
          className={classNames}
          role="separator"
          aria-orientation={orientation}
        >
          <span className={styles.line} />
          <span className={styles.label}>{label}</span>
          <span className={styles.line} />
        </div>
      );
    }

    return (
      <hr
        ref={ref}
        className={classNames}
        role="separator"
        aria-orientation={orientation}
        {...rest}
      />
    );
  },
);

Divider.displayName = "Divider";

/**
 * Divider Component Types
 *
 * Type definitions for the Orion Divider component.
 */

import type { HTMLAttributes } from "react";

/**
 * Divider orientation
 */
export type DividerOrientation = "horizontal" | "vertical";

/**
 * Divider visual variants
 */
export type DividerVariant = "solid" | "dashed" | "dotted";

/**
 * Divider component props
 *
 * @example
 * ```tsx
 * <Divider />
 * <Divider orientation="vertical" />
 * <Divider variant="dashed" spacing="lg" />
 * ```
 */
export interface DividerProps extends HTMLAttributes<HTMLHRElement> {
  /**
   * Orientation of the divider
   * @default 'horizontal'
   */
  orientation?: DividerOrientation;

  /**
   * Visual variant of the divider
   * @default 'solid'
   */
  variant?: DividerVariant;

  /**
   * Spacing around the divider
   * @default 'md'
   */
  spacing?: "none" | "sm" | "md" | "lg";

  /**
   * Optional label to display in the center of the divider
   */
  label?: string;
}

/**
 * Divider Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Base divider styles */
.divider {
  border: none;
  background: var(--border-subtle);
  flex-shrink: 0;
}

/* ============================================================================
 * ORIENTATIONS
 * ============================================================================ */

.horizontal {
  width: 100%;
  height: 1px;
}

.vertical {
  width: 1px;
  height: 100%;
  min-height: 24px;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.solid {
  background: var(--border-subtle);
}

.dashed {
  background: transparent;

  &.horizontal {
    border-top: 1px dashed var(--border-subtle);
  }

  &.vertical {
    border-left: 1px dashed var(--border-subtle);
  }
}

.dotted {
  background: transparent;

  &.horizontal {
    border-top: 1px dotted var(--border-subtle);
  }

  &.vertical {
    border-left: 1px dotted var(--border-subtle);
  }
}

/* ============================================================================
 * SPACING
 * ============================================================================ */

.spacing-none {
  margin: 0;
}

.spacing-sm {
  &.horizontal {
    margin: var(--spacing-2) 0;
  }

  &.vertical {
    margin: 0 var(--spacing-2);
  }
}

.spacing-md {
  &.horizontal {
    margin: var(--spacing-4) 0;
  }

  &.vertical {
    margin: 0 var(--spacing-4);
  }
}

.spacing-lg {
  &.horizontal {
    margin: var(--spacing-6) 0;
  }

  &.vertical {
    margin: 0 var(--spacing-6);
  }
}

/* ============================================================================
 * WITH LABEL
 * ============================================================================ */

.withLabel {
  display: flex;
  align-items: center;
  gap: var(--spacing-4);
  background: transparent;
  height: auto;
}

.line {
  flex: 1;
  height: 1px;
  background: var(--border-subtle);
}

.label {
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-medium);
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  white-space: nowrap;
}

/**
 * Divider Component - Visual content separator.
 *
 * @example
 * ```tsx
 * import { Divider } from '@orion-ds/react';
 *
 * <Divider />
 * <Divider variant="dashed" />
 * <Divider orientation="vertical" />
 * <Divider>OR</Divider>
 * ```
 */
export { Divider } from "./Divider";
export type {
  DividerProps,
  DividerOrientation,
  DividerVariant,
} from "./Divider.types";
