/**
 * Drawer Component
 *
 * A panel that slides in from the edge of the screen.
 *
 * @example
 * ```tsx
 * <Drawer open={isOpen} onClose={() => setIsOpen(false)} placement="right">
 *   <Drawer.Header>Settings</Drawer.Header>
 *   <Drawer.Body>Content here</Drawer.Body>
 *   <Drawer.Footer>
 *     <Button onClick={() => setIsOpen(false)}>Close</Button>
 *   </Drawer.Footer>
 * </Drawer>
 * ```
 */

import { forwardRef, useEffect, useCallback } from 'react';
import { createPortal } from 'react-dom';
import type {
  DrawerProps,
  DrawerHeaderProps,
  DrawerBodyProps,
  DrawerFooterProps,
} from './Drawer.types';
import styles from './Drawer.module.css';

/**
 * Drawer Header
 */
const DrawerHeader = forwardRef<HTMLDivElement, DrawerHeaderProps>(
  ({ children, className, ...rest }, ref) => {
    return (
      <div ref={ref} className={`${styles.header} ${className || ''}`} {...rest}>
        {children}
      </div>
    );
  },
);

DrawerHeader.displayName = 'Drawer.Header';

/**
 * Drawer Body
 */
const DrawerBody = forwardRef<HTMLDivElement, DrawerBodyProps>(
  ({ children, className, ...rest }, ref) => {
    return (
      <div ref={ref} className={`${styles.body} ${className || ''}`} {...rest}>
        {children}
      </div>
    );
  },
);

DrawerBody.displayName = 'Drawer.Body';

/**
 * Drawer Footer
 */
const DrawerFooter = forwardRef<HTMLDivElement, DrawerFooterProps>(
  ({ children, className, ...rest }, ref) => {
    return (
      <div ref={ref} className={`${styles.footer} ${className || ''}`} {...rest}>
        {children}
      </div>
    );
  },
);

DrawerFooter.displayName = 'Drawer.Footer';

/**
 * Drawer Component
 */
const DrawerRoot = forwardRef<HTMLDivElement, DrawerProps>(
  (
    {
      open,
      onClose,
      placement = 'right',
      size = 'md',
      closeOnBackdrop = true,
      closeOnEscape = true,
      showCloseButton = true,
      children,
      className,
      ...rest
    },
    ref,
  ) => {
    // Handle escape key
    const handleEscape = useCallback(
      (event: KeyboardEvent) => {
        if (closeOnEscape && event.key === 'Escape') {
          onClose();
        }
      },
      [closeOnEscape, onClose],
    );

    // Handle backdrop click
    const handleBackdropClick = useCallback(() => {
      if (closeOnBackdrop) {
        onClose();
      }
    }, [closeOnBackdrop, onClose]);

    // Lock body scroll when open
    useEffect(() => {
      if (open) {
        const originalOverflow = document.body.style.overflow;
        document.body.style.overflow = 'hidden';
        document.addEventListener('keydown', handleEscape);

        return () => {
          document.body.style.overflow = originalOverflow;
          document.removeEventListener('keydown', handleEscape);
        };
      }
    }, [open, handleEscape]);

    if (!open) {
      return null;
    }

    const overlayClasses = [styles.overlay, open && styles.overlayVisible]
      .filter(Boolean)
      .join(' ');

    const drawerClasses = [
      styles.drawer,
      styles[placement],
      styles[`size-${size}`],
      open && styles.drawerVisible,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const content = (
      <div className={overlayClasses}>
        {/* Backdrop */}
        <div className={styles.backdrop} onClick={handleBackdropClick} aria-hidden="true" />

        {/* Drawer panel */}
        <div ref={ref} className={drawerClasses} role="dialog" aria-modal="true" {...rest}>
          {showCloseButton && (
            <button
              type="button"
              className={styles.closeButton}
              onClick={onClose}
              aria-label="Close drawer"
            >
              <svg
                width="20"
                height="20"
                viewBox="0 0 20 20"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M15 5L5 15M5 5L15 15"
                  stroke="currentColor"
                  strokeWidth="1.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </button>
          )}
          {children}
        </div>
      </div>
    );

    // Render in portal
    return createPortal(content, document.body);
  },
);

DrawerRoot.displayName = 'Drawer';

// Compose the component with subcomponents
export const Drawer = Object.assign(DrawerRoot, {
  Header: DrawerHeader,
  Body: DrawerBody,
  Footer: DrawerFooter,
});

/**
 * Drawer Component Types
 *
 * Type definitions for the Orion Drawer component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Drawer placement options
 */
export type DrawerPlacement = 'left' | 'right' | 'top' | 'bottom';

/**
 * Drawer sizes
 */
export type DrawerSize = 'sm' | 'md' | 'lg' | 'xl' | 'full';

/**
 * Drawer component props
 *
 * @example
 * ```tsx
 * <Drawer open={isOpen} onClose={() => setIsOpen(false)}>
 *   <Drawer.Header>Settings</Drawer.Header>
 *   <Drawer.Body>Content here</Drawer.Body>
 * </Drawer>
 * ```
 */
export interface DrawerProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Whether the drawer is open
   */
  open: boolean;

  /**
   * Callback when the drawer should close
   */
  onClose: () => void;

  /**
   * Placement of the drawer
   * @default 'right'
   */
  placement?: DrawerPlacement;

  /**
   * Size of the drawer
   * @default 'md'
   */
  size?: DrawerSize;

  /**
   * Whether to close when clicking the backdrop
   * @default true
   */
  closeOnBackdrop?: boolean;

  /**
   * Whether to close when pressing Escape
   * @default true
   */
  closeOnEscape?: boolean;

  /**
   * Whether to show a close button
   * @default true
   */
  showCloseButton?: boolean;

  /**
   * Drawer content
   */
  children?: ReactNode;
}

/**
 * Drawer Header props
 */
export interface DrawerHeaderProps extends HTMLAttributes<HTMLDivElement> {
  children?: ReactNode;
}

/**
 * Drawer Body props
 */
export interface DrawerBodyProps extends HTMLAttributes<HTMLDivElement> {
  children?: ReactNode;
}

/**
 * Drawer Footer props
 */
export interface DrawerFooterProps extends HTMLAttributes<HTMLDivElement> {
  children?: ReactNode;
}

/**
 * Drawer Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Overlay container */
.overlay {
  position: fixed;
  inset: 0;
  z-index: var(--z-modal-backdrop);
  display: flex;
  visibility: hidden;
  opacity: 0;
  transition:
    visibility 0s linear var(--transition-normal),
    opacity var(--transition-normal);
}

.overlayVisible {
  visibility: visible;
  opacity: 1;
  transition:
    visibility 0s linear 0s,
    opacity var(--transition-normal);
}

/* Backdrop */
.backdrop {
  position: absolute;
  inset: 0;
  background: var(--overlay-dark);
}

/* Drawer panel */
.drawer {
  position: fixed;
  display: flex;
  flex-direction: column;
  background: var(--surface-base);
  box-shadow: var(--shadow-xl);
  z-index: var(--z-modal);
  transition: transform var(--transition-normal);
}

/* ============================================================================
 * PLACEMENTS
 * ============================================================================ */

.left {
  top: 0;
  left: 0;
  bottom: 0;
  transform: translateX(-100%);
}

.left.drawerVisible {
  transform: translateX(0);
}

.right {
  top: 0;
  right: 0;
  bottom: 0;
  transform: translateX(100%);
}

.right.drawerVisible {
  transform: translateX(0);
}

.top {
  top: 0;
  left: 0;
  right: 0;
  transform: translateY(-100%);
}

.top.drawerVisible {
  transform: translateY(0);
}

.bottom {
  bottom: 0;
  left: 0;
  right: 0;
  transform: translateY(100%);
}

.bottom.drawerVisible {
  transform: translateY(0);
}

/* ============================================================================
 * SIZES (for left/right placements)
 * ============================================================================ */

.left.size-sm,
.right.size-sm {
  width: 280px;
}

.left.size-md,
.right.size-md {
  width: 360px;
}

.left.size-lg,
.right.size-lg {
  width: 480px;
}

.left.size-xl,
.right.size-xl {
  width: 640px;
}

.left.size-full,
.right.size-full {
  width: 100%;
}

/* SIZES (for top/bottom placements) */
.top.size-sm,
.bottom.size-sm {
  height: 200px;
}

.top.size-md,
.bottom.size-md {
  height: 300px;
}

.top.size-lg,
.bottom.size-lg {
  height: 400px;
}

.top.size-xl,
.bottom.size-xl {
  height: 500px;
}

.top.size-full,
.bottom.size-full {
  height: 100%;
}

/* ============================================================================
 * CLOSE BUTTON
 * ============================================================================ */

.closeButton {
  position: absolute;
  top: var(--spacing-4);
  right: var(--spacing-4);
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  color: var(--text-tertiary);
  cursor: pointer;
  transition:
    background-color var(--transition-fast),
    color var(--transition-fast);
  z-index: var(--z-base);

  &:hover {
    background: var(--surface-subtle);
    color: var(--text-primary);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }
}

/* ============================================================================
 * SECTIONS
 * ============================================================================ */

.header {
  flex-shrink: 0;
  padding: var(--spacing-6) var(--spacing-6) var(--spacing-4);
  padding-right: var(--spacing-12); /* Space for close button */
  font-family: var(--font-secondary);
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  border-bottom: 1px solid var(--border-subtle);
}

.body {
  flex: 1;
  overflow-y: auto;
  padding: var(--spacing-4) var(--spacing-6);
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  color: var(--text-secondary);
}

.footer {
  flex-shrink: 0;
  padding: var(--spacing-4) var(--spacing-6);
  border-top: 1px solid var(--border-subtle);
  display: flex;
  gap: var(--spacing-3);
  justify-content: flex-end;
}

/* ============================================================================
 * REDUCED MOTION
 * ============================================================================ */

@media (prefers-reduced-motion: reduce) {
  .overlay,
  .drawer {
    transition: none;
  }
}

/* ============================================================================
 * RESPONSIVE
 * ============================================================================ */

@media (max-width: 640px) {
  .left.size-lg,
  .right.size-lg,
  .left.size-xl,
  .right.size-xl {
    width: 100%;
  }
}
