/**
 * EmptyState Component
 *
 * Displays when there's no content to show.
 *
 * @example
 * ```tsx
 * <EmptyState
 *   icon={<Inbox size={48} />}
 *   title="No messages yet"
 *   description="When you receive messages, they'll appear here."
 *   action={<Button>Send a message</Button>}
 * />
 * ```
 */

import { forwardRef } from "react";
import type { EmptyStateProps } from "./EmptyState.types";
import styles from "./EmptyState.module.css";

export const EmptyState = forwardRef<HTMLDivElement, EmptyStateProps>(
  (
    {
      icon,
      title,
      description,
      action,
      secondaryAction,
      size = "md",
      className,
      ...rest
    },
    ref,
  ) => {
    const classNames = [styles.emptyState, styles[size], className]
      .filter(Boolean)
      .join(" ");

    return (
      <div ref={ref} className={classNames} {...rest}>
        {icon && (
          <div className={styles.icon} aria-hidden="true">
            {icon}
          </div>
        )}

        <h3 className={styles.title}>{title}</h3>

        {description && <p className={styles.description}>{description}</p>}

        {(action || secondaryAction) && (
          <div className={styles.actions}>
            {action}
            {secondaryAction}
          </div>
        )}
      </div>
    );
  },
);

EmptyState.displayName = "EmptyState";

/**
 * EmptyState Component Types
 *
 * Type definitions for the Orion EmptyState component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * EmptyState sizes
 */
export type EmptyStateSize = "sm" | "md" | "lg";

/**
 * EmptyState component props
 *
 * @example
 * ```tsx
 * <EmptyState
 *   icon={<InboxIcon />}
 *   title="No messages"
 *   description="Your inbox is empty"
 *   action={<Button>Compose</Button>}
 * />
 * ```
 */
export interface EmptyStateProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Icon or illustration to display
   */
  icon?: ReactNode;

  /**
   * Main title text
   */
  title: string;

  /**
   * Description text
   */
  description?: string;

  /**
   * Primary action button
   */
  action?: ReactNode;

  /**
   * Secondary action button
   */
  secondaryAction?: ReactNode;

  /**
   * Size variant
   * @default 'md'
   */
  size?: EmptyStateSize;
}

/**
 * EmptyState Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

.emptyState {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: var(--spacing-8);
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm {
  padding: var(--spacing-4);

  .icon {
    margin-bottom: var(--spacing-3);
  }

  .title {
    font-size: var(--font-size-14);
  }

  .description {
    font-size: var(--font-size-12);
    max-width: 280px;
  }

  .actions {
    margin-top: var(--spacing-3);
    gap: var(--spacing-2);
  }
}

.md {
  .icon {
    margin-bottom: var(--spacing-4);
  }

  .title {
    font-size: var(--font-size-18);
  }

  .description {
    font-size: var(--font-size-14);
    max-width: 360px;
  }

  .actions {
    margin-top: var(--spacing-4);
    gap: var(--spacing-3);
  }
}

.lg {
  padding: var(--spacing-12);

  .icon {
    margin-bottom: var(--spacing-6);
  }

  .title {
    font-size: var(--font-size-24);
  }

  .description {
    font-size: var(--font-size-16);
    max-width: 440px;
  }

  .actions {
    margin-top: var(--spacing-6);
    gap: var(--spacing-4);
  }
}

/* ============================================================================
 * ELEMENTS
 * ============================================================================ */

.icon {
  color: var(--text-tertiary);
}

.title {
  margin: 0;
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
}

.description {
  margin: var(--spacing-2) 0 0;
  font-family: var(--font-secondary);
  color: var(--text-secondary);
  line-height: var(--line-height-relaxed);
}

.actions {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}

/**
 * EmptyState Component - Placeholder for empty data views.
 *
 * @example
 * ```tsx
 * import { EmptyState, Button } from '@orion-ds/react';
 * import { Inbox } from 'lucide-react';
 *
 * <EmptyState
 *   icon={<Inbox size={48} />}
 *   title="No messages"
 *   description="You don't have any messages yet."
 *   action={<Button>Compose</Button>}
 * />
 * ```
 */
export { EmptyState } from "./EmptyState";
export type { EmptyStateProps, EmptyStateSize } from "./EmptyState.types";

