/**
 * Features Component
 *
 * A grid-based features section with support for icons, badges, and hover effects.
 * Composes atomic components from @orion/react (Card, Badge).
 *
 * @example
 * ```tsx
 * <Features
 *   eyebrow={<Badge>Features</Badge>}
 *   title="Everything you need"
 *   description="Powerful features designed for modern apps"
 *   items={[
 *     { icon: <Zap />, title: "Fast", description: "Lightning speed" },
 *     { icon: <Shield />, title: "Secure", description: "Bank-grade security" },
 *     { icon: <Code />, title: "Developer First", description: "Built for devs" },
 *   ]}
 *   columns={3}
 * />
 * ```
 */

import { forwardRef } from 'react';
import type { FeaturesProps } from './Features.types';
import { Section } from '../Section';
import { Container } from '../Container';
import { FeatureCard } from './FeatureCard';
import styles from './Features.module.css';

export const Features = forwardRef<HTMLElement, FeaturesProps>(
  (
    {
      eyebrow,
      title,
      description,
      items,
      columns = 3,
      background = 'subtle',
      interactive = true,
      centered = true,
      className,
      ...rest
    },
    ref,
  ) => {
    const hasHeader = eyebrow || title || description;

    const classNames = [styles.features, centered && styles.centered, className]
      .filter(Boolean)
      .join(' ');

    return (
      <Section ref={ref} spacing="lg" background={background} className={classNames} {...rest}>
        <Container size="xl">
          {hasHeader && (
            <header className={styles.header}>
              {eyebrow && <div className={styles.eyebrow}>{eyebrow}</div>}
              {title && <h2 className={styles.title}>{title}</h2>}
              {description && <p className={styles.description}>{description}</p>}
            </header>
          )}

          <div className={`${styles.grid} ${styles[`cols-${columns}`]}`}>
            {items.map((item, index) => (
              <FeatureCard key={item.title || index} feature={item} interactive={interactive} />
            ))}
          </div>
        </Container>
      </Section>
    );
  },
);

Features.displayName = 'Features';

/**
 * Features Component Types
 *
 * Type definitions for the Orion Features section component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Grid column count
 */
export type FeaturesColumns = 2 | 3 | 4;

/**
 * Feature item data structure
 */
export interface FeatureItem {
  /**
   * Icon element (typically from lucide-react)
   */
  icon?: ReactNode;

  /**
   * Feature title
   */
  title: string;

  /**
   * Feature description
   */
  description: string;

  /**
   * Optional badge text
   */
  badge?: string;

  /**
   * Optional link href
   */
  href?: string;
}

/**
 * Feature card props (internal component)
 */
export interface FeatureCardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Feature item data
   */
  feature: FeatureItem;

  /**
   * Enable hover effects
   * @default true
   */
  interactive?: boolean;
}

/**
 * Features section props
 *
 * @example
 * ```tsx
 * <Features
 *   title="Everything you need"
 *   description="Powerful features designed for modern apps"
 *   items={[
 *     { icon: <Zap />, title: "Fast", description: "Lightning speed" },
 *     { icon: <Shield />, title: "Secure", description: "Bank-grade security" },
 *   ]}
 *   columns={3}
 * />
 * ```
 */
export interface FeaturesProps extends Omit<HTMLAttributes<HTMLElement>, 'title'> {
  /**
   * Optional eyebrow/badge above title
   */
  eyebrow?: ReactNode;

  /**
   * Section title
   */
  title?: ReactNode;

  /**
   * Section description
   */
  description?: ReactNode;

  /**
   * Feature items array
   */
  items: FeatureItem[];

  /**
   * Number of grid columns
   * @default 3
   */
  columns?: FeaturesColumns;

  /**
   * Background style
   * @default 'subtle'
   */
  background?: 'base' | 'subtle' | 'none';

  /**
   * Enable card hover effects
   * @default true
   */
  interactive?: boolean;

  /**
   * Center the header text
   * @default true
   */
  centered?: boolean;
}

/**
 * Features Component Exports
 */

export { Features } from './Features';
export { FeatureCard } from './FeatureCard';
export type {
  FeaturesProps,
  FeatureItem,
  FeatureCardProps,
  FeaturesColumns,
} from './Features.types';
