/**
 * Icon Browser - Documentation Utility
 *
 * A comprehensive gallery for browsing all available Lucide icons
 * with search, filtering, and click-to-copy functionality.
 *
 * This is a documentation tool, not a component for use in applications.
 * All display settings are hardcoded for optimal browsing experience.
 */

import React, { useState, useMemo, useCallback } from "react";
import { icons, Search } from "lucide-react";
import type { LucideIcon } from "lucide-react";
import type { IconGalleryProps } from "./IconGallery.types";
import styles from "./IconGallery.module.css";

const ICON_CATEGORIES: Record<string, string[]> = {
  Navigation: [
    "Menu",
    "X",
    "ChevronDown",
    "ChevronUp",
    "ChevronLeft",
    "ChevronRight",
    "ArrowDown",
    "ArrowUp",
    "ArrowLeft",
    "ArrowRight",
    "Home",
    "Settings",
    "Bell",
    "User",
    "LogOut",
    "LogIn",
    "Search",
    "MapPin",
    "Navigation",
  ],
  Actions: [
    "Plus",
    "Minus",
    "Check",
    "Copy",
    "Download",
    "Upload",
    "Share2",
    "Trash2",
    "Edit",
    "Eye",
    "EyeOff",
    "Lock",
    "Unlock",
    "Zap",
    "Refresh",
    "RotateCw",
    "Save",
    "Send",
    "Archive",
    "Flag",
  ],
  Status: [
    "AlertCircle",
    "CheckCircle",
    "XCircle",
    "Info",
    "HelpCircle",
    "AlertTriangle",
    "CheckCircle2",
    "Circle",
    "Loader",
    "Loader2",
    "Clock",
    "Calendar",
  ],
  Media: [
    "Image",
    "Video",
    "Music",
    "FileText",
    "File",
    "Folder",
    "FolderOpen",
    "Camera",
    "Paperclip",
    "Play",
    "Pause",
    "Volume2",
    "VolumeX",
  ],
  Communication: [
    "Mail",
    "MessageSquare",
    "Heart",
    "Star",
    "Share",
    "MessageCircle",
    "Phone",
    "Smartphone",
    "Gift",
    "ThumbsUp",
    "ThumbsDown",
  ],
  Commerce: [
    "ShoppingCart",
    "DollarSign",
    "CreditCard",
    "Tag",
    "Briefcase",
    "TrendingUp",
    "TrendingDown",
    "BarChart",
    "PieChart",
    "Activity",
  ],
};

export const IconGallery: React.FC<IconGalleryProps> = ({ className }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [copiedIcon, setCopiedIcon] = useState<string | null>(null);

  // Get all icon names from lucide-react's icons export
  const allIconNames = useMemo(() => {
    return Object.keys(icons).sort();
  }, []);

  // Filter icons based on search and category
  const filteredIcons = useMemo(() => {
    let filtered = allIconNames;

    if (selectedCategory) {
      const categoryIcons = ICON_CATEGORIES[selectedCategory];
      if (categoryIcons) {
        filtered = filtered.filter((name) => categoryIcons.includes(name));
      }
    }

    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter((name) => name.toLowerCase().includes(term));
    }

    return filtered.sort();
  }, [allIconNames, searchTerm, selectedCategory]);

  const copyToClipboard = useCallback((iconName: string) => {
    const code = `import { ${iconName} } from 'lucide-react';`;
    navigator.clipboard.writeText(code);
    setCopiedIcon(iconName);
    setTimeout(() => setCopiedIcon(null), 1500);
  }, []);

  const renderIcon = useCallback((iconName: string) => {
    const Icon = icons[iconName as keyof typeof icons] as
      | LucideIcon
      | undefined;
    return Icon ? <Icon size={24} /> : null;
  }, []);

  return (
    <div className={`${styles.gallery}${className ? ` ${className}` : ""}`}>
      {/* Header */}
      <div className={styles.header}>
        <h1>Icon Browser</h1>
        <p>{allIconNames.length.toLocaleString()} icons available</p>
      </div>

      {/* Search */}
      <div className={styles.searchContainer}>
        <Search size={20} className={styles.searchIcon} />
        <input
          type="text"
          placeholder="Search icons..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={styles.searchInput}
        />
        {searchTerm && (
          <span className={styles.resultCount}>
            {filteredIcons.length} results
          </span>
        )}
      </div>

      {/* Categories */}
      <div className={styles.categories}>
        <button
          className={`${styles.categoryBtn} ${!selectedCategory ? styles.active : ""}`}
          onClick={() => setSelectedCategory(null)}
          type="button"
        >
          All
        </button>
        {Object.keys(ICON_CATEGORIES).map((category) => (
          <button
            key={category}
            className={`${styles.categoryBtn} ${selectedCategory === category ? styles.active : ""}`}
            onClick={() => setSelectedCategory(category)}
            type="button"
          >
            {category}
          </button>
        ))}
      </div>

      {/* Icon Grid */}
      <div className={styles.iconGrid}>
        {filteredIcons.map((iconName) => (
          <button
            key={iconName}
            className={styles.iconItem}
            onClick={() => copyToClipboard(iconName)}
            title={iconName}
            type="button"
          >
            <span className={styles.iconDisplay}>{renderIcon(iconName)}</span>
            <span className={styles.iconName}>{iconName}</span>
            {copiedIcon === iconName && (
              <span className={styles.copiedBadge}>Copied!</span>
            )}
          </button>
        ))}
      </div>

      {/* No Results */}
      {filteredIcons.length === 0 && (
        <div className={styles.noResults}>
          <p>No icons found for "{searchTerm}"</p>
        </div>
      )}

      {/* Usage */}
      <div className={styles.usageInfo}>
        <h3>Usage</h3>
        <pre
          className={styles.codeBlock}
        >{`import { Search, Download } from 'lucide-react';

<Button icon={<Search size={20} />}>Search</Button>`}</pre>
      </div>
    </div>
  );
};

IconGallery.displayName = "IconGallery";

/**
 * IconGallery Component Types
 *
 * Type definitions for the Icon Browser utility page.
 * This is a documentation tool, not a configurable component.
 */

/**
 * IconGallery props
 *
 * Note: This is a documentation utility page with minimal configuration.
 * All display options are hardcoded for optimal browsing experience.
 */
export interface IconGalleryProps {
  /**
   * Additional CSS class name
   */
  className?: string;
}

/**
 * Icon Gallery Styles
 *
 * Responsive gallery for displaying and browsing Lucide icons
 */

.gallery {
  padding: var(--spacing-8);
  background: var(--surface-base);
  min-height: 100vh;
}

/* Header */
.header {
  margin-bottom: var(--spacing-6);
  text-align: center;
}

.header h1 {
  font-size: var(--font-size-32);
  font-weight: 700;
  color: var(--text-primary);
  margin: 0 0 var(--spacing-2) 0;
}

.header p {
  font-size: var(--font-size-16);
  color: var(--text-secondary);
  margin: 0;
}

/* Search */
.searchContainer {
  position: relative;
  max-width: 400px;
  margin: 0 auto var(--spacing-6);
}

.searchIcon {
  position: absolute;
  left: var(--spacing-3);
  top: 50%;
  transform: translateY(-50%);
  color: var(--text-tertiary);
  pointer-events: none;
}

.searchInput {
  width: 100%;
  padding: var(--spacing-3) var(--spacing-4) var(--spacing-3) var(--spacing-10);
  font-size: var(--font-size-16);
  border: 2px solid var(--border-subtle);
  border-radius: var(--radius-control);
  background: var(--surface-layer);
  color: var(--text-primary);
  transition: border-color var(--transition-fast);
}

.searchInput:focus {
  outline: none;
  border-color: var(--interactive-primary);
  background: var(--surface-base);
}

.searchInput::placeholder {
  color: var(--text-tertiary);
}

.resultCount {
  position: absolute;
  right: var(--spacing-3);
  top: 50%;
  transform: translateY(-50%);
  font-size: var(--font-size-13);
  color: var(--text-secondary);
  pointer-events: none;
}

/* Categories */
.categories {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-2);
  margin-bottom: var(--spacing-6);
  justify-content: center;
}

.categoryBtn {
  padding: var(--spacing-2) var(--spacing-3);
  font-size: var(--font-size-14);
  font-weight: 500;
  background: var(--surface-subtle);
  color: var(--text-secondary);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-control);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.categoryBtn:hover {
  background: var(--surface-layer);
  color: var(--text-primary);
  border-color: var(--border-strong);
}

.categoryBtn.active {
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
  border-color: var(--interactive-primary);
}

/* Icon Grid - Responsive auto-fit */
.iconGrid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(100px, 1fr));
  gap: var(--spacing-3);
  max-width: 1400px;
  margin: 0 auto var(--spacing-8);
}

.iconItem {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--spacing-1);
  padding: var(--spacing-3);
  background: var(--surface-layer);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-control);
  cursor: pointer;
  transition: all var(--transition-fast);
  position: relative;
  min-height: 80px;
}

.iconItem:hover {
  background: var(--surface-base);
  border-color: var(--interactive-primary);
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.iconItem:focus {
  outline: none;
  border-color: var(--interactive-primary);
  box-shadow: 0 0 0 3px var(--interactive-primary-subtle);
}

.iconDisplay {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  color: var(--text-primary);
}

.iconName {
  font-size: var(--font-size-11);
  color: var(--text-secondary);
  text-align: center;
  word-break: break-word;
  line-height: 1.3;
  max-width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
}

.copiedBadge {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--radius-sm);
  font-size: var(--font-size-11);
  font-weight: 600;
  animation: fadeInOut 1.5s ease;
  z-index: var(--z-dropdown);
  pointer-events: none;
}

@keyframes fadeInOut {
  0% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.8);
  }
  15% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  85% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.8);
  }
}

/* No Results */
.noResults {
  text-align: center;
  padding: var(--spacing-8);
  color: var(--text-secondary);
}

.noResults p {
  margin: 0;
  font-size: var(--font-size-16);
}

/* Usage Info */
.usageInfo {
  max-width: 600px;
  margin: var(--spacing-8) auto 0;
  padding: var(--spacing-4);
  background: var(--surface-layer);
  border-radius: var(--radius-control);
  border: 1px solid var(--border-subtle);
}

.usageInfo h3 {
  margin: 0 0 var(--spacing-3) 0;
  font-size: var(--font-size-16);
  color: var(--text-primary);
}

.codeBlock {
  margin: 0;
  padding: var(--spacing-3);
  background: var(--surface-base);
  border-radius: var(--radius-sm);
  border: 1px solid var(--border-subtle);
  overflow-x: auto;
  font-family: var(--font-mono);
  font-size: var(--font-size-13);
  color: var(--text-primary);
  line-height: 1.5;
}

/* Responsive */
@media (max-width: 768px) {
  .gallery {
    padding: var(--spacing-4);
  }

  .header h1 {
    font-size: var(--font-size-24);
  }

  .searchContainer {
    max-width: 100%;
  }

  .categories {
    justify-content: flex-start;
    overflow-x: auto;
    flex-wrap: nowrap;
    padding-bottom: var(--spacing-2);
    -webkit-overflow-scrolling: touch;
  }

  .categoryBtn {
    flex-shrink: 0;
  }

  .iconGrid {
    grid-template-columns: repeat(auto-fill, minmax(80px, 1fr));
    gap: var(--spacing-2);
  }

  .iconItem {
    min-height: 70px;
    padding: var(--spacing-2);
  }

  .iconDisplay {
    width: 32px;
    height: 32px;
  }

  .iconName {
    font-size: var(--font-size-10);
  }
}

@media (max-width: 480px) {
  .header h1 {
    font-size: var(--font-size-20);
  }

  .iconGrid {
    grid-template-columns: repeat(3, 1fr);
  }

  .usageInfo {
    padding: var(--spacing-3);
  }

  .codeBlock {
    font-size: var(--font-size-12);
    padding: var(--spacing-2);
  }
}

