/**
 * Icon Component
 *
 * A normalized wrapper for Lucide icons that provides:
 * - Consistent sizing via design tokens
 * - Semantic color variants
 * - Proper accessibility handling
 * - Tree-shaking friendly (icons imported directly from lucide-react)
 *
 * @example Basic usage
 * ```tsx
 * import { Icon } from '@orion/react';
 * import { Search, AlertCircle, Check } from 'lucide-react';
 *
 * // Decorative icon (hidden from screen readers)
 * <Icon icon={Search} size="md" decorative />
 *
 * // Semantic icon (announced to screen readers)
 * <Icon icon={AlertCircle} size="lg" label="Warning" color="warning" />
 *
 * // Custom pixel size
 * <Icon icon={Check} size={18} color="success" />
 * ```
 *
 * @example In Button component
 * ```tsx
 * import { Button, Icon } from '@orion/react';
 * import { Download } from 'lucide-react';
 *
 * <Button icon={<Icon icon={Download} decorative />}>
 *   Download File
 * </Button>
 * ```
 */

import { forwardRef, useMemo } from "react";
import type { IconProps, IconSize } from "./Icon.types";
import styles from "./Icon.module.css";

/**
 * Size token to pixel mapping
 * These values must match the CSS variables in theme.css
 */
const SIZE_MAP: Record<IconSize, number> = {
  xs: 12,
  sm: 16,
  md: 20,
  lg: 24,
  xl: 32,
};

export const Icon = forwardRef<SVGSVGElement, IconProps>(
  (
    {
      icon: IconComponent,
      size = "md",
      color = "current",
      strokeWidth = 2,
      label,
      decorative = false,
      disabled = false,
      className,
      style,
      ...rest
    },
    ref,
  ) => {
    // Resolve size to pixels
    const resolvedSize = useMemo(() => {
      if (typeof size === "number") {
        return size;
      }
      return SIZE_MAP[size];
    }, [size]);

    // Build class names
    const classNames = useMemo(() => {
      const classes = [styles.icon];

      // Add size class only for token sizes (not custom pixels)
      if (typeof size === "string") {
        classes.push(styles[size]);
      }

      // Add color class
      classes.push(styles[color]);

      // Add state classes
      if (disabled) {
        classes.push(styles.disabled);
      }

      // Add custom class
      if (className) {
        classes.push(className);
      }

      return classes.filter(Boolean).join(" ");
    }, [size, color, disabled, className]);

    // Accessibility attributes
    const a11yProps = useMemo(() => {
      if (decorative) {
        // Decorative icons are hidden from screen readers
        return {
          "aria-hidden": true as const,
          role: "presentation" as const,
        };
      }

      // Semantic icons need accessible name
      return {
        "aria-label": label,
        role: "img" as const,
      };
    }, [decorative, label]);

    // Merge custom styles with size override for pixel values
    const mergedStyle = useMemo(() => {
      if (typeof size === "number") {
        return {
          width: size,
          height: size,
          ...style,
        };
      }
      return style;
    }, [size, style]);

    return (
      <IconComponent
        ref={ref}
        className={classNames}
        size={resolvedSize}
        strokeWidth={strokeWidth}
        style={mergedStyle}
        {...a11yProps}
        {...rest}
      />
    );
  },
);

Icon.displayName = "Icon";

/**
 * Icon Component Styles
 *
 * Normalized icon styling with design token integration.
 * All sizes and colors use CSS variables for theming.
 */

/* Base icon wrapper */
.icon {
  /* Layout */
  display: inline-flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;

  /* Default to inherit color */
  color: currentColor;

  /* Smooth transitions for state changes */
  transition:
    color var(--transition-fast),
    opacity var(--transition-fast);
}

/* ============================================================================
 * SIZES (mapped to --icon-size-* tokens)
 * ============================================================================ */

.xs {
  width: var(--icon-size-sm); /* 16px - xs uses sm token as minimum */
  height: var(--icon-size-sm);
}

.sm {
  width: var(--icon-size-sm); /* 16px */
  height: var(--icon-size-sm);
}

.md {
  width: var(--icon-size-md); /* 20px */
  height: var(--icon-size-md);
}

.lg {
  width: var(--icon-size-lg); /* 24px */
  height: var(--icon-size-lg);
}

.xl {
  width: var(--icon-size-xl); /* 32px */
  height: var(--icon-size-xl);
}

/* ============================================================================
 * COLORS (mapped to semantic tokens)
 * ============================================================================ */

.current {
  color: currentColor;
}

.primary {
  color: var(--text-primary);
}

.secondary {
  color: var(--text-secondary);
}

.tertiary {
  color: var(--text-tertiary);
}

.brand {
  color: var(--text-brand);
}

.success {
  color: var(--status-success);
}

.warning {
  color: var(--status-warning);
}

.error {
  color: var(--status-error);
}

.info {
  color: var(--status-info);
}

.inverse {
  color: var(--text-inverse);
}

/* ============================================================================
 * STATES
 * ============================================================================ */

.disabled {
  opacity: 0.5;
  pointer-events: none;
}

/**
 * Icon Component - Render Lucide icons with Orion styling.
 *
 * Prefer importing icons directly from lucide-react for better tree-shaking.
 *
 * @example
 * ```tsx
 * import { Icon } from '@orion-ds/react';
 * // Or directly: import { Search, Settings } from 'lucide-react';
 *
 * <Icon name="search" size="md" />
 * <Icon name="settings" size="lg" color="brand" />
 *
 * // Preferred: Direct import from lucide-react
 * import { Search } from 'lucide-react';
 * <Search size={20} />
 * ```
 */
export { Icon } from "./Icon";
export type {
  IconProps,
  IconSize,
  IconColor,
  IconRenderProps,
} from "./Icon.types";
