/**
 * Link Component
 *
 * A styled anchor element for navigation.
 *
 * @example
 * ```tsx
 * <Link href="/about">About us</Link>
 * <Link href="https://docs.example.com" external>Documentation</Link>
 * <Link href="/profile" variant="brand">View Profile</Link>
 * ```
 */

import { forwardRef } from 'react';
import { ExternalLink as ExternalLinkIcon } from 'lucide-react';
import type { LinkProps } from './Link.types';
import styles from './Link.module.css';

export const Link = forwardRef<HTMLAnchorElement, LinkProps>(
  (
    {
      variant = 'default',
      size = 'md',
      external = false,
      underline = true,
      showExternalIcon = true,
      icon,
      iconRight,
      iconAnimation,
      className,
      children,
      ...rest
    },
    ref,
  ) => {
    const classNames = [
      styles.link,
      styles[variant],
      styles[size],
      underline && styles.underline,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    // Security attributes for external links
    const externalProps = external
      ? {
          target: '_blank',
          rel: 'noopener noreferrer',
        }
      : {};

    // Get icon size based on link size
    const getIconSize = () => {
      switch (size) {
        case 'sm':
          return 12;
        case 'lg':
          return 16;
        default:
          return 14;
      }
    };

    // Get icon animation class
    const getIconAnimationClass = (position: 'left' | 'right') => {
      if (!iconAnimation) {
        // Default animations based on context
        if (position === 'right' && external) return styles.iconExternal;
        return '';
      }
      switch (iconAnimation) {
        case 'arrow':
          return styles.iconArrow;
        case 'arrow-left':
          return styles.iconArrowLeft;
        case 'external':
          return styles.iconExternal;
        default:
          return '';
      }
    };

    // Determine the right icon to show
    const renderRightIcon = () => {
      if (iconRight) {
        return (
          <span
            className={`${styles.icon} ${styles.iconRight} ${getIconAnimationClass('right')}`}
            aria-hidden="true"
          >
            {iconRight}
          </span>
        );
      }

      // Auto-show external icon if external and showExternalIcon is true
      if (external && showExternalIcon) {
        return (
          <span
            className={`${styles.icon} ${styles.iconRight} ${styles.iconExternal}`}
            aria-hidden="true"
          >
            <ExternalLinkIcon size={getIconSize()} />
          </span>
        );
      }

      return null;
    };

    return (
      <a ref={ref} className={classNames} {...externalProps} {...rest}>
        {icon && (
          <span className={`${styles.icon} ${getIconAnimationClass('left')}`} aria-hidden="true">
            {icon}
          </span>
        )}
        <span>{children}</span>
        {renderRightIcon()}
      </a>
    );
  },
);

Link.displayName = 'Link';

/**
 * Link Component Types
 *
 * Type definitions for the Orion Link component.
 */

import type { AnchorHTMLAttributes, ReactNode } from 'react';

/**
 * Link visual variants
 */
export type LinkVariant = 'default' | 'subtle' | 'brand';

/**
 * Link sizes
 */
export type LinkSize = 'sm' | 'md' | 'lg';

/**
 * Icon animation types
 */
export type LinkIconAnimation = 'arrow' | 'arrow-left' | 'external' | 'none';

/**
 * Link component props
 *
 * @example
 * ```tsx
 * <Link href="/about">About us</Link>
 * <Link href="/docs" variant="brand" external>Documentation</Link>
 * ```
 */
export interface LinkProps extends AnchorHTMLAttributes<HTMLAnchorElement> {
  /**
   * Visual variant of the link
   * @default 'default'
   */
  variant?: LinkVariant;

  /**
   * Size of the link
   * @default 'md'
   */
  size?: LinkSize;

  /**
   * Whether the link opens in a new tab
   * Automatically adds rel="noopener noreferrer" for security
   * @default false
   */
  external?: boolean;

  /**
   * Whether to show an underline
   * @default true
   */
  underline?: boolean;

  /**
   * Whether to show the external link icon automatically when external is true
   * @default true
   */
  showExternalIcon?: boolean;

  /**
   * Icon to display before the link text
   */
  icon?: ReactNode;

  /**
   * Icon to display after the link text (e.g., external link icon)
   */
  iconRight?: ReactNode;

  /**
   * Icon hover animation type
   * - 'arrow': moves icon right on hover
   * - 'arrow-left': moves icon left on hover
   * - 'external': diagonal movement for external links
   * - 'none': no animation
   */
  iconAnimation?: LinkIconAnimation;

  /**
   * Link content
   */
  children?: ReactNode;
}

/**
 * Link Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Base link styles */
.link {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-2);
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  text-decoration: none;
  cursor: pointer;
  transition: color var(--transition-fast);

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
    border-radius: var(--radius-sm);
  }
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.default {
  color: var(--text-primary);

  &:hover {
    color: var(--text-brand);
  }
}

.subtle {
  color: var(--text-secondary);

  &:hover {
    color: var(--text-primary);
  }
}

.brand {
  color: var(--text-brand);

  &:hover {
    color: var(--interactive-primary-hover);
  }
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm {
  font-size: var(--font-size-12);
  gap: var(--spacing-1);
}

.md {
  font-size: var(--font-size-14);
  gap: var(--spacing-2);
}

.lg {
  font-size: var(--font-size-16);
  gap: var(--spacing-2);
}

/* ============================================================================
 * MODIFIERS
 * ============================================================================ */

.underline {
  text-decoration: underline;
  text-decoration-color: color-mix(in srgb, currentColor 30%, transparent);
  text-underline-offset: 3px;
  text-decoration-thickness: 1px;

  &:hover {
    text-decoration-color: currentColor;
  }
}

/* ============================================================================
 * ICONS
 * ============================================================================ */

.icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  opacity: 0.7;
  transition:
    opacity var(--transition-fast),
    transform var(--transition-fast);
}

.link:hover .icon {
  opacity: 1;
}

/* Icon sizes based on link size */
.sm .icon {
  width: 12px;
  height: 12px;
}

.md .icon {
  width: 14px;
  height: 14px;
}

.lg .icon {
  width: 16px;
  height: 16px;
}

/* Right icon (external, arrow, etc.) */
.iconRight {
  /* Animation on hover - subtle movement */
}

/* External link icon animation */
.link:hover .iconExternal {
  transform: translate(1px, -1px);
}

/* Arrow right animation */
.link:hover .iconArrow {
  transform: translateX(2px);
}

/* Arrow left animation */
.link:hover .iconArrowLeft {
  transform: translateX(-2px);
}
