/**
 * List Component
 *
 * A flexible list component for displaying items with various layouts.
 *
 * @example
 * ```tsx
 * <List
 *   items={[
 *     { id: '1', primary: 'Item 1', secondary: 'Description' },
 *     { id: '2', primary: 'Item 2', leading: <UserIcon /> },
 *   ]}
 *   interactive
 * />
 * ```
 */

import { forwardRef, useCallback } from "react";
import { Check } from "lucide-react";
import type { ListProps, ListItem, ListItemProps } from "./List.types";
import styles from "./List.module.css";

/**
 * Single List Item
 */
const ListItemComponent = ({
  item,
  size,
  interactive,
  selectable,
  onClick,
}: ListItemProps) => {
  const handleClick = useCallback(() => {
    if (!item.disabled && (interactive || selectable) && item.onClick) {
      item.onClick();
    }
    onClick?.();
  }, [item, interactive, selectable, onClick]);

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent) => {
      if ((e.key === "Enter" || e.key === " ") && (interactive || selectable)) {
        e.preventDefault();
        handleClick();
      }
    },
    [interactive, selectable, handleClick],
  );

  const itemClasses = [
    styles.item,
    styles[size],
    interactive && styles.interactive,
    selectable && styles.selectable,
    item.selected && styles.selected,
    item.disabled && styles.disabled,
    item.className,
  ]
    .filter(Boolean)
    .join(" ");

  const isClickable =
    !item.disabled && (interactive || selectable || item.onClick);

  return (
    <li
      className={itemClasses}
      role={selectable ? "option" : interactive ? "button" : undefined}
      tabIndex={isClickable ? 0 : undefined}
      aria-selected={selectable ? item.selected : undefined}
      aria-disabled={item.disabled}
      onClick={isClickable ? handleClick : undefined}
      onKeyDown={isClickable ? handleKeyDown : undefined}
    >
      {item.leading && (
        <span className={styles.leading} aria-hidden="true">
          {item.leading}
        </span>
      )}

      <div className={styles.content}>
        <span className={styles.primary}>{item.primary}</span>
        {item.secondary && (
          <span className={styles.secondary}>{item.secondary}</span>
        )}
      </div>

      {item.trailing && (
        <span className={styles.trailing}>{item.trailing}</span>
      )}

      {selectable && item.selected && (
        <span className={styles.checkmark} aria-hidden="true">
          <Check size={size === "sm" ? 14 : size === "lg" ? 20 : 16} />
        </span>
      )}
    </li>
  );
};

export const List = forwardRef<HTMLUListElement, ListProps>(
  (
    {
      items,
      size = "md",
      variant = "default",
      interactive = false,
      selectable = false,
      onSelect,
      renderItem,
      emptyContent,
      className,
      ...rest
    },
    ref,
  ) => {
    const handleItemClick = useCallback(
      (item: ListItem) => {
        if (onSelect && !item.disabled) {
          onSelect(item);
        }
      },
      [onSelect],
    );

    const listClasses = [styles.list, styles[variant], className]
      .filter(Boolean)
      .join(" ");

    if (items.length === 0 && emptyContent) {
      return <div className={styles.empty}>{emptyContent}</div>;
    }

    return (
      <ul
        ref={ref}
        className={listClasses}
        role={selectable ? "listbox" : "list"}
        {...rest}
      >
        {items.map((item, index) =>
          renderItem ? (
            <li key={item.id} className={styles.customItem}>
              {renderItem(item, index)}
            </li>
          ) : (
            <ListItemComponent
              key={item.id}
              item={item}
              size={size}
              interactive={interactive}
              selectable={selectable}
              onClick={() => handleItemClick(item)}
            />
          ),
        )}
      </ul>
    );
  },
);

List.displayName = "List";

/**
 * List Component Types
 *
 * Type definitions for the Orion List component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * List size variants
 */
export type ListSize = "sm" | "md" | "lg";

/**
 * List variant
 */
export type ListVariant = "default" | "bordered" | "divided";

/**
 * List item
 */
export interface ListItem {
  /**
   * Unique identifier
   */
  id: string;

  /**
   * Primary content (text or element)
   */
  primary: ReactNode;

  /**
   * Secondary content (smaller text below primary)
   */
  secondary?: ReactNode;

  /**
   * Icon or avatar on the left
   */
  leading?: ReactNode;

  /**
   * Content on the right (icon, badge, etc.)
   */
  trailing?: ReactNode;

  /**
   * Whether this item is disabled
   */
  disabled?: boolean;

  /**
   * Whether this item is selected (for selectable lists)
   */
  selected?: boolean;

  /**
   * Click handler for the item
   */
  onClick?: () => void;

  /**
   * Additional class name for the item
   */
  className?: string;
}

/**
 * List component props
 *
 * @example
 * ```tsx
 * <List
 *   items={[
 *     { id: '1', primary: 'Item 1', secondary: 'Description' },
 *     { id: '2', primary: 'Item 2', leading: <UserIcon /> },
 *   ]}
 * />
 * ```
 */
export interface ListProps extends Omit<
  HTMLAttributes<HTMLUListElement>,
  "onSelect"
> {
  /**
   * List items
   */
  items: ListItem[];

  /**
   * Size variant
   * @default 'md'
   */
  size?: ListSize;

  /**
   * Visual variant
   * @default 'default'
   */
  variant?: ListVariant;

  /**
   * Whether the list items are interactive (hoverable/clickable)
   * @default false
   */
  interactive?: boolean;

  /**
   * Whether items can be selected
   * @default false
   */
  selectable?: boolean;

  /**
   * Callback when an item is selected
   */
  onSelect?: (item: ListItem) => void;

  /**
   * Render custom list item content
   */
  renderItem?: (item: ListItem, index: number) => ReactNode;

  /**
   * Custom empty state when no items
   */
  emptyContent?: ReactNode;
}

/**
 * List Item component props (internal)
 */
export interface ListItemProps {
  item: ListItem;
  size: ListSize;
  interactive: boolean;
  selectable: boolean;
  onClick?: () => void;
}

/**
 * List Component Styles
 */

.list {
  margin: 0;
  padding: 0;
  list-style: none;
}

/* Variants */
.bordered {
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-control);
  overflow: hidden;
}

.divided .item:not(:last-child) {
  border-bottom: 1px solid var(--border-subtle);
}

.bordered .item:not(:last-child) {
  border-bottom: 1px solid var(--border-subtle);
}

/* List Item */
.item {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  padding: var(--spacing-3) var(--spacing-4);
  background: var(--surface-base);
  transition: background-color var(--duration-fast) var(--ease-out);
}

/* Size variants */
.sm {
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-3);
  font-size: var(--font-size-sm);
}

.md {
  font-size: var(--font-size-base);
}

.lg {
  gap: var(--spacing-4);
  padding: var(--spacing-4) var(--spacing-5);
  font-size: var(--font-size-lg);
}

/* Interactive items */
.interactive {
  cursor: pointer;
}

.interactive:hover:not(.disabled) {
  background: var(--surface-subtle);
}

.interactive:active:not(.disabled) {
  background: var(--surface-layer);
}

.interactive:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: -2px;
}

/* Selectable items */
.selectable {
  cursor: pointer;
  padding-right: var(--spacing-10);
}

.selectable:hover:not(.disabled) {
  background: var(--surface-subtle);
}

.selected {
  background: var(--interactive-primary-subtle);
}

.selected:hover:not(.disabled) {
  background: var(--interactive-primary-subtle);
}

/* Disabled state */
.disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Leading content (icon/avatar) */
.leading {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  color: var(--text-secondary);
}

.sm .leading {
  width: 20px;
  height: 20px;
}

.md .leading {
  width: 24px;
  height: 24px;
}

.lg .leading {
  width: 32px;
  height: 32px;
}

/* Content */
.content {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.primary {
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.secondary {
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.sm .secondary {
  font-size: var(--font-size-xs);
}

.lg .secondary {
  font-size: var(--font-size-base);
}

/* Trailing content */
.trailing {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--text-tertiary);
}

/* Checkmark for selectable */
.checkmark {
  position: absolute;
  right: var(--spacing-3);
  display: flex;
  align-items: center;
  color: var(--interactive-primary);
}

.selectable {
  position: relative;
}

/* Custom item wrapper */
.customItem {
  list-style: none;
}

/* Empty state */
.empty {
  padding: var(--spacing-8);
  text-align: center;
  color: var(--text-secondary);
}

/**
 * List Component - Vertical item list display.
 *
 * @example
 * ```tsx
 * import { List } from '@orion-ds/react';
 * import { User, Settings, LogOut } from 'lucide-react';
 *
 * <List
 *   items={[
 *     { id: '1', label: 'Profile', icon: <User size={18} />, onClick: goToProfile },
 *     { id: '2', label: 'Settings', icon: <Settings size={18} />, onClick: goToSettings },
 *     { id: '3', label: 'Log out', icon: <LogOut size={18} />, onClick: handleLogout },
 *   ]}
 * />
 * ```
 */
export { List } from "./List";
export type { ListProps, ListItem, ListSize, ListVariant } from "./List.types";
