/**
 * LogoCloud Component
 *
 * A logo cloud section for displaying partner/client logos.
 * Supports grid, inline, and animated marquee layouts.
 *
 * @example
 * ```tsx
 * <LogoCloud
 *   eyebrow="Trusted by"
 *   title="Industry leaders use Orion"
 *   logos={[
 *     { logo: <img src="/vercel.svg" alt="" />, name: "Vercel" },
 *     { logo: <img src="/stripe.svg" alt="" />, name: "Stripe" },
 *     { logo: <img src="/linear.svg" alt="" />, name: "Linear" },
 *     { logo: <img src="/notion.svg" alt="" />, name: "Notion" },
 *     { logo: <img src="/figma.svg" alt="" />, name: "Figma" }
 *   ]}
 *   layout="inline"
 *   grayscale={true}
 * />
 * ```
 */

import { forwardRef } from "react";
import type { LogoCloudProps } from "./LogoCloud.types";
import { Section } from "../Section";
import { Container } from "../Container";
import styles from "./LogoCloud.module.css";

export const LogoCloud = forwardRef<HTMLElement, LogoCloudProps>(
  (
    {
      eyebrow,
      title,
      description,
      logos,
      layout = "inline",
      columns = 5,
      size = "md",
      grayscale = true,
      background = "none",
      centered = true,
      marqueeSpeed = "normal",
      className,
      ...rest
    },
    ref,
  ) => {
    const hasHeader = eyebrow || title || description;

    const classNames = [
      styles.logoCloud,
      styles[`layout-${layout}`],
      styles[`size-${size}`],
      grayscale && styles.grayscale,
      centered && styles.centered,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    const renderLogo = (item: (typeof logos)[0], index: number) => {
      const logoElement = (
        <div className={styles.logo} key={item.name || index}>
          {item.logo}
          <span className="sr-only">{item.name}</span>
        </div>
      );

      if (item.href) {
        return (
          <a
            key={item.name || index}
            href={item.href}
            className={styles.logoLink}
            target="_blank"
            rel="noopener noreferrer"
            aria-label={item.name}
          >
            {item.logo}
          </a>
        );
      }

      return logoElement;
    };

    const renderLogos = () => {
      if (layout === "marquee") {
        return (
          <div
            className={`${styles.marquee} ${styles[`speed-${marqueeSpeed}`]}`}
          >
            <div className={styles.marqueeTrack}>
              {logos.map(renderLogo)}
              {/* Duplicate for seamless loop */}
              {logos.map((item, index) =>
                renderLogo(item, index + logos.length),
              )}
            </div>
          </div>
        );
      }

      return (
        <div
          className={`${styles.logosContainer} ${layout === "grid" ? styles[`cols-${columns}`] : ""}`}
        >
          {logos.map(renderLogo)}
        </div>
      );
    };

    return (
      <Section
        ref={ref}
        spacing="md"
        background={background}
        className={classNames}
        {...rest}
      >
        <Container size="xl">
          {hasHeader && (
            <header className={styles.header}>
              {eyebrow && <div className={styles.eyebrow}>{eyebrow}</div>}
              {title && <h2 className={styles.title}>{title}</h2>}
              {description && (
                <p className={styles.description}>{description}</p>
              )}
            </header>
          )}

          {renderLogos()}
        </Container>
      </Section>
    );
  },
);

LogoCloud.displayName = "LogoCloud";

/**
 * LogoCloud Component Types
 *
 * Type definitions for the Orion LogoCloud section component.
 */

import type { HTMLAttributes, ReactNode } from "react";

/**
 * LogoCloud layout variant
 */
export type LogoCloudLayout = "grid" | "inline" | "marquee";

/**
 * Logo size variant
 */
export type LogoCloudSize = "sm" | "md" | "lg";

/**
 * Logo item data structure
 */
export interface LogoItem {
  /**
   * Logo element (typically an img or svg)
   */
  logo: ReactNode;

  /**
   * Company/brand name (for accessibility)
   */
  name: string;

  /**
   * Optional link URL
   */
  href?: string;
}

/**
 * LogoCloud section props
 *
 * @example
 * ```tsx
 * <LogoCloud
 *   title="Trusted by leading companies"
 *   logos={[
 *     { logo: <img src="/vercel.svg" alt="" />, name: "Vercel" },
 *     { logo: <img src="/stripe.svg" alt="" />, name: "Stripe" },
 *     { logo: <img src="/linear.svg" alt="" />, name: "Linear" }
 *   ]}
 *   layout="grid"
 *   columns={5}
 * />
 * ```
 */
export interface LogoCloudProps extends Omit<
  HTMLAttributes<HTMLElement>,
  "title"
> {
  /**
   * Optional eyebrow/badge above title
   */
  eyebrow?: ReactNode;

  /**
   * Section title
   */
  title?: ReactNode;

  /**
   * Section description
   */
  description?: ReactNode;

  /**
   * Array of logo items
   */
  logos: LogoItem[];

  /**
   * Layout variant
   * - grid: Fixed grid layout
   * - inline: Flexible inline/wrap layout
   * - marquee: Animated scrolling marquee
   * @default 'inline'
   */
  layout?: LogoCloudLayout;

  /**
   * Number of columns (for grid layout)
   * @default 5
   */
  columns?: 3 | 4 | 5 | 6;

  /**
   * Logo size
   * @default 'md'
   */
  size?: LogoCloudSize;

  /**
   * Grayscale logos (colorize on hover)
   * @default true
   */
  grayscale?: boolean;

  /**
   * Background style
   * @default 'none'
   */
  background?: "base" | "subtle" | "none";

  /**
   * Center the content
   * @default true
   */
  centered?: boolean;

  /**
   * Marquee animation speed (for marquee layout)
   * @default 'normal'
   */
  marqueeSpeed?: "slow" | "normal" | "fast";
}

/**
 * LogoCloud Component Exports
 */

export { LogoCloud } from "./LogoCloud";
export type {
  LogoCloudProps,
  LogoItem,
  LogoCloudLayout,
  LogoCloudSize,
} from "./LogoCloud.types";
