/**
 * MetricCards Component
 *
 * A row of KPI cards for dashboard overviews.
 * Optimized for Product Mode with minimal visual noise and data density.
 *
 * @example
 * ```tsx
 * <MetricCards
 *   metrics={[
 *     { label: 'Revenue', value: '$12,345', change: { value: '+12%', positive: true } },
 *     { label: 'Users', value: '1,234', change: { value: '+5%', positive: true } },
 *     { label: 'Conversion', value: '3.2%', change: { value: '-0.5%', positive: false } }
 *   ]}
 *   columns={3}
 * />
 * ```
 */

import { forwardRef } from 'react';
import { TrendingUp, TrendingDown } from 'lucide-react';
import type { MetricCardsProps, MetricCardProps } from './MetricCards.types';
import styles from './MetricCards.module.css';

// Sparkline mini component
const Sparkline = ({ data }: { data: number[] }) => {
  if (!data || data.length < 2) return null;

  const max = Math.max(...data);
  const min = Math.min(...data);
  const range = max - min || 1;
  const firstValue = data[0];
  const lastValue = data[data.length - 1];
  const height = 24;
  const width = 64;
  const stepX = width / (data.length - 1);

  const points = data
    .map((value, index) => {
      const x = index * stepX;
      const y = height - ((value - min) / range) * height;
      return `${x},${y}`;
    })
    .join(' ');

  const isPositive = firstValue !== undefined && lastValue !== undefined && lastValue >= firstValue;

  return (
    <svg
      className={styles.sparkline}
      width={width}
      height={height}
      viewBox={`0 0 ${width} ${height}`}
    >
      <polyline
        fill="none"
        stroke={isPositive ? 'var(--text-success)' : 'var(--text-error)'}
        strokeWidth="1.5"
        strokeLinecap="round"
        strokeLinejoin="round"
        points={points}
      />
    </svg>
  );
};

// Individual MetricCard component
const MetricCard = forwardRef<HTMLDivElement, MetricCardProps>(
  ({ metric, variant = 'default', loading = false, className, ...rest }, ref) => {
    const {
      label,
      value,
      change,
      icon,
      sparkline,
      href,
      onClick,
      description,
      loading: itemLoading,
    } = metric;

    const isLoading = loading || itemLoading;
    const isInteractive = href || onClick;

    const classNames = [
      styles.card,
      styles[`variant-${variant}`],
      isInteractive && styles.interactive,
      isLoading && styles.loading,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const content = (
      <>
        <div className={styles.cardHeader}>
          {icon && <span className={styles.cardIcon}>{icon}</span>}
          <span className={styles.label}>{label}</span>
        </div>

        <div className={styles.cardBody}>
          {isLoading ? (
            <div className={styles.skeleton} />
          ) : (
            <>
              <span className={styles.value}>{value}</span>
              {sparkline && variant === 'detailed' && <Sparkline data={sparkline} />}
            </>
          )}
        </div>

        {(change || description) && !isLoading && (
          <div className={styles.cardFooter}>
            {change && (
              <span
                className={`${styles.change} ${change.positive ? styles.positive : styles.negative}`}
              >
                {change.positive ? <TrendingUp size={12} /> : <TrendingDown size={12} />}
                <span>{change.value}</span>
                {change.label && <span className={styles.changeLabel}>{change.label}</span>}
              </span>
            )}
            {description && variant === 'detailed' && (
              <span className={styles.description}>{description}</span>
            )}
          </div>
        )}
      </>
    );

    if (href) {
      return (
        <a
          ref={ref as React.Ref<HTMLAnchorElement>}
          href={href}
          className={classNames}
          {...(rest as React.AnchorHTMLAttributes<HTMLAnchorElement>)}
        >
          {content}
        </a>
      );
    }

    if (onClick) {
      return (
        <button
          ref={ref as React.Ref<HTMLButtonElement>}
          type="button"
          onClick={onClick}
          className={classNames}
          {...(rest as React.ButtonHTMLAttributes<HTMLButtonElement>)}
        >
          {content}
        </button>
      );
    }

    return (
      <div ref={ref} className={classNames} {...rest}>
        {content}
      </div>
    );
  },
);

MetricCard.displayName = 'MetricCard';

// Main MetricCards component
export const MetricCards = forwardRef<HTMLDivElement, MetricCardsProps>(
  ({ metrics, columns = 4, variant = 'default', loading = false, className, ...rest }, ref) => {
    const classNames = [styles.metricCards, styles[`cols-${columns}`], className]
      .filter(Boolean)
      .join(' ');

    return (
      <div ref={ref} className={classNames} {...rest}>
        {metrics.map((metric, index) => (
          <MetricCard
            key={metric.id || metric.label || index}
            metric={metric}
            variant={variant}
            loading={loading}
          />
        ))}
      </div>
    );
  },
);

MetricCards.displayName = 'MetricCards';

export { MetricCard };

/**
 * MetricCards Component Types
 *
 * Type definitions for the Orion MetricCards section component.
 * Designed for Product Mode (SaaS dashboards) KPI displays.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Trend indicator for a metric
 */
export interface MetricTrend {
  /**
   * Trend value (e.g., "+12%", "-5%")
   */
  value: string;

  /**
   * Whether the trend is positive
   * @default true
   */
  positive?: boolean;

  /**
   * Comparison period label (e.g., "vs last month")
   */
  label?: string;
}

/**
 * Sparkline data point
 */
export type SparklineData = number[];

/**
 * Individual metric item
 */
export interface MetricItem {
  /**
   * Unique identifier
   */
  id?: string;

  /**
   * Metric label
   */
  label: string;

  /**
   * Main value (e.g., "$12,345", "89%", "1,234")
   */
  value: string | number;

  /**
   * Optional change/trend indicator
   */
  change?: MetricTrend;

  /**
   * Optional icon element
   */
  icon?: ReactNode;

  /**
   * Optional sparkline data
   */
  sparkline?: SparklineData;

  /**
   * Optional link to detailed view
   */
  href?: string;

  /**
   * Click handler (alternative to href)
   */
  onClick?: () => void;

  /**
   * Additional description
   */
  description?: string;

  /**
   * Loading state for this metric
   */
  loading?: boolean;
}

/**
 * MetricCards column count
 */
export type MetricCardsColumns = 2 | 3 | 4 | 5;

/**
 * MetricCards visual variant
 */
export type MetricCardsVariant = 'default' | 'compact' | 'detailed';

/**
 * MetricCards section props
 *
 * @example
 * ```tsx
 * <MetricCards
 *   metrics={[
 *     { label: 'Revenue', value: '$12,345', change: { value: '+12%', positive: true } },
 *     { label: 'Users', value: '1,234', change: { value: '+5%', positive: true } },
 *     { label: 'Conversion', value: '3.2%', change: { value: '-0.5%', positive: false } }
 *   ]}
 *   columns={3}
 * />
 * ```
 */
export interface MetricCardsProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Array of metric items
   */
  metrics: MetricItem[];

  /**
   * Number of grid columns
   * @default 4
   */
  columns?: MetricCardsColumns;

  /**
   * Visual variant
   * - default: Standard cards with borders
   * - compact: Smaller padding and text
   * - detailed: Includes sparklines and descriptions
   * @default 'default'
   */
  variant?: MetricCardsVariant;

  /**
   * Loading state for all cards
   * @default false
   */
  loading?: boolean;
}

/**
 * Individual MetricCard props (internal component)
 */
export interface MetricCardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Metric data
   */
  metric: MetricItem;

  /**
   * Visual variant
   */
  variant?: MetricCardsVariant;

  /**
   * Loading state
   */
  loading?: boolean;
}

export { MetricCards, MetricCard } from './MetricCards';
export type {
  MetricCardsProps,
  MetricCardProps,
  MetricItem,
  MetricTrend,
  SparklineData,
  MetricCardsColumns,
  MetricCardsVariant,
} from './MetricCards.types';
