/**
 * Navbar Component
 *
 * Application navigation header with brand, links, and actions.
 * Includes responsive mobile menu with Toggle and Collapse.
 *
 * @example
 * ```tsx
 * // Basic usage
 * <Navbar sticky bordered>
 *   <Navbar.Brand href="/">MyApp</Navbar.Brand>
 *   <Navbar.Nav>
 *     <Navbar.Link href="/" active>Home</Navbar.Link>
 *     <Navbar.Link href="/about">About</Navbar.Link>
 *   </Navbar.Nav>
 *   <Navbar.Actions>
 *     <Button>Get Started</Button>
 *   </Navbar.Actions>
 * </Navbar>
 *
 * // With responsive mobile menu
 * function ResponsiveNavbar() {
 *   const [isOpen, setIsOpen] = useState(false);
 *   return (
 *     <Navbar>
 *       <Navbar.Brand href="/">MyApp</Navbar.Brand>
 *       <Navbar.Nav>...</Navbar.Nav>
 *       <Navbar.Actions>...</Navbar.Actions>
 *       <Navbar.Toggle isOpen={isOpen} onToggle={() => setIsOpen(!isOpen)} />
 *       <Navbar.Collapse isOpen={isOpen}>
 *         <Navbar.Link href="/">Home</Navbar.Link>
 *         <Navbar.Link href="/about">About</Navbar.Link>
 *       </Navbar.Collapse>
 *     </Navbar>
 *   );
 * }
 * ```
 */

import React from "react";
import type {
  NavbarProps,
  NavbarBrandProps,
  NavbarNavProps,
  NavbarLinkProps,
  NavbarActionsProps,
  NavbarToggleProps,
  NavbarCollapseProps,
} from "./Navbar.types";
import styles from "./Navbar.module.css";

// ============================================================================
// Brand Component
// ============================================================================

const NavbarBrand: React.FC<NavbarBrandProps> = ({
  children,
  href,
  className,
}) => {
  const brandClasses = [styles.brand, className].filter(Boolean).join(" ");

  if (href) {
    return (
      <a href={href} className={brandClasses}>
        {children}
      </a>
    );
  }

  return <div className={brandClasses}>{children}</div>;
};

NavbarBrand.displayName = "Navbar.Brand";

// ============================================================================
// Nav Component (Links Container)
// ============================================================================

const NavbarNav: React.FC<NavbarNavProps> = ({
  children,
  className,
  ...rest
}) => {
  const navClasses = [styles.nav, className].filter(Boolean).join(" ");

  return (
    <nav className={navClasses} {...rest}>
      {children}
    </nav>
  );
};

NavbarNav.displayName = "Navbar.Nav";

// ============================================================================
// Link Component
// ============================================================================

const NavbarLink: React.FC<NavbarLinkProps> = ({
  children,
  href,
  active = false,
  className,
  onClick,
  ...rest
}) => {
  const linkClasses = [styles.link, active && styles.active, className]
    .filter(Boolean)
    .join(" ");

  return (
    <a
      href={href}
      className={linkClasses}
      aria-current={active ? "page" : undefined}
      onClick={onClick}
      {...rest}
    >
      {children}
    </a>
  );
};

NavbarLink.displayName = "Navbar.Link";

// ============================================================================
// Actions Component
// ============================================================================

const NavbarActions: React.FC<NavbarActionsProps> = ({
  children,
  className,
  ...rest
}) => {
  const actionsClasses = [styles.actions, className].filter(Boolean).join(" ");

  return (
    <div className={actionsClasses} {...rest}>
      {children}
    </div>
  );
};

NavbarActions.displayName = "Navbar.Actions";

// ============================================================================
// Toggle Component (Hamburger Button)
// ============================================================================

const NavbarToggle: React.FC<NavbarToggleProps> = ({
  isOpen,
  onToggle,
  className,
}) => {
  const toggleClasses = [styles.toggle, isOpen && styles.toggleOpen, className]
    .filter(Boolean)
    .join(" ");

  return (
    <button
      type="button"
      className={toggleClasses}
      onClick={onToggle}
      aria-expanded={isOpen}
      aria-label={isOpen ? "Close menu" : "Open menu"}
    >
      <span className={styles.toggleIcon}>
        <span className={styles.toggleLine} />
        <span className={styles.toggleLine} />
        <span className={styles.toggleLine} />
      </span>
    </button>
  );
};

NavbarToggle.displayName = "Navbar.Toggle";

// ============================================================================
// Collapse Component (Mobile Menu Container)
// ============================================================================

const NavbarCollapse: React.FC<NavbarCollapseProps> = ({
  children,
  isOpen,
  className,
  ...rest
}) => {
  const collapseClasses = [
    styles.collapse,
    isOpen && styles.collapseOpen,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  return (
    <div className={collapseClasses} {...rest}>
      {children}
    </div>
  );
};

NavbarCollapse.displayName = "Navbar.Collapse";

// ============================================================================
// Collapse Actions (Actions inside mobile menu)
// ============================================================================

const NavbarCollapseActions: React.FC<NavbarActionsProps> = ({
  children,
  className,
  ...rest
}) => {
  const actionsClasses = [styles.collapseActions, className]
    .filter(Boolean)
    .join(" ");

  return (
    <div className={actionsClasses} {...rest}>
      {children}
    </div>
  );
};

NavbarCollapseActions.displayName = "Navbar.CollapseActions";

// ============================================================================
// Main Navbar Component
// ============================================================================

export const Navbar: React.FC<NavbarProps> & {
  Brand: typeof NavbarBrand;
  Nav: typeof NavbarNav;
  Link: typeof NavbarLink;
  Actions: typeof NavbarActions;
  Toggle: typeof NavbarToggle;
  Collapse: typeof NavbarCollapse;
  CollapseActions: typeof NavbarCollapseActions;
} = ({
  children,
  height = "md",
  variant = "solid",
  colorScheme = "auto",
  sticky = false,
  bordered = true,
  className,
  ...rest
}) => {
  const navbarClasses = [
    styles.navbar,
    styles[height],
    styles[variant],
    colorScheme !== "auto" &&
      styles[
        `scheme${colorScheme.charAt(0).toUpperCase() + colorScheme.slice(1)}`
      ],
    sticky && styles.sticky,
    bordered && styles.bordered,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  return (
    <header className={navbarClasses} {...rest}>
      {children}
    </header>
  );
};

// Attach subcomponents
Navbar.Brand = NavbarBrand;
Navbar.Nav = NavbarNav;
Navbar.Link = NavbarLink;
Navbar.Actions = NavbarActions;
Navbar.Toggle = NavbarToggle;
Navbar.Collapse = NavbarCollapse;
Navbar.CollapseActions = NavbarCollapseActions;

Navbar.displayName = "Navbar";

/**
 * Navbar Component Types
 *
 * Type definitions for the Orion Navbar component.
 */

import type { ReactNode, HTMLAttributes } from "react";

/**
 * Navbar height variants
 */
export type NavbarHeight = "sm" | "md" | "lg";

/**
 * Navbar background variants
 */
export type NavbarVariant = "solid" | "transparent" | "glass";

/**
 * Navbar color scheme (for transparent/glass variants on colored backgrounds)
 */
export type NavbarColorScheme = "auto" | "light" | "dark";

/**
 * Navbar component props
 *
 * @example
 * ```tsx
 * <Navbar>
 *   <Navbar.Brand>
 *     <img src="/logo.svg" alt="Logo" />
 *   </Navbar.Brand>
 *   <Navbar.Nav>
 *     <Navbar.Link href="/">Home</Navbar.Link>
 *     <Navbar.Link href="/about">About</Navbar.Link>
 *   </Navbar.Nav>
 *   <Navbar.Actions>
 *     <Button>Sign In</Button>
 *   </Navbar.Actions>
 * </Navbar>
 * ```
 */
export interface NavbarProps extends HTMLAttributes<HTMLElement> {
  /**
   * Navbar content
   */
  children: ReactNode;

  /**
   * Navbar height
   * @default 'md'
   */
  height?: NavbarHeight;

  /**
   * Navbar variant
   * @default 'solid'
   */
  variant?: NavbarVariant;

  /**
   * Color scheme for text/links when using transparent or glass variants
   * - 'auto': Uses default theme colors (dark text on light, light text on dark)
   * - 'light': Forces light/white text (use on dark backgrounds)
   * - 'dark': Forces dark text (use on light backgrounds)
   * @default 'auto'
   */
  colorScheme?: NavbarColorScheme;

  /**
   * Sticky positioning
   * @default false
   */
  sticky?: boolean;

  /**
   * Border on bottom
   * @default true
   */
  bordered?: boolean;

  /**
   * Additional CSS class
   */
  className?: string;
}

/**
 * Navbar Brand props
 */
export interface NavbarBrandProps {
  children: ReactNode;
  href?: string;
  className?: string;
}

/**
 * Navbar Nav (navigation links container) props
 */
export interface NavbarNavProps extends HTMLAttributes<HTMLDivElement> {
  children: ReactNode;
}

/**
 * Navbar Link props
 */
export interface NavbarLinkProps extends HTMLAttributes<HTMLAnchorElement> {
  children: ReactNode;
  href: string;
  active?: boolean;
  /**
   * Callback when link is clicked (useful for closing mobile menu)
   */
  onClick?: () => void;
}

/**
 * Navbar Actions (right side actions) props
 */
export interface NavbarActionsProps extends HTMLAttributes<HTMLDivElement> {
  children: ReactNode;
}

/**
 * Navbar Toggle (hamburger button) props
 */
export interface NavbarToggleProps {
  /**
   * Whether the menu is open
   */
  isOpen: boolean;
  /**
   * Toggle callback
   */
  onToggle: () => void;
  /**
   * Additional CSS class
   */
  className?: string;
}

/**
 * Navbar Collapse (collapsible container) props
 */
export interface NavbarCollapseProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Content to show in collapse
   */
  children: ReactNode;
  /**
   * Whether the collapse is open (controlled by Toggle)
   */
  isOpen: boolean;
}

/**
 * Navbar Component Styles
 *
 * Application navigation header with responsive mobile menu.
 */

/* Container */
.navbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--spacing-4);
  width: 100%;
  padding: 0 var(--spacing-6);
  z-index: var(--z-sticky);
  position: relative;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.solid {
  background: var(--surface-base);
}

.transparent {
  background: transparent;
}

.glass {
  background: color-mix(in srgb, var(--surface-base) 70%, transparent);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border-bottom: 1px solid
    color-mix(in srgb, var(--border-subtle) 50%, transparent);
}

/* Glass with light scheme - for dark/colored backgrounds */
.glass.schemeLight {
  background: var(--overlay-light-subtle);
  border-bottom-color: var(--overlay-light-subtle);
}

/* Glass with dark scheme - for light backgrounds */
.glass.schemeDark {
  background: var(--overlay-light);
  border-bottom-color: var(--overlay-dark-subtle);
}

/* ============================================================================
 * COLOR SCHEMES (for transparent/glass on colored backgrounds)
 * ============================================================================ */

/* Light color scheme - white/light text for dark backgrounds */
.schemeLight .brand {
  color: var(--color-neutral-0);
}

.schemeLight .link {
  color: var(--color-neutral-100);
}

.schemeLight .link:hover {
  color: var(--color-neutral-0);
  background: var(--overlay-light-subtle);
}

.schemeLight .link.active {
  color: var(--color-neutral-0);
  background: var(--overlay-light);
}

.schemeLight .toggleLine {
  background: var(--color-neutral-0);
}

/* Dark color scheme - dark text for light backgrounds */
.schemeDark .brand {
  color: var(--text-primary);
}

.schemeDark .link {
  color: var(--text-secondary);
}

.schemeDark .link:hover {
  color: var(--text-primary);
  background: var(--overlay-dark-subtle);
}

.schemeDark .link.active {
  color: var(--text-brand);
  background: var(--soft-brand);
}

/* ============================================================================
 * BORDERS
 * ============================================================================ */

.bordered {
  border-bottom: 1px solid var(--border-subtle);
}

/* ============================================================================
 * STICKY
 * ============================================================================ */

.sticky {
  position: sticky;
  top: 0;
}

/* ============================================================================
 * HEIGHTS
 * ============================================================================ */

.sm {
  height: 56px;
}

.md {
  height: 64px;
}

.lg {
  height: 72px;
}

/* ============================================================================
 * BRAND
 * ============================================================================ */

.brand {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  flex-shrink: 0;
  font-family: var(--font-secondary);
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-bold);
  color: var(--text-primary);
  text-decoration: none;
  transition: opacity var(--transition-fast);
}

.brand:hover {
  opacity: 0.8;
}

.brand:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
  border-radius: var(--radius-sm);
}

/* ============================================================================
 * NAV (Links Container) - Desktop
 * ============================================================================ */

.nav {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  flex: 1;
  justify-content: center;
}

/* ============================================================================
 * LINK
 * ============================================================================ */

.link {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-3);
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-medium);
  color: var(--text-secondary);
  text-decoration: none;
  white-space: nowrap;
  border-radius: var(--radius-button);
  transition: all var(--transition-fast);
}

.link:hover {
  color: var(--text-primary);
  background: var(--surface-subtle);
}

.link:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
}

.link.active {
  color: var(--text-brand);
  background: var(--soft-brand);
}

/* ============================================================================
 * ACTIONS
 * ============================================================================ */

.actions {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  flex-shrink: 0;
}

/* ============================================================================
 * TOGGLE (Hamburger Button) - Mobile Only
 * ============================================================================ */

.toggle {
  display: none;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-button);
  cursor: pointer;
  transition: background var(--transition-fast);
}

.toggle:hover {
  background: var(--surface-subtle);
}

.toggle:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
}

.toggleIcon {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 5px;
  width: 20px;
  height: 20px;
}

.toggleLine {
  width: 20px;
  height: 2px;
  background: var(--text-primary);
  border-radius: 1px;
  transition: all var(--transition-fast);
}

/* Hamburger to X animation */
.toggleOpen .toggleLine:nth-child(1) {
  transform: translateY(7px) rotate(45deg);
}

.toggleOpen .toggleLine:nth-child(2) {
  opacity: 0;
  transform: scaleX(0);
}

.toggleOpen .toggleLine:nth-child(3) {
  transform: translateY(-7px) rotate(-45deg);
}

/* ============================================================================
 * COLLAPSE (Mobile Menu Container)
 * ============================================================================ */

.collapse {
  display: none;
}

.collapseOpen {
  display: flex;
  flex-direction: column;
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background: var(--surface-base);
  border-bottom: 1px solid var(--border-subtle);
  padding: var(--spacing-4);
  gap: var(--spacing-2);
  animation: slideDown var(--transition-normal) ease-out;
  box-shadow: var(--shadow-lg);
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Mobile nav links in collapse */
.collapse .link,
.collapseOpen .link {
  width: 100%;
  padding: var(--spacing-3) var(--spacing-4);
  font-size: var(--font-size-16);
  border-radius: var(--radius-control);
}

/* Mobile actions in collapse */
.collapseActions {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
  padding-top: var(--spacing-3);
  margin-top: var(--spacing-2);
  border-top: 1px solid var(--border-subtle);
}

.collapseActions > * {
  width: 100%;
}

/* ============================================================================
 * RESPONSIVE - Tablet and below
 * ============================================================================ */

@media (max-width: 768px) {
  .navbar {
    padding: 0 var(--spacing-4);
  }

  /* Hide desktop nav and actions */
  .nav {
    display: none;
  }

  .actions {
    display: none;
  }

  /* Show toggle button */
  .toggle {
    display: flex;
  }
}

/* ============================================================================
 * RESPONSIVE - Large screens
 * ============================================================================ */

@media (min-width: 769px) {
  /* Hide mobile elements on desktop */
  .toggle {
    display: none !important;
  }

  .collapse,
  .collapseOpen {
    display: none !important;
  }
}
