/**
 * Pagination Component
 *
 * Navigation control for paginated content.
 *
 * @example
 * ```tsx
 * <Pagination
 *   currentPage={currentPage}
 *   totalPages={totalPages}
 *   onPageChange={setCurrentPage}
 * />
 * ```
 */

import { forwardRef, useMemo } from "react";
import type { PaginationProps } from "./Pagination.types";
import styles from "./Pagination.module.css";

/**
 * Generate array of page numbers to display
 */
const generatePages = (
  currentPage: number,
  totalPages: number,
  siblingCount: number,
): (number | "ellipsis")[] => {
  const totalNumbers = siblingCount * 2 + 5; // siblings + first + last + current + 2 ellipses

  // If we can show all pages
  if (totalPages <= totalNumbers) {
    return Array.from({ length: totalPages }, (_, i) => i + 1);
  }

  const leftSiblingIndex = Math.max(currentPage - siblingCount, 1);
  const rightSiblingIndex = Math.min(currentPage + siblingCount, totalPages);

  const showLeftEllipsis = leftSiblingIndex > 2;
  const showRightEllipsis = rightSiblingIndex < totalPages - 1;

  const pages: (number | "ellipsis")[] = [];

  // First page
  pages.push(1);

  // Left ellipsis
  if (showLeftEllipsis) {
    pages.push("ellipsis");
  } else if (leftSiblingIndex === 2) {
    pages.push(2);
  }

  // Middle pages
  for (let i = leftSiblingIndex; i <= rightSiblingIndex; i++) {
    if (i !== 1 && i !== totalPages) {
      pages.push(i);
    }
  }

  // Right ellipsis
  if (showRightEllipsis) {
    pages.push("ellipsis");
  } else if (rightSiblingIndex === totalPages - 1) {
    pages.push(totalPages - 1);
  }

  // Last page
  if (totalPages > 1) {
    pages.push(totalPages);
  }

  return pages;
};

export const Pagination = forwardRef<HTMLElement, PaginationProps>(
  (
    {
      currentPage,
      totalPages,
      onPageChange,
      siblingCount = 1,
      showFirstLast = true,
      showPrevNext = true,
      size = "md",
      disabled = false,
      className,
      ...rest
    },
    ref,
  ) => {
    const pages = useMemo(
      () => generatePages(currentPage, totalPages, siblingCount),
      [currentPage, totalPages, siblingCount],
    );

    const canGoPrev = currentPage > 1;
    const canGoNext = currentPage < totalPages;

    const handlePageChange = (page: number) => {
      if (
        !disabled &&
        page >= 1 &&
        page <= totalPages &&
        page !== currentPage
      ) {
        onPageChange(page);
      }
    };

    const classNames = [
      styles.pagination,
      styles[size],
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(" ");

    if (totalPages <= 0) {
      return null;
    }

    return (
      <nav ref={ref} className={classNames} aria-label="Pagination" {...rest}>
        <ul className={styles.list}>
          {/* First page button */}
          {showFirstLast && (
            <li>
              <button
                type="button"
                className={styles.button}
                onClick={() => handlePageChange(1)}
                disabled={!canGoPrev || disabled}
                aria-label="Go to first page"
              >
                <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                  <path
                    d="M11 12L7 8l4-4M5 12V4"
                    stroke="currentColor"
                    strokeWidth="1.5"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
            </li>
          )}

          {/* Previous button */}
          {showPrevNext && (
            <li>
              <button
                type="button"
                className={styles.button}
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={!canGoPrev || disabled}
                aria-label="Go to previous page"
              >
                <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                  <path
                    d="M10 12L6 8l4-4"
                    stroke="currentColor"
                    strokeWidth="1.5"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
            </li>
          )}

          {/* Page numbers */}
          {pages.map((page, index) => (
            <li key={page === "ellipsis" ? `ellipsis-${index}` : page}>
              {page === "ellipsis" ? (
                <span className={styles.ellipsis} aria-hidden="true">
                  â€¦
                </span>
              ) : (
                <button
                  type="button"
                  className={`${styles.button} ${styles.pageButton} ${
                    page === currentPage ? styles.active : ""
                  }`}
                  onClick={() => handlePageChange(page)}
                  disabled={disabled}
                  aria-label={`Go to page ${page}`}
                  aria-current={page === currentPage ? "page" : undefined}
                >
                  {page}
                </button>
              )}
            </li>
          ))}

          {/* Next button */}
          {showPrevNext && (
            <li>
              <button
                type="button"
                className={styles.button}
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={!canGoNext || disabled}
                aria-label="Go to next page"
              >
                <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                  <path
                    d="M6 12l4-4-4-4"
                    stroke="currentColor"
                    strokeWidth="1.5"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
            </li>
          )}

          {/* Last page button */}
          {showFirstLast && (
            <li>
              <button
                type="button"
                className={styles.button}
                onClick={() => handlePageChange(totalPages)}
                disabled={!canGoNext || disabled}
                aria-label="Go to last page"
              >
                <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
                  <path
                    d="M5 12l4-4-4-4M11 12V4"
                    stroke="currentColor"
                    strokeWidth="1.5"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
            </li>
          )}
        </ul>
      </nav>
    );
  },
);

Pagination.displayName = "Pagination";

/**
 * Pagination Component Types
 *
 * Type definitions for the Orion Pagination component.
 */

import type { HTMLAttributes } from "react";

/**
 * Pagination sizes
 */
export type PaginationSize = "sm" | "md" | "lg";

/**
 * Pagination component props
 *
 * @example
 * ```tsx
 * <Pagination
 *   currentPage={1}
 *   totalPages={10}
 *   onPageChange={(page) => setPage(page)}
 * />
 * ```
 */
export interface PaginationProps extends Omit<
  HTMLAttributes<HTMLElement>,
  "onChange"
> {
  /**
   * Current active page (1-indexed)
   */
  currentPage: number;

  /**
   * Total number of pages
   */
  totalPages: number;

  /**
   * Callback when page changes
   */
  onPageChange: (page: number) => void;

  /**
   * Number of sibling pages to show on each side of current page
   * @default 1
   */
  siblingCount?: number;

  /**
   * Whether to show first/last page buttons
   * @default true
   */
  showFirstLast?: boolean;

  /**
   * Whether to show previous/next buttons
   * @default true
   */
  showPrevNext?: boolean;

  /**
   * Size variant
   * @default 'md'
   */
  size?: PaginationSize;

  /**
   * Whether the pagination is disabled
   * @default false
   */
  disabled?: boolean;
}

/**
 * Pagination Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

.pagination {
  display: inline-flex;
  align-items: center;
}

.list {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  margin: 0;
  padding: 0;
  list-style: none;
}

/* ============================================================================
 * BUTTONS
 * ============================================================================ */

.button {
  display: flex;
  align-items: center;
  justify-content: center;
  background: transparent;
  border: 1px solid transparent;
  border-radius: var(--radius-button);
  color: var(--text-secondary);
  cursor: pointer;
  transition:
    background-color var(--transition-fast),
    color var(--transition-fast),
    border-color var(--transition-fast);

  &:hover:not(:disabled) {
    background: var(--surface-subtle);
    color: var(--text-primary);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }

  &:disabled {
    opacity: 0.4;
    cursor: not-allowed;
  }
}

.pageButton {
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
}

.active {
  background: var(--interactive-primary);
  color: var(--interactive-primary-text);
  border-color: var(--interactive-primary);

  &:hover:not(:disabled) {
    background: var(--interactive-primary-hover);
    color: var(--interactive-primary-text);
  }
}

/* ============================================================================
 * ELLIPSIS
 * ============================================================================ */

.ellipsis {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--text-tertiary);
  font-family: var(--font-secondary);
  user-select: none;
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm {
  .button {
    min-width: 28px;
    height: 28px;
    font-size: var(--font-size-12);
  }

  .ellipsis {
    min-width: 28px;
    height: 28px;
    font-size: var(--font-size-12);
  }
}

.md {
  .button {
    min-width: 36px;
    height: 36px;
    font-size: var(--font-size-14);
  }

  .ellipsis {
    min-width: 36px;
    height: 36px;
    font-size: var(--font-size-14);
  }
}

.lg {
  .button {
    min-width: 44px;
    height: 44px;
    font-size: var(--font-size-16);
  }

  .ellipsis {
    min-width: 44px;
    height: 44px;
    font-size: var(--font-size-16);
  }

  .list {
    gap: var(--spacing-2);
  }
}

/* ============================================================================
 * DISABLED STATE
 * ============================================================================ */

.disabled {
  pointer-events: none;
  opacity: 0.5;
}

/**
 * Pagination Component - Page navigation controls.
 *
 * @example
 * ```tsx
 * import { Pagination } from '@orion-ds/react';
 *
 * <Pagination
 *   total={100}
 *   current={1}
 *   pageSize={10}
 *   onPageChange={(page) => setCurrentPage(page)}
 * />
 * ```
 */
export { Pagination } from "./Pagination";
export type { PaginationProps, PaginationSize } from "./Pagination.types";
