/**
 * ProgressBar Component
 *
 * Progress indicator with variants, sizes, and labels.
 *
 * @example
 * ```tsx
 * <ProgressBar value={65} />
 * <ProgressBar value={80} variant="success" showLabel />
 * <ProgressBar value={25} size="lg" label="Uploading files..." />
 * <ProgressBar indeterminate label="Loading..." />
 * ```
 */

import React from "react";
import type { ProgressBarProps } from "./ProgressBar.types";
import styles from "./ProgressBar.module.css";

export const ProgressBar: React.FC<ProgressBarProps> = ({
  value,
  max = 100,
  variant = "primary",
  size = "md",
  showLabel = false,
  label,
  indeterminate = false,
  className,
  ...rest
}) => {
  // Calculate percentage
  const percentage =
    value !== undefined ? Math.min(Math.max((value / max) * 100, 0), 100) : 0;

  const containerClasses = [
    styles.container,
    styles[size],
    styles[variant],
    indeterminate && styles.indeterminate,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  return (
    <div className={containerClasses} {...rest}>
      {/* Label and percentage */}
      {(showLabel || label) && (
        <div className={styles.labelWrapper}>
          {label && <span>{label}</span>}
          {showLabel && !indeterminate && (
            <span className={styles.percentage}>{Math.round(percentage)}%</span>
          )}
        </div>
      )}

      {/* Progress track */}
      <div
        className={styles.track}
        role="progressbar"
        aria-valuenow={indeterminate ? undefined : value}
        aria-valuemin={0}
        aria-valuemax={max}
        aria-label={label || `Progress: ${Math.round(percentage)}%`}
      >
        {/* Progress bar */}
        <div
          className={styles.bar}
          style={{
            width: indeterminate ? undefined : `${percentage}%`,
          }}
        />
      </div>
    </div>
  );
};

ProgressBar.displayName = "ProgressBar";

/**
 * ProgressBar Component Types
 *
 * Type definitions for the Orion ProgressBar component.
 */

import type { HTMLAttributes } from "react";

/**
 * ProgressBar sizes
 */
export type ProgressBarSize = "sm" | "md" | "lg";

/**
 * ProgressBar variants
 */
export type ProgressBarVariant =
  | "primary"
  | "success"
  | "warning"
  | "error"
  | "info";

/**
 * ProgressBar component props
 *
 * @example
 * ```tsx
 * <ProgressBar value={65} />
 * <ProgressBar value={80} variant="success" showLabel />
 * <ProgressBar value={25} size="lg" label="Uploading..." />
 * ```
 */
export interface ProgressBarProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Progress value (0-100)
   * Optional when indeterminate is true
   */
  value?: number;

  /**
   * Maximum value
   * @default 100
   */
  max?: number;

  /**
   * Progress bar variant (color)
   * @default 'primary'
   */
  variant?: ProgressBarVariant;

  /**
   * Progress bar size
   * @default 'md'
   */
  size?: ProgressBarSize;

  /**
   * Show percentage label
   * @default false
   */
  showLabel?: boolean;

  /**
   * Custom label text
   */
  label?: string;

  /**
   * Indeterminate/loading state
   * @default false
   */
  indeterminate?: boolean;
}

/**
 * ProgressBar Component Styles
 *
 * Progress indicator with variants and sizes.
 */

/* Container */
.container {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
  width: 100%;
}

/* Label */
.labelWrapper {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: var(--font-size-12);
  color: var(--text-secondary);
  font-weight: var(--font-weight-medium);
}

.percentage {
  font-variant-numeric: tabular-nums;
  color: var(--text-primary);
}

/* Track (background) */
.track {
  position: relative;
  width: 100%;
  background: var(--surface-layer);
  border-radius: var(--radius-full);
  overflow: hidden;
}

/* Bar (foreground) */
.bar {
  height: 100%;
  border-radius: var(--radius-full);
  transition: width var(--transition-slow);
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm .track {
  height: 4px;
}

.md .track {
  height: 8px;
}

.lg .track {
  height: 12px;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.primary .bar {
  background: var(--interactive-primary);
}

.success .bar {
  background: var(--status-success);
}

.warning .bar {
  background: var(--status-warning);
}

.error .bar {
  background: var(--status-error);
}

.info .bar {
  background: var(--status-info);
}

/* ============================================================================
 * INDETERMINATE (LOADING) STATE
 * ============================================================================ */

.indeterminate .bar {
  width: 30% !important;
  animation: indeterminate 1.5s ease-in-out infinite;
}

@keyframes indeterminate {
  0% {
    transform: translateX(-100%);
  }
  50% {
    transform: translateX(350%);
  }
  100% {
    transform: translateX(-100%);
  }
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  .bar {
    transition: none;
  }

  .indeterminate .bar {
    animation: none;
    width: 100% !important;
  }
}

/**
 * ProgressBar Component - Visual progress indicator.
 *
 * @example
 * ```tsx
 * import { ProgressBar } from '@orion-ds/react';
 *
 * <ProgressBar value={75} max={100} />
 * <ProgressBar value={50} variant="success" showLabel />
 * <ProgressBar value={30} size="sm" variant="brand" />
 * ```
 */
export { ProgressBar } from "./ProgressBar";
export type {
  ProgressBarProps,
  ProgressBarSize,
  ProgressBarVariant,
} from "./ProgressBar.types";
