/**
 * Radio Component
 *
 * Single-select radio input with label and validation.
 * Implements WCAG 2.1 AA accessibility guidelines.
 *
 * @example
 * ```tsx
 * <Radio name="plan" value="free" label="Free Plan" />
 * <Radio name="plan" value="pro" label="Pro Plan" checked />
 * <Radio name="plan" value="enterprise" label="Enterprise" />
 *
 * <Radio
 *   name="size"
 *   value="small"
 *   label="Small"
 *   helperText="Recommended for personal use"
 * />
 * ```
 */

import { forwardRef, useId } from 'react';
import { AlertCircle } from 'lucide-react';
import type { RadioProps } from './Radio.types';
import styles from './Radio.module.css';

export const Radio = forwardRef<HTMLInputElement, RadioProps>(
  (
    {
      label,
      helperText,
      error,
      size = 'md',
      className,
      id,
      disabled,
      required,
      'aria-label': ariaLabel,
      'aria-describedby': ariaDescribedBy,
      ...rest
    },
    ref,
  ) => {
    // Use React's useId for stable, unique IDs (SSR-safe)
    const generatedId = useId();
    const radioId = id || `radio-${generatedId}`;

    // IDs for accessible associations
    const errorId = `${radioId}-error`;
    const helperId = `${radioId}-helper`;

    // Build aria-describedby from multiple sources
    const describedByIds: string[] = [];
    if (error) describedByIds.push(errorId);
    if (helperText && !error) describedByIds.push(helperId);
    if (ariaDescribedBy) describedByIds.push(ariaDescribedBy);
    const computedDescribedBy = describedByIds.length > 0 ? describedByIds.join(' ') : undefined;

    // Ensure radio has an accessible name
    const hasAccessibleName = Boolean(label || ariaLabel);
    if (!hasAccessibleName && process.env.NODE_ENV === 'development') {
      console.warn(
        'Radio: Missing accessible name. Provide either a `label` prop or `aria-label` for screen reader users.',
      );
    }

    const containerClasses = [
      styles.container,
      styles[size],
      error && styles.error,
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={containerClasses}>
        {/* Hidden input */}
        <input
          ref={ref}
          type="radio"
          id={radioId}
          className={styles.input}
          disabled={disabled}
          required={required}
          aria-required={required || undefined}
          aria-invalid={error ? 'true' : 'false'}
          aria-describedby={computedDescribedBy}
          aria-label={!label ? ariaLabel : undefined}
          {...rest}
        />

        {/* Label wrapper (clickable area) */}
        <label htmlFor={radioId} className={styles.labelWrapper}>
          {/* Custom radio indicator */}
          <span className={styles.radio} aria-hidden="true">
            <span className={styles.dot} />
          </span>

          {/* Label text */}
          {label && (
            <span className={styles.label}>
              {label}
              {required && (
                <span className={styles.required} aria-hidden="true">
                  *
                </span>
              )}
            </span>
          )}
        </label>

        {/* Helper text */}
        {helperText && !error && (
          <p id={helperId} className={styles.helperText}>
            {helperText}
          </p>
        )}

        {/* Error message */}
        {error && (
          <p id={errorId} className={styles.errorMessage} role="alert" aria-live="assertive">
            <AlertCircle size={14} aria-hidden="true" />
            <span>{error}</span>
          </p>
        )}
      </div>
    );
  },
);

Radio.displayName = 'Radio';

/**
 * Radio Component Types
 *
 * Type definitions for the Orion Radio component.
 * Implements WCAG 2.1 AA accessibility guidelines.
 */

import type { InputHTMLAttributes } from 'react';

/**
 * Radio sizes
 */
export type RadioSize = 'sm' | 'md' | 'lg';

/**
 * Radio component props
 *
 * @example
 * ```tsx
 * <Radio name="plan" value="free" label="Free Plan" />
 * <Radio name="plan" value="pro" label="Pro Plan" checked />
 * ```
 */
export interface RadioProps extends Omit<InputHTMLAttributes<HTMLInputElement>, 'size' | 'type'> {
  /**
   * Radio label text.
   * Required for accessibility unless aria-label is provided.
   */
  label?: string;

  /**
   * Helper text displayed below the radio.
   * Automatically linked via aria-describedby.
   */
  helperText?: string;

  /**
   * Error message.
   * Shows error state and displays message below radio.
   */
  error?: string;

  /**
   * Radio size variant.
   * @default 'md'
   */
  size?: RadioSize;

  // ============================================================================
  // ACCESSIBILITY PROPS
  // ============================================================================

  /**
   * Accessible label for screen readers when no visible label is provided.
   * REQUIRED if `label` prop is not provided.
   *
   * @example
   * ```tsx
   * <Radio
   *   name="color"
   *   value="red"
   *   aria-label="Red color"
   *   style={{ background: 'red' }}
   * />
   * ```
   */
  'aria-label'?: string;

  /**
   * Additional element IDs that describe this radio.
   * Automatically includes error and helper text IDs when provided.
   */
  'aria-describedby'?: string;
}

/**
 * Radio Component Styles
 *
 * Single-select radio input.
 * Implements WCAG 2.1 AA accessibility guidelines.
 */

/* ============================================================================
 * CONTAINER
 * ============================================================================ */

.container {
  display: inline-flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.disabled {
  opacity: 0.5;
}

/* ============================================================================
 * LABEL WRAPPER (clickable area)
 * ============================================================================ */

.labelWrapper {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-3);
  cursor: pointer;
  user-select: none;
}

.disabled .labelWrapper {
  cursor: not-allowed;
  pointer-events: none;
}

/* ============================================================================
 * HIDDEN INPUT
 * ============================================================================ */

.input {
  position: absolute;
  opacity: 0;
  width: 0;
  height: 0;
  pointer-events: none;
}

/* ============================================================================
 * CUSTOM RADIO INDICATOR
 * ============================================================================ */

.radio {
  position: relative;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  /* Default size (md) */
  width: 20px;
  height: 20px;

  /* Styling */
  background: var(--surface-base);
  border: 2px solid var(--border-strong);
  border-radius: 50%;

  /* Interaction */
  transition: all var(--transition-fast);
}

/* Radio dot (inner circle) */
.dot {
  width: 10px;
  height: 10px;
  background: var(--interactive-primary-text);
  border-radius: 50%;
  opacity: 0;
  transform: scale(0);
  transition: all var(--transition-fast);
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm .radio {
  width: 16px;
  height: 16px;
}

.sm .dot {
  width: 8px;
  height: 8px;
}

.md .radio {
  width: 20px;
  height: 20px;
}

.md .dot {
  width: 10px;
  height: 10px;
}

.lg .radio {
  width: 24px;
  height: 24px;
}

.lg .dot {
  width: 12px;
  height: 12px;
}

/* ============================================================================
 * CHECKED STATE
 * ============================================================================ */

.input:checked + .labelWrapper .radio {
  background: var(--interactive-primary);
  border-color: var(--interactive-primary);
}

.input:checked + .labelWrapper .dot {
  opacity: 1;
  transform: scale(1);
}

/* ============================================================================
 * HOVER STATE
 * ============================================================================ */

.labelWrapper:hover .radio {
  border-color: var(--interactive-primary);
}

.input:checked + .labelWrapper:hover .radio {
  background: var(--interactive-primary-hover);
  border-color: var(--interactive-primary-hover);
}

/* ============================================================================
 * FOCUS STATE
 * ============================================================================ */

.input:focus-visible + .labelWrapper .radio {
  outline: 2px solid var(--interactive-primary);
  outline-offset: 2px;
  box-shadow: 0 0 0 4px var(--soft-brand);
}

/* ============================================================================
 * DISABLED STATE
 * ============================================================================ */

.input:disabled + .labelWrapper {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}

.input:disabled + .labelWrapper .radio {
  background: var(--surface-subtle);
  border-color: var(--border-subtle);
}

.input:disabled:checked + .labelWrapper .radio {
  background: var(--text-tertiary);
  border-color: var(--text-tertiary);
}

/* ============================================================================
 * ERROR STATE
 * ============================================================================ */

.error .radio {
  border-color: var(--status-error);
}

.error .labelWrapper:hover .radio {
  border-color: var(--status-error);
}

.error .input:focus-visible + .labelWrapper .radio {
  outline-color: var(--status-error);
  box-shadow: 0 0 0 4px var(--soft-error);
}

/* ============================================================================
 * LABEL & TEXT
 * ============================================================================ */

.label {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-normal);
  color: var(--text-primary);
  line-height: 1.4;
}

.sm .label {
  font-size: var(--font-size-13);
}

.lg .label {
  font-size: var(--font-size-16);
}

.required {
  color: var(--status-error);
  margin-left: 2px;
}

/* Helper text - aligned with label */
.helperText {
  margin: 0;
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  color: var(--text-secondary);
  line-height: var(--line-height-snug);
  padding-left: calc(20px + var(--spacing-3)); /* radio width + gap */
}

.sm .helperText {
  padding-left: calc(16px + var(--spacing-3));
}

.lg .helperText {
  padding-left: calc(24px + var(--spacing-3));
}

/* Error message */
.errorMessage {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  margin: 0;
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  color: var(--status-error);
  line-height: var(--line-height-snug);
  padding-left: calc(20px + var(--spacing-3));
}

.sm .errorMessage {
  padding-left: calc(16px + var(--spacing-3));
}

.lg .errorMessage {
  padding-left: calc(24px + var(--spacing-3));
}

/* ============================================================================
 * HIGH CONTRAST MODE SUPPORT
 * ============================================================================ */

@media (forced-colors: active) {
  .radio {
    border: 2px solid CanvasText;
    background: Canvas;
  }

  .input:checked + .labelWrapper .radio {
    background: Highlight;
    border-color: Highlight;
  }

  .dot {
    background: Canvas;
  }

  .input:focus-visible + .labelWrapper .radio {
    outline: 3px solid Highlight;
    outline-offset: 2px;
  }

  .input:disabled + .labelWrapper .radio {
    border-color: GrayText;
  }

  .error .radio {
    border-color: LinkText;
  }

  .errorMessage {
    color: LinkText;
  }

  .label {
    color: CanvasText;
  }
}

/* ============================================================================
 * REDUCED MOTION
 * ============================================================================ */

@media (prefers-reduced-motion: reduce) {
  .radio,
  .dot {
    transition: none;
  }
}
