/**
 * Skeleton Component
 *
 * A loading placeholder that mimics content structure.
 *
 * @example
 * ```tsx
 * <Skeleton width={200} height={20} />
 * <Skeleton variant="circular" width={40} height={40} />
 * <Skeleton variant="text" lines={3} />
 * ```
 */

import { forwardRef } from 'react';
import type { SkeletonProps } from './Skeleton.types';
import styles from './Skeleton.module.css';

export const Skeleton = forwardRef<HTMLDivElement, SkeletonProps>(
  (
    {
      variant = 'text',
      animation = 'pulse',
      width,
      height,
      lines = 1,
      borderRadius,
      className,
      style,
      ...rest
    },
    ref,
  ) => {
    const classNames = [styles.skeleton, styles[variant], styles[animation], className]
      .filter(Boolean)
      .join(' ');

    const inlineStyle = {
      width: typeof width === 'number' ? `${width}px` : width,
      height: typeof height === 'number' ? `${height}px` : height,
      borderRadius: typeof borderRadius === 'number' ? `${borderRadius}px` : borderRadius,
      ...style,
    };

    // For text variant with multiple lines
    if (variant === 'text' && lines > 1) {
      return (
        <div ref={ref} className={styles.lines} {...rest}>
          {Array.from({ length: lines }).map((_, index) => (
            <div
              key={index}
              className={classNames}
              style={{
                ...inlineStyle,
                // Last line is shorter for visual variety
                width: index === lines - 1 ? '80%' : inlineStyle.width,
              }}
              aria-hidden="true"
            />
          ))}
        </div>
      );
    }

    return (
      <div ref={ref} className={classNames} style={inlineStyle} aria-hidden="true" {...rest} />
    );
  },
);

Skeleton.displayName = 'Skeleton';

/**
 * Skeleton Component Types
 *
 * Type definitions for the Orion Skeleton component.
 */

import type { HTMLAttributes } from 'react';

/**
 * Skeleton shape variants
 */
export type SkeletonVariant = 'text' | 'circular' | 'rectangular' | 'rounded';

/**
 * Skeleton animation types
 */
export type SkeletonAnimation = 'pulse' | 'wave' | 'none';

/**
 * Skeleton component props
 *
 * @example
 * ```tsx
 * <Skeleton width={200} height={20} />
 * <Skeleton variant="circular" width={40} height={40} />
 * <Skeleton variant="rectangular" width="100%" height={200} />
 * ```
 */
export interface SkeletonProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Shape variant of the skeleton
   * @default 'text'
   */
  variant?: SkeletonVariant;

  /**
   * Animation type
   * @default 'pulse'
   */
  animation?: SkeletonAnimation;

  /**
   * Width of the skeleton
   * Can be a number (pixels) or string (e.g., '100%', '10rem')
   */
  width?: number | string;

  /**
   * Height of the skeleton
   * Can be a number (pixels) or string (e.g., '100%', '2rem')
   */
  height?: number | string;

  /**
   * Number of skeleton lines to render (for text variant)
   * @default 1
   */
  lines?: number;

  /**
   * Border radius override
   */
  borderRadius?: number | string;
}

/**
 * Skeleton Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Base skeleton styles */
.skeleton {
  background-color: var(--surface-layer);
  display: block;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.text {
  width: 100%;
  height: 1em;
  border-radius: var(--radius-sm);
}

.circular {
  border-radius: 50%;
}

.rectangular {
  border-radius: 0;
}

.rounded {
  border-radius: var(--radius-control);
}

/* ============================================================================
 * ANIMATIONS
 * ============================================================================ */

.pulse {
  animation: pulse 1.5s ease-in-out infinite;
}

.wave {
  position: relative;
  overflow: hidden;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, var(--surface-subtle), transparent);
    animation: wave 1.5s linear infinite;
  }
}

.none {
  animation: none;
}

@keyframes pulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.4;
  }
}

@keyframes wave {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

/* ============================================================================
 * MULTIPLE LINES
 * ============================================================================ */

.lines {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-2);
}

/* ============================================================================
 * REDUCED MOTION
 * ============================================================================ */

@media (prefers-reduced-motion: reduce) {
  .pulse,
  .wave {
    animation: none;
  }

  .wave::after {
    animation: none;
  }
}
