/**
 * Slider Component
 *
 * A range input for selecting numeric values.
 *
 * @example
 * ```tsx
 * <Slider
 *   value={50}
 *   onChange={(value) => setValue(value)}
 *   min={0}
 *   max={100}
 * />
 * ```
 */

import { forwardRef, useCallback, useRef, useId } from 'react';
import type { SliderProps } from './Slider.types';
import styles from './Slider.module.css';

export const Slider = forwardRef<HTMLDivElement, SliderProps>(
  (
    {
      value,
      onChange,
      min = 0,
      max = 100,
      step = 1,
      size = 'md',
      disabled = false,
      showValue = false,
      formatValue,
      showLabels = false,
      showTicks = false,
      tickValues,
      label,
      id: providedId,
      onChangeStart,
      onChangeEnd,
      className,
      ...rest
    },
    ref,
  ) => {
    const generatedId = useId();
    const inputId = providedId || generatedId;
    const isDragging = useRef(false);

    const percentage = ((value - min) / (max - min)) * 100;

    const displayValue = formatValue ? formatValue(value) : value.toString();

    const handleChange = useCallback(
      (e: React.ChangeEvent<HTMLInputElement>) => {
        const newValue = parseFloat(e.target.value);
        onChange(newValue);
      },
      [onChange],
    );

    const handleMouseDown = useCallback(() => {
      isDragging.current = true;
      onChangeStart?.();
    }, [onChangeStart]);

    const handleMouseUp = useCallback(() => {
      if (isDragging.current) {
        isDragging.current = false;
        onChangeEnd?.(value);
      }
    }, [onChangeEnd, value]);

    // Calculate tick marks
    const getTicks = () => {
      if (tickValues) {
        return tickValues;
      }

      const ticks: number[] = [];
      const tickStep = (max - min) / 4; // Default to 5 ticks
      for (let i = min; i <= max; i += tickStep) {
        ticks.push(Math.round(i * 100) / 100);
      }
      return ticks;
    };

    const containerClasses = [
      styles.container,
      styles[size],
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <div ref={ref} className={containerClasses} {...rest}>
        {showValue && (
          <div
            className={styles.valueDisplay}
            style={{ left: `${percentage}%` }}
            aria-hidden="true"
          >
            {displayValue}
          </div>
        )}

        <div className={styles.track}>
          <div className={styles.fill} style={{ width: `${percentage}%` }} aria-hidden="true" />

          <input
            id={inputId}
            type="range"
            className={styles.input}
            value={value}
            onChange={handleChange}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onTouchStart={handleMouseDown}
            onTouchEnd={handleMouseUp}
            min={min}
            max={max}
            step={step}
            disabled={disabled}
            aria-label={label}
            aria-valuemin={min}
            aria-valuemax={max}
            aria-valuenow={value}
            aria-valuetext={displayValue}
          />
        </div>

        {showTicks && (
          <div className={styles.ticks} aria-hidden="true">
            {getTicks().map((tick) => (
              <div
                key={tick}
                className={styles.tick}
                style={{ left: `${((tick - min) / (max - min)) * 100}%` }}
              />
            ))}
          </div>
        )}

        {showLabels && (
          <div className={styles.labels} aria-hidden="true">
            <span className={styles.labelMin}>{formatValue ? formatValue(min) : min}</span>
            <span className={styles.labelMax}>{formatValue ? formatValue(max) : max}</span>
          </div>
        )}
      </div>
    );
  },
);

Slider.displayName = 'Slider';

/**
 * Slider Component Types
 *
 * Type definitions for the Orion Slider/Range component.
 */

import type { HTMLAttributes } from 'react';

/**
 * Slider size variants
 */
export type SliderSize = 'sm' | 'md' | 'lg';

/**
 * Slider component props
 *
 * @example
 * ```tsx
 * <Slider
 *   value={50}
 *   onChange={(value) => setValue(value)}
 *   min={0}
 *   max={100}
 * />
 * ```
 */
export interface SliderProps extends Omit<HTMLAttributes<HTMLDivElement>, 'onChange'> {
  /**
   * Current value
   */
  value: number;

  /**
   * Callback when value changes
   */
  onChange: (value: number) => void;

  /**
   * Minimum value
   * @default 0
   */
  min?: number;

  /**
   * Maximum value
   * @default 100
   */
  max?: number;

  /**
   * Step increment
   * @default 1
   */
  step?: number;

  /**
   * Size variant
   * @default 'md'
   */
  size?: SliderSize;

  /**
   * Whether the slider is disabled
   * @default false
   */
  disabled?: boolean;

  /**
   * Show the current value above the thumb
   * @default false
   */
  showValue?: boolean;

  /**
   * Format function for displaying the value
   */
  formatValue?: (value: number) => string;

  /**
   * Show min and max labels
   * @default false
   */
  showLabels?: boolean;

  /**
   * Show tick marks
   * @default false
   */
  showTicks?: boolean;

  /**
   * Custom tick values (if not provided, uses step)
   */
  tickValues?: number[];

  /**
   * Label for the slider (accessibility)
   */
  label?: string;

  /**
   * ID for the slider input
   */
  id?: string;

  /**
   * Callback when dragging starts
   */
  onChangeStart?: () => void;

  /**
   * Callback when dragging ends
   */
  onChangeEnd?: (value: number) => void;
}

/**
 * Slider Component Styles
 */

/* Component-level tokens for slider sizing */
:root {
  --slider-track-sm: var(--spacing-1);
  --slider-track-md: 6px;
  --slider-track-lg: var(--spacing-2);
  --slider-thumb-sm: 14px;
  --slider-thumb-md: 18px;
  --slider-thumb-lg: 24px;
  --slider-tooltip-arrow: var(--spacing-1);
}

.container {
  position: relative;
  width: 100%;
  padding-top: var(--spacing-4);
  padding-bottom: var(--spacing-4);
}

/* Size variants */
.sm .track {
  height: var(--slider-track-sm);
}

.sm .input::-webkit-slider-thumb {
  width: var(--slider-thumb-sm);
  height: var(--slider-thumb-sm);
}

.sm .input::-moz-range-thumb {
  width: var(--slider-thumb-sm);
  height: var(--slider-thumb-sm);
}

.md .track {
  height: var(--slider-track-md);
}

.md .input::-webkit-slider-thumb {
  width: var(--slider-thumb-md);
  height: var(--slider-thumb-md);
}

.md .input::-moz-range-thumb {
  width: var(--slider-thumb-md);
  height: var(--slider-thumb-md);
}

.lg .track {
  height: var(--slider-track-lg);
}

.lg .input::-webkit-slider-thumb {
  width: var(--slider-thumb-lg);
  height: var(--slider-thumb-lg);
}

.lg .input::-moz-range-thumb {
  width: var(--slider-thumb-lg);
  height: var(--slider-thumb-lg);
}

/* Track */
.track {
  position: relative;
  width: 100%;
  background: var(--surface-layer);
  border-radius: 9999px;
  overflow: visible;
}

/* Fill (progress) */
.fill {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  background: var(--interactive-primary);
  border-radius: 9999px;
  pointer-events: none;
}

/* Native input */
.input {
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
  background: transparent;
  cursor: pointer;
  appearance: none;
  -webkit-appearance: none;
}

.input:focus {
  outline: none;
}

/* Webkit thumb */
.input::-webkit-slider-thumb {
  appearance: none;
  -webkit-appearance: none;
  background: var(--interactive-primary);
  border: 2px solid var(--surface-base);
  border-radius: 50%;
  box-shadow: var(--shadow-sm);
  cursor: pointer;
  transition:
    transform var(--duration-fast) var(--ease-out),
    box-shadow var(--duration-fast) var(--ease-out);
}

.input::-webkit-slider-thumb:hover {
  transform: scale(1.1);
  box-shadow: var(--shadow-md);
}

.input:focus-visible::-webkit-slider-thumb {
  box-shadow: 0 0 0 3px var(--interactive-primary-subtle);
}

.input:active::-webkit-slider-thumb {
  transform: scale(0.95);
}

/* Firefox thumb */
.input::-moz-range-thumb {
  background: var(--interactive-primary);
  border: 2px solid var(--surface-base);
  border-radius: 50%;
  box-shadow: var(--shadow-sm);
  cursor: pointer;
  transition:
    transform var(--duration-fast) var(--ease-out),
    box-shadow var(--duration-fast) var(--ease-out);
}

.input::-moz-range-thumb:hover {
  transform: scale(1.1);
  box-shadow: var(--shadow-md);
}

.input:focus-visible::-moz-range-thumb {
  box-shadow: 0 0 0 3px var(--interactive-primary-subtle);
}

.input:active::-moz-range-thumb {
  transform: scale(0.95);
}

/* Firefox track */
.input::-moz-range-track {
  background: transparent;
  border: none;
}

/* Value display */
.valueDisplay {
  position: absolute;
  top: 0;
  transform: translateX(-50%);
  padding: var(--spacing-1) var(--spacing-2);
  background: var(--surface-inverse);
  color: var(--text-inverse);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
  border-radius: var(--radius-sm);
  white-space: nowrap;
}

.valueDisplay::after {
  content: '';
  position: absolute;
  bottom: calc(var(--slider-tooltip-arrow) * -1);
  left: 50%;
  transform: translateX(-50%);
  border-left: var(--slider-tooltip-arrow) solid transparent;
  border-right: var(--slider-tooltip-arrow) solid transparent;
  border-top: var(--slider-tooltip-arrow) solid var(--surface-inverse);
}

/* Ticks */
.ticks {
  position: relative;
  height: var(--spacing-2);
  margin-top: var(--spacing-2);
}

.tick {
  position: absolute;
  width: 1px;
  height: 100%;
  background: var(--border-subtle);
  transform: translateX(-50%);
}

/* Labels */
.labels {
  display: flex;
  justify-content: space-between;
  margin-top: var(--spacing-2);
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
}

.labelMin,
.labelMax {
  flex-shrink: 0;
}

/* Disabled state */
.disabled {
  opacity: 0.5;
  pointer-events: none;
}

.disabled .fill {
  background: var(--text-tertiary);
}

.disabled .input::-webkit-slider-thumb {
  background: var(--text-tertiary);
  cursor: not-allowed;
}

.disabled .input::-moz-range-thumb {
  background: var(--text-tertiary);
  cursor: not-allowed;
}
