/**
 * Spinner Component
 *
 * Loading indicator with multiple sizes and variants.
 *
 * @example
 * ```tsx
 * <Spinner />
 * <Spinner size="lg" variant="primary" />
 * <Spinner label="Loading data..." showLabel />
 * ```
 */

import React from 'react';
import type { SpinnerProps } from './Spinner.types';
import styles from './Spinner.module.css';

export const Spinner: React.FC<SpinnerProps> = ({
  size = 'md',
  variant = 'primary',
  label = 'Loading...',
  showLabel = false,
  className,
  ...rest
}) => {
  const containerClasses = [styles.container, className].filter(Boolean).join(' ');

  const spinnerClasses = [styles.spinner, styles[size], styles[variant]].filter(Boolean).join(' ');

  return (
    <div className={containerClasses} {...rest}>
      <div className={spinnerClasses} role="status" aria-label={label} aria-live="polite" />
      {showLabel && <span className={styles.label}>{label}</span>}
    </div>
  );
};

Spinner.displayName = 'Spinner';

/**
 * Spinner Component Types
 *
 * Type definitions for the Orion Spinner component (loading indicator).
 */

import type { HTMLAttributes } from 'react';

/**
 * Spinner sizes
 */
export type SpinnerSize = 'xs' | 'sm' | 'md' | 'lg' | 'xl';

/**
 * Spinner variants
 */
export type SpinnerVariant = 'primary' | 'secondary' | 'neutral' | 'inverse';

/**
 * Spinner component props
 *
 * @example
 * ```tsx
 * <Spinner />
 * <Spinner size="lg" variant="primary" />
 * <Spinner label="Loading..." />
 * ```
 */
export interface SpinnerProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Spinner size
   * @default 'md'
   */
  size?: SpinnerSize;

  /**
   * Spinner variant (color)
   * @default 'primary'
   */
  variant?: SpinnerVariant;

  /**
   * Loading label (for accessibility)
   * @default 'Loading...'
   */
  label?: string;

  /**
   * Show label text below spinner
   * @default false
   */
  showLabel?: boolean;
}

/**
 * Spinner Component Styles
 *
 * Loading indicator with animation.
 */

/* Container */
.container {
  display: inline-flex;
  flex-direction: column;
  align-items: center;
  gap: var(--spacing-2);
}

/* Spinner */
.spinner {
  border-radius: 50%;
  border-style: solid;
  animation: spin 600ms linear infinite;

  /* Reduce motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    animation: spin 1200ms linear infinite;
  }
}

/* Spin animation */
@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.xs {
  width: 12px;
  height: 12px;
  border-width: 2px;
}

.sm {
  width: 16px;
  height: 16px;
  border-width: 2px;
}

.md {
  width: 24px;
  height: 24px;
  border-width: 3px;
}

.lg {
  width: 32px;
  height: 32px;
  border-width: 3px;
}

.xl {
  width: 48px;
  height: 48px;
  border-width: 4px;
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.primary {
  border-color: var(--interactive-primary);
  border-top-color: transparent;
}

.secondary {
  border-color: var(--text-tertiary);
  border-top-color: transparent;
}

.neutral {
  border-color: var(--border-strong);
  border-top-color: transparent;
}

.inverse {
  border-color: var(--text-inverse);
  border-top-color: transparent;
}

/* ============================================================================
 * LABEL
 * ============================================================================ */

.label {
  font-size: var(--font-size-12);
  color: var(--text-secondary);
  font-weight: var(--font-weight-medium);
}
