/**
 * Stepper Component
 *
 * A component to display progress through a multi-step process.
 *
 * @example
 * ```tsx
 * <Stepper
 *   steps={[
 *     { id: '1', title: 'Account', description: 'Create your account' },
 *     { id: '2', title: 'Profile', description: 'Set up your profile' },
 *     { id: '3', title: 'Complete', description: 'Review and finish' },
 *   ]}
 *   currentStep={1}
 * />
 * ```
 */

import { forwardRef, useCallback, useRef, useEffect } from 'react';
import { Check, AlertCircle } from 'lucide-react';
import type { StepperProps, StepProps, StepStatus } from './Stepper.types';
import styles from './Stepper.module.css';

/**
 * Individual Step Component
 */
const Step = ({
  step,
  index,
  status,
  size,
  showNumber,
  showCheckmark,
  clickable,
  onClick,
  labelPosition,
}: StepProps) => {
  const handleClick = useCallback(() => {
    if (clickable && !step.disabled && onClick) {
      onClick();
    }
  }, [clickable, step.disabled, onClick]);

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent) => {
      if ((e.key === 'Enter' || e.key === ' ') && clickable && !step.disabled) {
        e.preventDefault();
        onClick?.();
      }
    },
    [clickable, step.disabled, onClick],
  );

  const stepClasses = [
    styles.step,
    styles[status],
    styles[size],
    styles[`label-${labelPosition}`],
    clickable && !step.disabled && styles.clickable,
    step.disabled && styles.disabled,
  ]
    .filter(Boolean)
    .join(' ');

  const iconSize = size === 'sm' ? 14 : size === 'lg' ? 20 : 16;

  const renderIndicator = () => {
    // Error state
    if (status === 'error' || step.error) {
      return <AlertCircle size={iconSize} />;
    }

    // Custom icon
    if (step.icon) {
      return step.icon;
    }

    // Checkmark for completed
    if (status === 'complete' && showCheckmark) {
      return <Check size={iconSize} />;
    }

    // Number
    if (showNumber) {
      return <span className={styles.number}>{index + 1}</span>;
    }

    return null;
  };

  return (
    <div
      className={stepClasses}
      role={clickable ? 'button' : undefined}
      tabIndex={clickable && !step.disabled ? 0 : undefined}
      aria-current={status === 'current' ? 'step' : undefined}
      aria-disabled={step.disabled}
      onClick={handleClick}
      onKeyDown={handleKeyDown}
    >
      <div className={styles.indicator}>{renderIndicator()}</div>

      <div className={styles.content}>
        <span className={styles.title}>
          {step.title}
          {step.optional && <span className={styles.optional}>(Optional)</span>}
        </span>
        {step.description && <span className={styles.description}>{step.description}</span>}
        {(step.error || status === 'error') && step.errorMessage && (
          <span className={styles.errorMessage}>{step.errorMessage}</span>
        )}
      </div>
    </div>
  );
};

export const Stepper = forwardRef<HTMLDivElement, StepperProps>(
  (
    {
      steps,
      currentStep = 0,
      onStepClick,
      clickable = false,
      orientation = 'horizontal',
      size = 'md',
      showNumbers = true,
      showConnectors = true,
      showCheckmarks = true,
      labelPosition = 'bottom',
      scrollable = false,
      className,
      ...rest
    },
    ref,
  ) => {
    const listRef = useRef<HTMLOListElement>(null);

    // Auto-scroll to current step when scrollable is enabled
    useEffect(() => {
      if (scrollable && listRef.current) {
        const items = listRef.current.children;
        const currentItem = items[currentStep] as HTMLElement | undefined;
        if (currentItem) {
          currentItem.scrollIntoView({
            behavior: 'smooth',
            block: 'nearest',
            inline: 'center',
          });
        }
      }
    }, [currentStep, scrollable]);

    const getStepStatus = (index: number): StepStatus => {
      if (steps[index]?.error) return 'error';
      if (index < currentStep) return 'complete';
      if (index === currentStep) return 'current';
      return 'upcoming';
    };

    const handleStepClick = useCallback(
      (index: number) => {
        const step = steps[index];
        if (onStepClick && step && !step.disabled) {
          onStepClick(index, step);
        }
      },
      [onStepClick, steps],
    );

    const containerClasses = [
      styles.stepper,
      styles[orientation],
      styles[size],
      showConnectors && styles.withConnectors,
      scrollable && styles.scrollable,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <div ref={ref} className={containerClasses} role="navigation" aria-label="Progress" {...rest}>
        <ol ref={listRef} className={styles.list}>
          {steps.map((step, index) => (
            <li key={step.id} className={styles.item}>
              <Step
                step={step}
                index={index}
                status={getStepStatus(index)}
                size={size}
                showNumber={showNumbers}
                showCheckmark={showCheckmarks}
                clickable={clickable}
                onClick={() => handleStepClick(index)}
                labelPosition={labelPosition}
              />
              {showConnectors && index < steps.length - 1 && (
                <div
                  className={`${styles.connector} ${index < currentStep ? styles.complete : ''}`}
                  aria-hidden="true"
                />
              )}
            </li>
          ))}
        </ol>
      </div>
    );
  },
);

Stepper.displayName = 'Stepper';

/**
 * Stepper Component Types
 *
 * Type definitions for the Orion Stepper/Wizard component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Stepper orientation
 */
export type StepperOrientation = 'horizontal' | 'vertical';

/**
 * Stepper size variants
 */
export type StepperSize = 'sm' | 'md' | 'lg';

/**
 * Step status
 */
export type StepStatus = 'complete' | 'current' | 'upcoming' | 'error';

/**
 * Step item definition
 */
export interface StepItem {
  /**
   * Unique identifier for the step
   */
  id: string;

  /**
   * Step title/label
   */
  title: string;

  /**
   * Optional description
   */
  description?: string;

  /**
   * Custom icon for the step
   */
  icon?: ReactNode;

  /**
   * Whether this step is optional
   */
  optional?: boolean;

  /**
   * Whether this step has an error
   */
  error?: boolean;

  /**
   * Error message to display
   */
  errorMessage?: string;

  /**
   * Whether this step is disabled
   */
  disabled?: boolean;
}

/**
 * Stepper component props
 *
 * @example
 * ```tsx
 * <Stepper
 *   steps={[
 *     { id: '1', title: 'Account', description: 'Create your account' },
 *     { id: '2', title: 'Profile', description: 'Set up your profile' },
 *     { id: '3', title: 'Complete', description: 'Review and finish' },
 *   ]}
 *   currentStep={1}
 * />
 * ```
 */
export interface StepperProps extends Omit<HTMLAttributes<HTMLDivElement>, 'onChange'> {
  /**
   * Array of step items
   */
  steps: StepItem[];

  /**
   * Current active step index (0-based)
   * @default 0
   */
  currentStep?: number;

  /**
   * Callback when a step is clicked (if clickable)
   */
  onStepClick?: (stepIndex: number, step: StepItem) => void;

  /**
   * Whether steps are clickable
   * @default false
   */
  clickable?: boolean;

  /**
   * Stepper orientation
   * @default 'horizontal'
   */
  orientation?: StepperOrientation;

  /**
   * Size variant
   * @default 'md'
   */
  size?: StepperSize;

  /**
   * Whether to show step numbers
   * @default true
   */
  showNumbers?: boolean;

  /**
   * Whether to show connector lines between steps
   * @default true
   */
  showConnectors?: boolean;

  /**
   * Whether completed steps should be marked with a checkmark
   * @default true
   */
  showCheckmarks?: boolean;

  /**
   * Alternative label position (only for horizontal orientation)
   * @default 'bottom'
   */
  labelPosition?: 'bottom' | 'right';

  /**
   * Enable horizontal scrolling on mobile for better UX with many steps
   * When enabled, labels remain visible and the stepper scrolls horizontally.
   * Auto-scrolls to center the current step when it changes.
   * @default false
   */
  scrollable?: boolean;
}

/**
 * Step component props (internal)
 */
export interface StepProps {
  step: StepItem;
  index: number;
  status: StepStatus;
  size: StepperSize;
  showNumber: boolean;
  showCheckmark: boolean;
  clickable: boolean;
  onClick?: () => void;
  labelPosition: 'bottom' | 'right';
}

/**
 * Stepper Component Styles
 *
 * Minimalist Linear-style stepper with elegant, refined design.
 * - Thin borders and subtle colors
 * - No heavy backgrounds on indicators
 * - Clean typography with generous spacing
 */

.stepper {
  width: 100%;
}

.list {
  display: flex;
  margin: 0;
  padding: 0;
  list-style: none;
}

/* ============================================================================
 * MOBILE-FIRST: HORIZONTAL (Default)
 * Compact dots with labels hidden, current step info shown separately
 * ============================================================================ */

.horizontal .list {
  flex-direction: row;
  align-items: center;
  justify-content: center;
  gap: 0;
}

.horizontal .item {
  display: flex;
  align-items: center;
  flex: 1;
  max-width: 120px;
}

.horizontal .item:last-child {
  flex: 0;
  max-width: none;
}

/* ============================================================================
 * STEP
 * ============================================================================ */

.step {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
}

.horizontal .step {
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.horizontal .label-right .step {
  flex-direction: row;
  text-align: left;
}

/* ============================================================================
 * INDICATOR - Minimalist Style
 * ============================================================================ */

.indicator {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  border-radius: 50%;
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  transition: all var(--transition-fast);
  position: relative;
  z-index: var(--z-base);
}

/* Mobile sizes - smaller and more elegant */
.sm .indicator {
  width: 20px;
  height: 20px;
  font-size: var(--font-size-11);
}

.md .indicator {
  width: 24px;
  height: 24px;
  font-size: var(--font-size-12);
}

.lg .indicator {
  width: 28px;
  height: 28px;
  font-size: var(--font-size-13);
}

/* ============================================================================
 * STATUS STYLES - Minimalist (Linear-inspired)
 * ============================================================================ */

/* Upcoming - thin border, transparent background */
.upcoming .indicator {
  background: transparent;
  border: 1px solid var(--border-subtle);
  color: var(--text-tertiary);
}

/* Current - brand border only, no fill */
.current .indicator {
  background: transparent;
  border: 1.5px solid var(--interactive-primary);
  color: var(--interactive-primary);
  /* No shadow, no glow - clean and minimal */
}

/* Complete - no border, just the checkmark */
.complete .indicator {
  background: transparent;
  border: none;
  color: var(--status-success);
}

/* Error - no border, just the icon */
.error .indicator {
  background: transparent;
  border: none;
  color: var(--status-error);
}

/* ============================================================================
 * NUMBER
 * ============================================================================ */

.number {
  font-variant-numeric: tabular-nums;
}

/* ============================================================================
 * CONTENT (Labels)
 * ============================================================================ */

.content {
  display: none; /* Hidden on mobile by default */
  flex-direction: column;
  gap: var(--spacing-1);
  min-width: 0;
}

/* ============================================================================
 * TITLE - Clean typography
 * ============================================================================ */

.title {
  font-family: var(--font-secondary);
  font-weight: var(--font-weight-medium);
  color: var(--text-primary);
  line-height: 1.4;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.sm .title {
  font-size: var(--font-size-12);
}
.md .title {
  font-size: var(--font-size-13);
}
.lg .title {
  font-size: var(--font-size-14);
}

.upcoming .title {
  color: var(--text-tertiary);
}
.complete .title {
  color: var(--text-secondary);
}

/* ============================================================================
 * OPTIONAL
 * ============================================================================ */

.optional {
  font-size: var(--font-size-10);
  font-weight: var(--font-weight-normal);
  color: var(--text-tertiary);
  margin-left: var(--spacing-1);
}

/* ============================================================================
 * DESCRIPTION - Subtle secondary text
 * ============================================================================ */

.description {
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  line-height: 1.4;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.upcoming .description {
  color: var(--text-tertiary);
  opacity: 0.7;
}

/* ============================================================================
 * ERROR MESSAGE
 * ============================================================================ */

.errorMessage {
  color: var(--status-error);
  font-size: var(--font-size-11);
}

/* ============================================================================
 * CONNECTOR - Thin and elegant (1px)
 * ============================================================================ */

.connector {
  flex: 1;
  height: 1px;
  background: var(--border-subtle);
  transition: background var(--transition-fast);
  min-width: 24px;
}

.connector.complete {
  background: color-mix(in srgb, var(--status-success) 50%, transparent);
}

/* Horizontal connector */
.horizontal .connector {
  margin: 0;
}

.horizontal.sm .connector {
  height: 1px;
}
.horizontal.md .connector {
  height: 1px;
}
.horizontal.lg .connector {
  height: 1px;
}

/* When labels are shown, position connector properly */
.horizontal .label-bottom .connector {
  position: absolute;
  top: 50%;
  left: 100%;
  right: 0;
  transform: translateY(-50%);
  margin: 0;
}

/* Vertical connector */
.vertical .connector {
  width: 1px;
  height: 20px;
  margin: var(--spacing-2) 0;
  margin-left: 9px;
  flex: none;
}

.vertical.sm .connector {
  margin-left: 9px;
  height: 16px;
}
.vertical.md .connector {
  margin-left: 11px;
  height: 20px;
}
.vertical.lg .connector {
  margin-left: 13px;
  height: 24px;
}

/* ============================================================================
 * VERTICAL ORIENTATION
 * ============================================================================ */

.vertical .list {
  flex-direction: column;
  gap: 0;
}

.vertical .item {
  display: flex;
  flex-direction: column;
}

.vertical .step {
  flex-direction: row;
  align-items: flex-start;
  text-align: left;
  gap: var(--spacing-3);
}

.vertical .content {
  display: flex;
  padding-top: 2px;
}

/* ============================================================================
 * CLICKABLE - Subtle interaction
 * ============================================================================ */

.clickable {
  cursor: pointer;
}

.clickable:hover .indicator {
  transform: scale(1.05);
  opacity: 0.8;
}

.clickable:active .indicator {
  transform: scale(0.98);
}

.clickable:focus-visible {
  outline: none;
}

.clickable:focus-visible .indicator {
  outline: 1.5px solid var(--interactive-primary);
  outline-offset: 2px;
}

/* ============================================================================
 * DISABLED
 * ============================================================================ */

.disabled {
  opacity: 0.35;
  cursor: not-allowed;
  pointer-events: none;
}

/* ============================================================================
 * RESPONSIVE: TABLET AND UP (min-width: 640px)
 * Show labels on larger screens
 * ============================================================================ */

@media (min-width: 640px) {
  .horizontal .item {
    max-width: 200px;
  }

  .horizontal .content {
    display: flex;
    margin-top: var(--spacing-3);
  }

  .horizontal .label-right .content {
    margin-top: 0;
    margin-left: var(--spacing-3);
  }

  /* Slightly larger indicators on desktop - still minimal */
  .sm .indicator {
    width: 22px;
    height: 22px;
    font-size: var(--font-size-11);
  }

  .md .indicator {
    width: 26px;
    height: 26px;
    font-size: var(--font-size-12);
  }

  .lg .indicator {
    width: 30px;
    height: 30px;
    font-size: var(--font-size-13);
  }

  /* Vertical spacing on desktop */
  .vertical.sm .connector {
    height: 20px;
    margin-left: 10px;
  }
  .vertical.md .connector {
    height: 24px;
    margin-left: 12px;
  }
  .vertical.lg .connector {
    height: 28px;
    margin-left: 14px;
  }
}

/* ============================================================================
 * RESPONSIVE: DESKTOP (min-width: 768px)
 * ============================================================================ */

@media (min-width: 768px) {
  .horizontal .item {
    max-width: none;
  }

  .title {
    white-space: normal;
    overflow: visible;
  }

  .description {
    white-space: normal;
    overflow: visible;
  }
}

/* ============================================================================
 * SCROLLABLE MODE
 * Enables horizontal scrolling on mobile for better UX with many steps
 * ============================================================================ */

.scrollable .list {
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none; /* Firefox */
  scroll-behavior: smooth;
  scroll-snap-type: x mandatory;
  padding: var(--spacing-2) 0;
  /* Override horizontal defaults for proper scrolling */
  justify-content: flex-start;
  flex-wrap: nowrap;
}

.scrollable .list::-webkit-scrollbar {
  display: none; /* Chrome/Safari */
}

.scrollable .item {
  flex: 0 0 auto;
  min-width: 120px;
  max-width: none;
  scroll-snap-align: center;
}

.scrollable .item:last-child {
  flex: 0 0 auto;
  min-width: 120px;
}

/* In scrollable mode, always show labels */
.scrollable .content {
  display: flex;
  margin-top: var(--spacing-2);
}

/* Scrollable connectors need proper sizing */
.scrollable .connector {
  min-width: 40px;
  flex: 0 0 40px;
}

/* On tablet and up, scrollable items can be slightly larger */
@media (min-width: 640px) {
  .scrollable .item {
    min-width: 140px;
  }

  .scrollable .item:last-child {
    min-width: 140px;
  }

  .scrollable .content {
    margin-top: var(--spacing-3);
  }

  .scrollable .connector {
    min-width: 48px;
    flex: 0 0 48px;
  }
}

/**
 * Stepper Component - Multi-step process indicator.
 *
 * @example
 * ```tsx
 * import { Stepper } from '@orion-ds/react';
 *
 * <Stepper
 *   steps={[
 *     { id: '1', label: 'Account', status: 'completed' },
 *     { id: '2', label: 'Profile', status: 'current' },
 *     { id: '3', label: 'Review', status: 'pending' },
 *   ]}
 *   current={1}
 * />
 * ```
 */
export { Stepper } from './Stepper';
export type {
  StepperProps,
  StepItem,
  StepStatus,
  StepperOrientation,
  StepperSize,
} from './Stepper.types';
