/**
 * Table Component
 *
 * Data display table with sorting, customizable columns, and interactive rows.
 *
 * @example
 * ```tsx
 * interface User {
 *   id: number;
 *   name: string;
 *   email: string;
 *   status: 'active' | 'inactive';
 * }
 *
 * const columns: TableColumn<User>[] = [
 *   { key: 'name', header: 'Name', sortable: true },
 *   { key: 'email', header: 'Email', sortable: true },
 *   {
 *     key: 'status',
 *     header: 'Status',
 *     cell: (user) => (
 *       <Badge variant={user.status === 'active' ? 'success' : 'neutral'}>
 *         {user.status}
 *       </Badge>
 *     ),
 *     align: 'center'
 *   }
 * ];
 *
 * <Table
 *   columns={columns}
 *   data={users}
 *   striped
 *   hoverable
 *   onRowClick={(user) => navigate(`/users/${user.id}`)}
 * />
 * ```
 */

import { useState, useCallback } from "react";
import type { TableProps, TableColumn, SortDirection } from "./Table.types";
import styles from "./Table.module.css";

export const Table = <T extends Record<string, any>>({
  columns,
  data,
  size = "md",
  striped = false,
  hoverable = true,
  bordered = false,
  borderless = false,
  caption,
  emptyMessage = "No data available",
  onRowClick,
  onSortChange,
  sortState,
  getRowKey = (_, index) => index,
  className,
}: TableProps<T>) => {
  // Internal sort state (for uncontrolled sorting)
  const [internalSortState, setInternalSortState] = useState<{
    columnKey: string;
    direction: SortDirection;
  } | null>(null);

  // Use external sort state if provided, otherwise use internal
  const currentSort = sortState || internalSortState;

  // Handle column header click (sorting)
  const handleSort = useCallback(
    (column: TableColumn<T>) => {
      if (!column.sortable) return;

      const columnKey = column.key;
      let newDirection: SortDirection = "asc";

      // Toggle sort direction
      if (currentSort?.columnKey === columnKey) {
        if (currentSort.direction === "asc") {
          newDirection = "desc";
        } else if (currentSort.direction === "desc") {
          newDirection = null; // Clear sort
        }
      }

      // Update internal state if not controlled
      if (!sortState) {
        setInternalSortState(
          newDirection ? { columnKey, direction: newDirection } : null,
        );
      }

      // Notify parent
      onSortChange?.(columnKey, newDirection);
    },
    [currentSort, sortState, onSortChange],
  );

  // Render sort indicator
  const renderSortIndicator = (column: TableColumn<T>) => {
    if (!column.sortable) return null;

    const isActive = currentSort?.columnKey === column.key;
    const direction = isActive ? currentSort?.direction : null;

    const indicatorClasses = [styles.sortIndicator, isActive && styles.active]
      .filter(Boolean)
      .join(" ");

    if (direction === "asc") {
      return <span className={indicatorClasses}>▲</span>;
    }
    if (direction === "desc") {
      return <span className={indicatorClasses}>▼</span>;
    }

    return <span className={indicatorClasses}>⇅</span>;
  };

  // Render table header
  const renderHeader = () => (
    <thead className={styles.thead}>
      <tr>
        {columns.map((column) => {
          const thClasses = [
            styles.th,
            column.align &&
              styles[
                `align${column.align.charAt(0).toUpperCase()}${column.align.slice(1)}`
              ],
            column.sortable && styles.sortable,
          ]
            .filter(Boolean)
            .join(" ");

          const handleClick = column.sortable
            ? () => handleSort(column)
            : undefined;

          return (
            <th
              key={column.key}
              className={thClasses}
              style={{ width: column.width }}
              onClick={handleClick}
              tabIndex={column.sortable ? 0 : undefined}
              role={column.sortable ? "button" : undefined}
              aria-sort={
                currentSort?.columnKey === column.key
                  ? currentSort.direction === "asc"
                    ? "ascending"
                    : "descending"
                  : undefined
              }
              {...column.headerProps}
            >
              {column.header}
              {renderSortIndicator(column)}
            </th>
          );
        })}
      </tr>
    </thead>
  );

  // Render table body
  const renderBody = () => {
    if (data.length === 0) {
      return (
        <tbody>
          <tr>
            <td colSpan={columns.length} className={styles.empty}>
              {emptyMessage}
            </td>
          </tr>
        </tbody>
      );
    }

    return (
      <tbody className={styles.tbody}>
        {data.map((row, rowIndex) => {
          const rowKey = getRowKey(row, rowIndex);

          return (
            <tr
              key={rowKey}
              className={styles.tr}
              onClick={onRowClick ? () => onRowClick(row, rowIndex) : undefined}
            >
              {columns.map((column) => {
                const tdClasses = [
                  styles.td,
                  column.align &&
                    styles[
                      `align${column.align.charAt(0).toUpperCase()}${column.align.slice(1)}`
                    ],
                ]
                  .filter(Boolean)
                  .join(" ");

                // Use custom cell renderer if provided, otherwise use key accessor
                const cellContent = column.cell
                  ? column.cell(row, rowIndex)
                  : row[column.key];

                return (
                  <td
                    key={column.key}
                    className={tdClasses}
                    {...column.cellProps}
                  >
                    {cellContent}
                  </td>
                );
              })}
            </tr>
          );
        })}
      </tbody>
    );
  };

  // Container classes
  const containerClasses = [
    styles.container,
    styles[size],
    striped && styles.striped,
    hoverable && styles.hoverable,
    onRowClick && styles.clickable,
    bordered && styles.bordered,
    borderless && styles.borderless,
    className,
  ]
    .filter(Boolean)
    .join(" ");

  return (
    <div className={containerClasses}>
      <table className={styles.table}>
        {caption && <caption className={styles.caption}>{caption}</caption>}
        {renderHeader()}
        {renderBody()}
      </table>
    </div>
  );
};

Table.displayName = "Table";

/**
 * Table Component Types
 *
 * Type definitions for the Orion Table component.
 */

import type { ReactNode, ThHTMLAttributes, TdHTMLAttributes } from "react";

/**
 * Table sizes
 */
export type TableSize = "sm" | "md" | "lg";

/**
 * Sort direction
 */
export type SortDirection = "asc" | "desc" | null;

/**
 * Column definition
 */
export interface TableColumn<T = Record<string, unknown>> {
  /**
   * Column unique key (used as accessor for data)
   */
  key: string;

  /**
   * Column header label
   */
  header: ReactNode;

  /**
   * Custom cell renderer
   * @param row - Row data
   * @param rowIndex - Row index
   * @returns Rendered cell content
   */
  cell?: (row: T, rowIndex: number) => ReactNode;

  /**
   * Column width (CSS value)
   */
  width?: string;

  /**
   * Enable sorting for this column
   * @default false
   */
  sortable?: boolean;

  /**
   * Align cell content
   * @default 'left'
   */
  align?: "left" | "center" | "right";

  /**
   * Additional props for header cell
   */
  headerProps?: ThHTMLAttributes<HTMLTableCellElement>;

  /**
   * Additional props for data cells
   */
  cellProps?: TdHTMLAttributes<HTMLTableCellElement>;
}

/**
 * Table component props
 *
 * @example
 * ```tsx
 * const columns: TableColumn<User>[] = [
 *   { key: 'name', header: 'Name', sortable: true },
 *   { key: 'email', header: 'Email' },
 *   {
 *     key: 'status',
 *     header: 'Status',
 *     cell: (user) => <Badge>{user.status}</Badge>
 *   }
 * ];
 *
 * <Table
 *   columns={columns}
 *   data={users}
 *   onRowClick={(user) => console.log(user)}
 * />
 * ```
 */
export interface TableProps<T = Record<string, unknown>> {
  /**
   * Column definitions
   */
  columns: TableColumn<T>[];

  /**
   * Table data
   */
  data: T[];

  /**
   * Table size
   * @default 'md'
   */
  size?: TableSize;

  /**
   * Striped rows
   * @default false
   */
  striped?: boolean;

  /**
   * Hoverable rows
   * @default true
   */
  hoverable?: boolean;

  /**
   * Show borders
   * @default false
   */
  bordered?: boolean;

  /**
   * Remove container border (for integration inside Cards)
   * @default false
   */
  borderless?: boolean;

  /**
   * Table caption
   */
  caption?: ReactNode;

  /**
   * Empty state message
   */
  emptyMessage?: ReactNode;

  /**
   * Row click handler
   */
  onRowClick?: (row: T, rowIndex: number) => void;

  /**
   * Sort change handler
   */
  onSortChange?: (columnKey: string, direction: SortDirection) => void;

  /**
   * Current sort state (for controlled sorting)
   */
  sortState?: {
    columnKey: string;
    direction: SortDirection;
  };

  /**
   * Row key extractor (for React keys)
   * @default (row, index) => index
   */
  getRowKey?: (row: T, index: number) => string | number;

  /**
   * Additional CSS class
   */
  className?: string;
}

/**
 * Table Component Styles
 *
 * Data display table with sorting, selection, and various styling options.
 */

/* Container */
.container {
  width: 100%;
  overflow-x: auto;
  border-radius: var(--radius-control);
  border: 1px solid var(--border-subtle);
  overflow: hidden;
}

/* Borderless variant - for integration inside Cards */
.borderless {
  border: none;
  border-radius: 0;
}

/* Table */
.table {
  width: 100%;
  border-collapse: collapse;
  font-size: var(--font-size-16);
  color: var(--text-primary);
  background: var(--surface-base);
}

/* Caption */
.caption {
  padding: var(--spacing-3);
  font-size: var(--font-size-12);
  color: var(--text-secondary);
  text-align: left;
  caption-side: top;
}

/* ============================================================================
 * HEADER
 * ============================================================================ */

.thead {
  border-bottom: 1px solid var(--border-subtle);
}

.th {
  padding: var(--spacing-2) var(--spacing-4);
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-medium);
  text-align: left;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  white-space: nowrap;
}

.th.alignCenter {
  text-align: center;
}

.th.alignRight {
  text-align: right;
}

/* Sortable column header */
.sortable {
  cursor: pointer;
  user-select: none;
  transition: background var(--transition-fast);
}

.sortable:hover {
  color: var(--text-primary);
}

.sortable:focus-visible {
  outline: 2px solid var(--interactive-primary);
  outline-offset: -2px;
}

/* Sort indicator */
.sortIndicator {
  display: inline-flex;
  margin-left: var(--spacing-1);
  color: var(--text-tertiary);
  font-size: 0.75em;
}

.sortIndicator.active {
  color: var(--interactive-primary);
}

/* ============================================================================
 * BODY
 * ============================================================================ */

.tbody {
  /* Base tbody styles */
}

.tr {
  border-bottom: 1px solid var(--border-subtle);
  transition: background var(--transition-fast);
}

/* Remove border from last row for cleaner look */
.tbody .tr:last-child {
  border-bottom: none;
}

.td {
  padding: var(--spacing-3) var(--spacing-4);
  text-align: left;
}

.td.alignCenter {
  text-align: center;
}

.td.alignRight {
  text-align: right;
}

/* ============================================================================
 * INTERACTIVE STATES
 * ============================================================================ */

/* Hoverable rows */
.hoverable .tr:hover {
  background: var(--surface-subtle);
}

/* Clickable rows */
.clickable .tr {
  cursor: pointer;
}

.clickable .tr:active {
  background: var(--surface-layer);
}

/* ============================================================================
 * STRIPED ROWS
 * ============================================================================ */

.striped .tr:nth-child(even) {
  background: var(--surface-subtle);
}

.striped.hoverable .tr:nth-child(even):hover {
  background: var(--surface-layer);
}

/* ============================================================================
 * BORDERED
 * ============================================================================ */

.bordered {
  border: 1px solid var(--border-subtle);
}

.bordered .th,
.bordered .td {
  border-right: 1px solid var(--border-subtle);
}

.bordered .th:last-child,
.bordered .td:last-child {
  border-right: none;
}

/* ============================================================================
 * SIZES
 * ============================================================================ */

.sm .th {
  padding: var(--spacing-1) var(--spacing-3);
  font-size: var(--font-size-10);
}

.sm .td {
  padding: var(--spacing-2) var(--spacing-3);
  font-size: var(--font-size-12);
}

.md .th {
  padding: var(--spacing-2) var(--spacing-4);
  font-size: var(--font-size-12);
}

.md .td {
  padding: var(--spacing-3) var(--spacing-4);
  font-size: var(--font-size-16);
}

.lg .th {
  padding: var(--spacing-3) var(--spacing-6);
  font-size: var(--font-size-12);
}

.lg .td {
  padding: var(--spacing-4) var(--spacing-6);
  font-size: var(--font-size-16);
}

/* ============================================================================
 * EMPTY STATE
 * ============================================================================ */

.empty {
  padding: var(--spacing-8) var(--spacing-4);
  text-align: center;
  color: var(--text-tertiary);
  font-size: var(--font-size-16);
}
