/**
 * Tabs Component
 *
 * Navigation tabs for switching between content sections.
 *
 * @example
 * ```tsx
 * <Tabs
 *   tabs={[
 *     {
 *       id: 'profile',
 *       label: 'Profile',
 *       content: <ProfileContent />,
 *       icon: <UserIcon />
 *     },
 *     {
 *       id: 'settings',
 *       label: 'Settings',
 *       content: <SettingsContent />,
 *       badge: 3
 *     },
 *     {
 *       id: 'billing',
 *       label: 'Billing',
 *       content: <BillingContent />,
 *       disabled: true
 *     }
 *   ]}
 *   defaultTab="profile"
 * />
 * ```
 */

import React, { useState, useRef, useCallback } from 'react';
import type { TabsProps } from './Tabs.types';
import styles from './Tabs.module.css';

export const Tabs: React.FC<TabsProps> = ({
  tabs,
  defaultTab,
  activeTab: controlledActiveTab,
  onChange,
  fullWidth = false,
  className,
}) => {
  // Determine if controlled or uncontrolled
  const isControlled = controlledActiveTab !== undefined;

  // Internal state for uncontrolled mode
  const [internalActiveTab, setInternalActiveTab] = useState(defaultTab || tabs[0]?.id || '');

  // Refs for tab buttons
  const tabRefs = useRef<Map<string, HTMLButtonElement>>(new Map());

  // Get current active tab
  const activeTab = isControlled ? controlledActiveTab : internalActiveTab;

  // Handle tab change
  const handleTabChange = (tabId: string) => {
    if (!isControlled) {
      setInternalActiveTab(tabId);
    }
    onChange?.(tabId);
  };

  // Get enabled tabs (filter out disabled)
  const getEnabledTabs = useCallback(() => {
    return tabs.filter((tab) => !tab.disabled);
  }, [tabs]);

  // Focus a specific tab by index in the enabled tabs list
  const focusTabByIndex = useCallback(
    (index: number) => {
      const enabledTabs = getEnabledTabs();
      if (enabledTabs.length === 0) return;

      // Wrap around
      const wrappedIndex = ((index % enabledTabs.length) + enabledTabs.length) % enabledTabs.length;
      const targetTab = enabledTabs[wrappedIndex];
      if (!targetTab) return;
      const tabElement = tabRefs.current.get(targetTab.id);
      tabElement?.focus();
    },
    [getEnabledTabs],
  );

  // Handle keyboard navigation
  const handleKeyDown = useCallback(
    (event: React.KeyboardEvent, currentTabId: string) => {
      const enabledTabs = getEnabledTabs();
      const currentIndex = enabledTabs.findIndex((tab) => tab.id === currentTabId);

      switch (event.key) {
        case 'ArrowLeft':
          event.preventDefault();
          focusTabByIndex(currentIndex - 1);
          break;
        case 'ArrowRight':
          event.preventDefault();
          focusTabByIndex(currentIndex + 1);
          break;
        case 'Home':
          event.preventDefault();
          focusTabByIndex(0);
          break;
        case 'End':
          event.preventDefault();
          focusTabByIndex(enabledTabs.length - 1);
          break;
      }
    },
    [getEnabledTabs, focusTabByIndex],
  );

  // Find active tab content
  const activeTabData = tabs.find((tab) => tab.id === activeTab);

  const containerClasses = [styles.container, fullWidth && styles.fullWidth, className]
    .filter(Boolean)
    .join(' ');

  return (
    <div className={containerClasses}>
      {/* Tab list */}
      <div className={styles.tabList} role="tablist">
        {tabs.map((tab) => {
          const isActive = tab.id === activeTab;

          const tabClasses = [styles.tab, isActive && styles.active].filter(Boolean).join(' ');

          return (
            <button
              key={tab.id}
              ref={(el) => {
                if (el) {
                  tabRefs.current.set(tab.id, el);
                } else {
                  tabRefs.current.delete(tab.id);
                }
              }}
              type="button"
              role="tab"
              aria-selected={isActive}
              aria-controls={`panel-${tab.id}`}
              id={`tab-${tab.id}`}
              className={tabClasses}
              onClick={() => handleTabChange(tab.id)}
              onKeyDown={(e) => handleKeyDown(e, tab.id)}
              disabled={tab.disabled}
              tabIndex={isActive ? 0 : -1}
            >
              {/* Icon */}
              {tab.icon && <span className={styles.icon}>{tab.icon}</span>}

              {/* Label */}
              <span>{tab.label}</span>

              {/* Badge */}
              {tab.badge !== undefined && <span className={styles.badge}>{tab.badge}</span>}
            </button>
          );
        })}
      </div>

      {/* Tab panel (content) */}
      {activeTabData && (
        <div
          key={activeTab}
          className={styles.panel}
          role="tabpanel"
          id={`panel-${activeTab}`}
          aria-labelledby={`tab-${activeTab}`}
        >
          {activeTabData.content}
        </div>
      )}
    </div>
  );
};

Tabs.displayName = 'Tabs';

/**
 * Tabs Component Types
 *
 * Type definitions for the Orion Tabs component.
 */

import type { ReactNode } from 'react';

/**
 * Tab item
 */
export interface TabItem {
  /**
   * Unique tab identifier
   */
  id: string;

  /**
   * Tab label
   */
  label: string;

  /**
   * Tab content
   */
  content: ReactNode;

  /**
   * Optional icon
   */
  icon?: ReactNode;

  /**
   * Disabled state
   * @default false
   */
  disabled?: boolean;

  /**
   * Badge/count indicator
   */
  badge?: string | number;
}

/**
 * Tabs component props
 *
 * @example
 * ```tsx
 * <Tabs
 *   tabs={[
 *     { id: 'tab1', label: 'Profile', content: <Profile /> },
 *     { id: 'tab2', label: 'Settings', content: <Settings /> }
 *   ]}
 * />
 * ```
 */
export interface TabsProps {
  /**
   * Array of tabs
   */
  tabs: TabItem[];

  /**
   * Default active tab ID
   */
  defaultTab?: string;

  /**
   * Controlled active tab ID
   */
  activeTab?: string;

  /**
   * Callback when tab changes
   */
  onChange?: (tabId: string) => void;

  /**
   * Full width tabs
   * @default false
   */
  fullWidth?: boolean;

  /**
   * Additional class name
   */
  className?: string;
}

/**
 * Tabs Component Styles
 *
 * Navigation tabs for switching between content sections.
 */

/* Container */
.container {
  display: flex;
  flex-direction: column;
  width: 100%;
}

/* Tab list */
.tabList {
  display: flex;
  gap: var(--spacing-1);
  border-bottom: 1px solid var(--border-subtle);
  overflow-x: auto;
  overflow-y: hidden;

  /* Hide scrollbar but keep functionality */
  scrollbar-width: none;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    display: none;
  }
}

.fullWidth .tabList {
  width: 100%;
}

/* Tab button */
.tab {
  /* Layout */
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-3) var(--spacing-4);
  white-space: nowrap;

  /* Typography */
  font-family: var(--font-secondary);
  font-size: var(--font-size-16);
  font-weight: var(--font-weight-medium);
  color: var(--text-secondary);
  line-height: var(--line-height-tight);

  /* Styling */
  background: transparent;
  border: none;
  border-bottom: 2px solid transparent;
  cursor: pointer;

  /* Interaction */
  transition: all var(--transition-fast);
  outline: none;

  /* Hover state */
  &:hover:not(:disabled) {
    color: var(--text-primary);
    background: var(--interactive-ghost-hover);
  }

  /* Focus state */
  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: -2px;
    border-radius: var(--radius-sm);
  }

  /* Disabled state */
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

/* Full width tabs */
.fullWidth .tab {
  flex: 1;
  justify-content: center;
}

/* Active tab */
.active {
  color: var(--text-brand);
  border-bottom-color: var(--interactive-primary);

  &:hover {
    color: var(--text-brand);
  }
}

/* Tab icon */
.icon {
  width: 20px;
  height: 20px;
  flex-shrink: 0;
}

/* Tab badge */
.badge {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 20px;
  height: 20px;
  padding: 0 var(--spacing-1);
  font-size: var(--text-xs);
  font-weight: var(--font-weight-medium);
  color: var(--text-inverse);
  background: var(--text-tertiary);
  border-radius: var(--radius-full);
  line-height: 1;
}

.active .badge {
  background: var(--interactive-primary);
}

/* Tab panel (content) */
.panel {
  padding: var(--spacing-4) 0;
  animation: fadeIn 200ms ease;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  .panel {
    animation: none;
  }
}
