/**
 * Testimonials Component
 *
 * A testimonials section with support for quotes, avatars, ratings, and
 * multiple visual variants. Composes atomic components from @orion/react.
 *
 * @example
 * ```tsx
 * <Testimonials
 *   eyebrow={<Badge>Testimonials</Badge>}
 *   title="What our customers say"
 *   description="See why thousands of teams love our product"
 *   testimonials={[
 *     {
 *       quote: "This product changed everything for us.",
 *       author: {
 *         name: "Jane Doe",
 *         role: "CEO",
 *         company: "Acme Corp",
 *         avatar: <img src="/avatar.jpg" alt="Jane" />
 *       },
 *       rating: 5
 *     }
 *   ]}
 *   columns={3}
 * />
 * ```
 */

import { forwardRef } from 'react';
import type { TestimonialsProps } from './Testimonials.types';
import { Section } from '../Section';
import { Container } from '../Container';
import { TestimonialCard } from './TestimonialCard';
import styles from './Testimonials.module.css';

export const Testimonials = forwardRef<HTMLElement, TestimonialsProps>(
  (
    {
      eyebrow,
      title,
      description,
      testimonials,
      columns = 3,
      variant = 'default',
      background = 'base',
      centered = true,
      className,
      ...rest
    },
    ref,
  ) => {
    const hasHeader = eyebrow || title || description;

    const classNames = [styles.testimonials, centered && styles.centered, className]
      .filter(Boolean)
      .join(' ');

    return (
      <Section ref={ref} spacing="lg" background={background} className={classNames} {...rest}>
        <Container size="xl">
          {hasHeader && (
            <header className={styles.header}>
              {eyebrow && <div className={styles.eyebrow}>{eyebrow}</div>}
              {title && <h2 className={styles.title}>{title}</h2>}
              {description && <p className={styles.description}>{description}</p>}
            </header>
          )}

          <div className={`${styles.grid} ${styles[`cols-${columns}`]}`}>
            {testimonials.map((testimonial, index) => (
              <TestimonialCard
                key={testimonial.author.name || index}
                testimonial={testimonial}
                variant={variant}
              />
            ))}
          </div>
        </Container>
      </Section>
    );
  },
);

Testimonials.displayName = 'Testimonials';

/**
 * Testimonials Component Types
 *
 * Type definitions for the Orion Testimonials section component.
 */

import type { HTMLAttributes, ReactNode } from 'react';

/**
 * Grid column count for testimonial cards
 */
export type TestimonialsColumns = 1 | 2 | 3;

/**
 * Testimonial variant styles
 */
export type TestimonialsVariant = 'default' | 'cards' | 'minimal';

/**
 * Author information for a testimonial
 */
export interface TestimonialAuthor {
  /**
   * Author's name
   */
  name: string;

  /**
   * Author's role or title
   */
  role?: string;

  /**
   * Author's company or organization
   */
  company?: string;

  /**
   * Avatar element (img or custom component)
   */
  avatar?: ReactNode;
}

/**
 * Testimonial data structure
 */
export interface Testimonial {
  /**
   * The testimonial quote text
   */
  quote: string;

  /**
   * Author information
   */
  author: TestimonialAuthor;

  /**
   * Star rating (1-5)
   */
  rating?: number;
}

/**
 * Testimonial card props (internal component)
 */
export interface TestimonialCardProps extends HTMLAttributes<HTMLDivElement> {
  /**
   * Testimonial data
   */
  testimonial: Testimonial;

  /**
   * Visual variant
   * @default 'default'
   */
  variant?: TestimonialsVariant;
}

/**
 * Testimonials section props
 *
 * @example
 * ```tsx
 * <Testimonials
 *   title="What our customers say"
 *   testimonials={[
 *     {
 *       quote: "This product changed everything for us.",
 *       author: {
 *         name: "Jane Doe",
 *         role: "CEO",
 *         company: "Acme Corp",
 *         avatar: <img src="/avatar.jpg" alt="Jane" />
 *       },
 *       rating: 5
 *     }
 *   ]}
 * />
 * ```
 */
export interface TestimonialsProps extends Omit<HTMLAttributes<HTMLElement>, 'title'> {
  /**
   * Optional eyebrow/badge above title
   */
  eyebrow?: ReactNode;

  /**
   * Section title
   */
  title?: ReactNode;

  /**
   * Section description
   */
  description?: ReactNode;

  /**
   * Array of testimonials
   */
  testimonials: Testimonial[];

  /**
   * Number of grid columns
   * @default 3
   */
  columns?: TestimonialsColumns;

  /**
   * Visual variant
   * - default: Full cards with borders
   * - cards: Elevated cards with shadows
   * - minimal: Simple quotes without cards
   * @default 'default'
   */
  variant?: TestimonialsVariant;

  /**
   * Background style
   * @default 'base'
   */
  background?: 'base' | 'subtle' | 'none';

  /**
   * Center the header text
   * @default true
   */
  centered?: boolean;
}

/**
 * Testimonials Component Exports
 */

export { Testimonials } from './Testimonials';
export { TestimonialCard } from './TestimonialCard';
export type {
  TestimonialsProps,
  Testimonial,
  TestimonialAuthor,
  TestimonialCardProps,
  TestimonialsColumns,
  TestimonialsVariant,
} from './Testimonials.types';
