/**
 * Toast Component
 *
 * A notification system for showing temporary messages.
 *
 * @example
 * ```tsx
 * // Wrap your app with ToastProvider
 * <ToastProvider>
 *   <App />
 * </ToastProvider>
 *
 * // Use the useToast hook in any component
 * const { toast, success, error } = useToast();
 *
 * toast({ message: 'Hello!' });
 * success('Operation completed');
 * error('Something went wrong');
 * ```
 */

import { createContext, useContext, useState, useCallback, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import type {
  Toast,
  ToastOptions,
  ToastProviderProps,
  ToastContextValue,
  ToastItemProps,
} from './Toast.types';
import styles from './Toast.module.css';

// Generate unique ID
let toastCounter = 0;
const generateId = () => `toast-${++toastCounter}`;

// Toast Context
const ToastContext = createContext<ToastContextValue | null>(null);

/**
 * Hook to access toast functions
 */
export const useToast = (): ToastContextValue => {
  const context = useContext(ToastContext);
  if (!context) {
    throw new Error('useToast must be used within a ToastProvider');
  }
  return context;
};

/**
 * Individual Toast Item
 */
const ToastItem = ({ toast, onDismiss }: ToastItemProps) => {
  const [isExiting, setIsExiting] = useState(false);
  const timerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  const handleDismiss = useCallback(() => {
    setIsExiting(true);
    setTimeout(() => {
      onDismiss(toast.id);
      toast.onDismiss?.();
    }, 200); // Match animation duration
  }, [toast, onDismiss]);

  // Auto-dismiss timer
  useEffect(() => {
    if (toast.duration && toast.duration > 0) {
      timerRef.current = setTimeout(handleDismiss, toast.duration);
      return () => {
        if (timerRef.current) {
          clearTimeout(timerRef.current);
        }
      };
    }
  }, [toast.duration, handleDismiss]);

  // Pause timer on hover
  const handleMouseEnter = () => {
    if (timerRef.current) {
      clearTimeout(timerRef.current);
    }
  };

  const handleMouseLeave = () => {
    if (toast.duration && toast.duration > 0) {
      timerRef.current = setTimeout(handleDismiss, toast.duration);
    }
  };

  const toastClasses = [styles.toast, styles[toast.variant || 'info'], isExiting && styles.exiting]
    .filter(Boolean)
    .join(' ');

  const getIcon = () => {
    switch (toast.variant) {
      case 'success':
        return (
          <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
            <path
              d="M16.667 5L7.5 14.167 3.333 10"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        );
      case 'error':
        return (
          <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
            <circle cx="10" cy="10" r="7.5" stroke="currentColor" strokeWidth="1.5" />
            <path
              d="M10 6v5M10 13.5v.5"
              stroke="currentColor"
              strokeWidth="1.5"
              strokeLinecap="round"
            />
          </svg>
        );
      case 'warning':
        return (
          <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
            <path
              d="M8.57 3.216L1.538 15.143a1.667 1.667 0 001.429 2.524h14.066a1.667 1.667 0 001.429-2.524L11.43 3.216a1.667 1.667 0 00-2.858 0z"
              stroke="currentColor"
              strokeWidth="1.5"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M10 7.5v3.333M10 14.167h.008"
              stroke="currentColor"
              strokeWidth="1.5"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        );
      default: // info
        return (
          <svg width="20" height="20" viewBox="0 0 20 20" fill="none">
            <circle cx="10" cy="10" r="7.5" stroke="currentColor" strokeWidth="1.5" />
            <path
              d="M10 9v4.5M10 6.5v.5"
              stroke="currentColor"
              strokeWidth="1.5"
              strokeLinecap="round"
            />
          </svg>
        );
    }
  };

  return (
    <div
      className={toastClasses}
      role="alert"
      aria-live="polite"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <span className={styles.icon} aria-hidden="true">
        {getIcon()}
      </span>

      <div className={styles.content}>
        {toast.title && <div className={styles.title}>{toast.title}</div>}
        <div className={styles.message}>{toast.message}</div>
      </div>

      {toast.action && (
        <button
          type="button"
          className={styles.action}
          onClick={() => {
            toast.action?.onClick();
            handleDismiss();
          }}
        >
          {toast.action.label}
        </button>
      )}

      {toast.dismissible !== false && (
        <button
          type="button"
          className={styles.closeButton}
          onClick={handleDismiss}
          aria-label="Dismiss"
        >
          <svg width="16" height="16" viewBox="0 0 16 16" fill="none">
            <path
              d="M12 4L4 12M4 4l8 8"
              stroke="currentColor"
              strokeWidth="1.5"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </button>
      )}
    </div>
  );
};

/**
 * Toast Provider Component
 */
export const ToastProvider = ({
  children,
  position = 'bottom-right',
  maxToasts = 5,
  defaultDuration = 5000,
}: ToastProviderProps) => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = useCallback(
    (options: ToastOptions): string => {
      const id = generateId();
      const newToast: Toast = {
        id,
        duration: defaultDuration,
        dismissible: true,
        variant: 'info',
        ...options,
      };

      setToasts((prev) => {
        const updated = [...prev, newToast];
        // Remove oldest toasts if exceeding max
        if (updated.length > maxToasts) {
          return updated.slice(-maxToasts);
        }
        return updated;
      });

      return id;
    },
    [defaultDuration, maxToasts],
  );

  const dismissToast = useCallback((id: string) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  }, []);

  const dismissAll = useCallback(() => {
    setToasts([]);
  }, []);

  // Shorthand methods
  const info = useCallback(
    (message: string, options?: Partial<ToastOptions>) =>
      addToast({ message, variant: 'info', ...options }),
    [addToast],
  );

  const success = useCallback(
    (message: string, options?: Partial<ToastOptions>) =>
      addToast({ message, variant: 'success', ...options }),
    [addToast],
  );

  const warning = useCallback(
    (message: string, options?: Partial<ToastOptions>) =>
      addToast({ message, variant: 'warning', ...options }),
    [addToast],
  );

  const error = useCallback(
    (message: string, options?: Partial<ToastOptions>) =>
      addToast({ message, variant: 'error', ...options }),
    [addToast],
  );

  const value: ToastContextValue = {
    toast: addToast,
    info,
    success,
    warning,
    error,
    dismiss: dismissToast,
    dismissAll,
  };

  const containerClasses = [styles.container, styles[position]].filter(Boolean).join(' ');

  return (
    <ToastContext.Provider value={value}>
      {children}
      {toasts.length > 0 &&
        createPortal(
          <div className={containerClasses} aria-label="Notifications">
            {toasts.map((toast) => (
              <ToastItem key={toast.id} toast={toast} onDismiss={dismissToast} />
            ))}
          </div>,
          document.body,
        )}
    </ToastContext.Provider>
  );
};

ToastProvider.displayName = 'ToastProvider';

/**
 * Toast Component Types
 *
 * Type definitions for the Orion Toast notification system.
 */

import type { ReactNode } from 'react';

/**
 * Toast variants
 */
export type ToastVariant = 'info' | 'success' | 'warning' | 'error';

/**
 * Toast position on screen
 */
export type ToastPosition =
  | 'top-left'
  | 'top-center'
  | 'top-right'
  | 'bottom-left'
  | 'bottom-center'
  | 'bottom-right';

/**
 * Individual toast data
 */
export interface Toast {
  /**
   * Unique identifier for the toast
   */
  id: string;

  /**
   * Toast title (optional)
   */
  title?: string;

  /**
   * Toast message content
   */
  message: ReactNode;

  /**
   * Visual variant of the toast
   * @default 'info'
   */
  variant?: ToastVariant;

  /**
   * Duration in milliseconds before auto-dismiss (0 = no auto-dismiss)
   * @default 5000
   */
  duration?: number;

  /**
   * Whether the toast can be dismissed by the user
   * @default true
   */
  dismissible?: boolean;

  /**
   * Optional action button
   */
  action?: {
    label: string;
    onClick: () => void;
  };

  /**
   * Callback when toast is dismissed
   */
  onDismiss?: () => void;
}

/**
 * Toast creation options (without id)
 */
export type ToastOptions = Omit<Toast, 'id'>;

/**
 * Toast Provider props
 */
export interface ToastProviderProps {
  /**
   * Children to wrap
   */
  children: ReactNode;

  /**
   * Default position for toasts
   * @default 'bottom-right'
   */
  position?: ToastPosition;

  /**
   * Maximum number of toasts to show at once
   * @default 5
   */
  maxToasts?: number;

  /**
   * Default duration for toasts
   * @default 5000
   */
  defaultDuration?: number;
}

/**
 * Toast context value
 */
export interface ToastContextValue {
  /**
   * Add a new toast
   */
  toast: (options: ToastOptions) => string;

  /**
   * Add an info toast (shorthand)
   */
  info: (message: string, options?: Partial<ToastOptions>) => string;

  /**
   * Add a success toast (shorthand)
   */
  success: (message: string, options?: Partial<ToastOptions>) => string;

  /**
   * Add a warning toast (shorthand)
   */
  warning: (message: string, options?: Partial<ToastOptions>) => string;

  /**
   * Add an error toast (shorthand)
   */
  error: (message: string, options?: Partial<ToastOptions>) => string;

  /**
   * Dismiss a toast by id
   */
  dismiss: (id: string) => void;

  /**
   * Dismiss all toasts
   */
  dismissAll: () => void;
}

/**
 * Single toast item props (internal)
 */
export interface ToastItemProps {
  toast: Toast;
  onDismiss: (id: string) => void;
}

/**
 * Toast Component Styles
 *
 * Uses CSS Modules for scoped styling.
 * All values reference semantic tokens via CSS variables.
 */

/* Container for all toasts */
.container {
  position: fixed;
  z-index: var(--z-notification);
  display: flex;
  flex-direction: column;
  gap: var(--spacing-3);
  padding: var(--spacing-4);
  pointer-events: none;
  max-width: 420px;
  width: 100%;
}

/* ============================================================================
 * POSITIONS
 * ============================================================================ */

.top-left {
  top: 0;
  left: 0;
}

.top-center {
  top: 0;
  left: 50%;
  transform: translateX(-50%);
}

.top-right {
  top: 0;
  right: 0;
}

.bottom-left {
  bottom: 0;
  left: 0;
  flex-direction: column-reverse;
}

.bottom-center {
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  flex-direction: column-reverse;
}

.bottom-right {
  bottom: 0;
  right: 0;
  flex-direction: column-reverse;
}

/* ============================================================================
 * TOAST ITEM
 * ============================================================================ */

.toast {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  padding: var(--spacing-4);
  background: var(--surface-base);
  border: 1px solid var(--border-subtle);
  border-radius: var(--radius-control);
  box-shadow: var(--shadow-lg);
  pointer-events: auto;
  animation: slideIn 200ms ease-out;
}

.exiting {
  animation: slideOut 200ms ease-in forwards;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideOut {
  from {
    opacity: 1;
    transform: translateX(0);
  }
  to {
    opacity: 0;
    transform: translateX(100%);
  }
}

/* Left-side positions */
.top-left .toast,
.bottom-left .toast {
  animation-name: slideInLeft;
}

.top-left .exiting,
.bottom-left .exiting {
  animation-name: slideOutLeft;
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideOutLeft {
  from {
    opacity: 1;
    transform: translateX(0);
  }
  to {
    opacity: 0;
    transform: translateX(-100%);
  }
}

/* Center positions */
.top-center .toast,
.bottom-center .toast {
  animation-name: slideInCenter;
}

.top-center .exiting,
.bottom-center .exiting {
  animation-name: slideOutCenter;
}

@keyframes slideInCenter {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideOutCenter {
  from {
    opacity: 1;
    transform: translateY(0);
  }
  to {
    opacity: 0;
    transform: translateY(-20px);
  }
}

/* ============================================================================
 * VARIANTS
 * ============================================================================ */

.info .icon {
  color: var(--status-info);
}

.success .icon {
  color: var(--status-success);
}

.warning .icon {
  color: var(--status-warning);
}

.error .icon {
  color: var(--status-error);
}

/* ============================================================================
 * CONTENT
 * ============================================================================ */

.icon {
  flex-shrink: 0;
  width: 20px;
  height: 20px;
}

.content {
  flex: 1;
  min-width: 0;
}

.title {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-semibold);
  color: var(--text-primary);
  margin-bottom: var(--spacing-1);
}

.message {
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  color: var(--text-secondary);
  line-height: var(--line-height-relaxed);
}

/* ============================================================================
 * BUTTONS
 * ============================================================================ */

.action {
  flex-shrink: 0;
  padding: var(--spacing-1) var(--spacing-2);
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  font-family: var(--font-secondary);
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-medium);
  color: var(--text-brand);
  cursor: pointer;
  transition: background-color var(--transition-fast);

  &:hover {
    background: var(--surface-subtle);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }
}

.closeButton {
  flex-shrink: 0;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  color: var(--text-tertiary);
  cursor: pointer;
  transition:
    background-color var(--transition-fast),
    color var(--transition-fast);
  margin: calc(var(--spacing-1) * -1) calc(var(--spacing-1) * -1) calc(var(--spacing-1) * -1) 0;

  &:hover {
    background: var(--surface-subtle);
    color: var(--text-primary);
  }

  &:focus-visible {
    outline: 2px solid var(--interactive-primary);
    outline-offset: 2px;
  }
}

/* ============================================================================
 * REDUCED MOTION
 * ============================================================================ */

@media (prefers-reduced-motion: reduce) {
  .toast,
  .exiting {
    animation: none;
  }
}

/* ============================================================================
 * RESPONSIVE
 * ============================================================================ */

@media (max-width: 480px) {
  .container {
    max-width: 100%;
    padding: var(--spacing-3);
  }

  .toast {
    padding: var(--spacing-3);
  }
}

/**
 * Toast Component - Temporary notification messages.
 *
 * @example
 * ```tsx
 * import { ToastProvider, useToast, Button } from '@orion-ds/react';
 *
 * // Wrap app with ToastProvider
 * <ToastProvider>
 *   <App />
 * </ToastProvider>
 *
 * // Use in any component
 * function MyComponent() {
 *   const { toast } = useToast();
 *
 *   return (
 *     <Button onClick={() => toast({ title: 'Saved!', variant: 'success' })}>
 *       Save
 *     </Button>
 *   );
 * }
 * ```
 */
export { ToastProvider, useToast } from './Toast';
export type {
  Toast,
  ToastOptions,
  ToastVariant,
  ToastPosition,
  ToastProviderProps,
  ToastContextValue,
} from './Toast.types';
