/**
 * Tooltip Component
 *
 * Hover information tooltip with multiple placements.
 *
 * @example
 * ```tsx
 * <Tooltip content="Click to edit">
 *   <Button icon={<EditIcon />} />
 * </Tooltip>
 *
 * <Tooltip content="Delete item" placement="right">
 *   <Button variant="danger" icon={<TrashIcon />} />
 * </Tooltip>
 * ```
 */

import React, { useState, useRef, cloneElement, isValidElement } from "react";
import type { TooltipProps } from "./Tooltip.types";
import styles from "./Tooltip.module.css";

export const Tooltip: React.FC<TooltipProps> = ({
  content,
  placement = "top",
  delay = 200,
  disabled = false,
  children,
  className,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const timeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);

  const handleMouseEnter = () => {
    if (disabled) return;

    timeoutRef.current = setTimeout(() => {
      setIsVisible(true);
    }, delay);
  };

  const handleMouseLeave = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setIsVisible(false);
  };

  const handleFocus = () => {
    if (disabled) return;
    setIsVisible(true);
  };

  const handleBlur = () => {
    setIsVisible(false);
  };

  const tooltipClasses = [styles.tooltip, styles[placement], className]
    .filter(Boolean)
    .join(" ");

  // Clone child element and add event handlers
  const childElement = isValidElement(children)
    ? cloneElement(children as React.ReactElement<any>, {
        onMouseEnter: handleMouseEnter,
        onMouseLeave: handleMouseLeave,
        onFocus: handleFocus,
        onBlur: handleBlur,
      })
    : children;

  return (
    <div className={styles.container}>
      {childElement}

      {isVisible && !disabled && (
        <div className={tooltipClasses} role="tooltip">
          {content}
          <div className={styles.arrow} />
        </div>
      )}
    </div>
  );
};

Tooltip.displayName = "Tooltip";

/**
 * Tooltip Component Types
 *
 * Type definitions for the Orion Tooltip component.
 */

import type { ReactNode } from "react";

/**
 * Tooltip placement
 */
export type TooltipPlacement = "top" | "right" | "bottom" | "left";

/**
 * Tooltip component props
 *
 * @example
 * ```tsx
 * <Tooltip content="Click to edit">
 *   <Button icon={<EditIcon />} />
 * </Tooltip>
 * ```
 */
export interface TooltipProps {
  /**
   * Tooltip content
   */
  content: ReactNode;

  /**
   * Tooltip placement
   * @default 'top'
   */
  placement?: TooltipPlacement;

  /**
   * Delay before showing (ms)
   * @default 200
   */
  delay?: number;

  /**
   * Element to attach tooltip to
   */
  children: ReactNode;

  /**
   * Disable tooltip
   * @default false
   */
  disabled?: boolean;

  /**
   * Additional class name
   */
  className?: string;
}

/**
 * Tooltip Component Styles
 *
 * Hover information tooltip.
 */

/* Component-level tokens */
.container {
  --tooltip-arrow-size: var(--spacing-2);
  --tooltip-arrow-offset: calc(var(--tooltip-arrow-size) / 2);
  --tooltip-gap: var(--spacing-2);
}

/* Container */
.container {
  position: relative;
  display: inline-flex;
}

/* Tooltip */
.tooltip {
  position: absolute;
  z-index: var(--z-tooltip);
  padding: var(--spacing-2) var(--spacing-3);
  background: var(--text-primary);
  color: var(--text-inverse);
  font-size: var(--font-size-12);
  font-weight: var(--font-weight-medium);
  line-height: var(--line-height-tight);
  border-radius: var(--radius-sm);
  white-space: nowrap;
  box-shadow: var(--shadow-md);
  pointer-events: none;
  animation: tooltipFadeIn 150ms ease;
}

@keyframes tooltipFadeIn {
  from {
    opacity: 0;
    transform: var(--tooltip-transform-from);
  }
  to {
    opacity: 1;
    transform: var(--tooltip-transform-to);
  }
}

/* Arrow */
.arrow {
  position: absolute;
  width: var(--tooltip-arrow-size);
  height: var(--tooltip-arrow-size);
  background: var(--text-primary);
  transform: rotate(45deg);
}

/* ============================================================================
 * PLACEMENTS
 * ============================================================================ */

/* Top placement */
.top {
  --tooltip-transform-from: translateX(-50%)
    translateY(var(--tooltip-arrow-offset));
  --tooltip-transform-to: translateX(-50%) translateY(0);
  bottom: calc(100% + var(--tooltip-gap));
  left: 50%;
  transform: translateX(-50%);
}

.top .arrow {
  bottom: calc(var(--tooltip-arrow-offset) * -1);
  left: 50%;
  transform: translateX(-50%) rotate(45deg);
}

/* Bottom placement */
.bottom {
  --tooltip-transform-from: translateX(-50%)
    translateY(calc(var(--tooltip-arrow-offset) * -1));
  --tooltip-transform-to: translateX(-50%) translateY(0);
  top: calc(100% + var(--tooltip-gap));
  left: 50%;
  transform: translateX(-50%);
}

.bottom .arrow {
  top: calc(var(--tooltip-arrow-offset) * -1);
  left: 50%;
  transform: translateX(-50%) rotate(45deg);
}

/* Left placement */
.left {
  --tooltip-transform-from: translateY(-50%)
    translateX(var(--tooltip-arrow-offset));
  --tooltip-transform-to: translateY(-50%) translateX(0);
  right: calc(100% + var(--tooltip-gap));
  top: 50%;
  transform: translateY(-50%);
}

.left .arrow {
  right: calc(var(--tooltip-arrow-offset) * -1);
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
}

/* Right placement */
.right {
  --tooltip-transform-from: translateY(-50%)
    translateX(calc(var(--tooltip-arrow-offset) * -1));
  --tooltip-transform-to: translateY(-50%) translateX(0);
  left: calc(100% + var(--tooltip-gap));
  top: 50%;
  transform: translateY(-50%);
}

.right .arrow {
  left: calc(var(--tooltip-arrow-offset) * -1);
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  .tooltip {
    animation: none;
  }
}
