import { useState, forwardRef } from "react";
import { Building2, ChevronDown, Settings, Users, Plus } from "lucide-react";
import { Popover } from "../../components/Popover";
import { Avatar } from "../../components/Avatar";
import { Badge } from "../../components/Badge";
import { Button } from "../../components/Button";
import { Divider } from "../../components/Divider";
import styles from "./WorkspaceSwitcher.module.css";
import type { WorkspaceSwitcherProps } from "./WorkspaceSwitcher.types";

export const WorkspaceSwitcher = forwardRef<HTMLDivElement, WorkspaceSwitcherProps>(
  (
    {
      currentOrg,
      organizations,
      onSettings,
      onParticipants,
      onCreateOrganization,
      onOrgChange,
      fullWidth = true,
      className,
      ...rest
    },
    ref,
  ) => {
    const [isOpen, setIsOpen] = useState(false);

    const trigger = (
      <button className={styles.trigger}>
        <Avatar size="xs" icon={<Building2 size={16} />} />
        <span className={styles.triggerName}>{currentOrg.name}</span>
        <ChevronDown
          size={16}
          className={`${styles.chevron} ${isOpen ? styles.chevronOpen : ""}`}
        />
      </button>
    );

    const content = (
      <div className={styles.content}>
        {/* Active workspace */}
        <div className={styles.activeWorkspace}>
          <Avatar size="lg" icon={<Building2 size={20} />} />
          <Badge variant="secondary" size="sm">{currentOrg.role}</Badge>
          <div>
            <div className={styles.orgName}>{currentOrg.name}</div>
            <div className={styles.orgMeta}>{currentOrg.memberCount} participantes</div>
          </div>
        </div>

        {/* Action buttons */}
        <div className={styles.actionButtons}>
          <Button variant="secondary" size="sm" icon={<Settings size={16} />} onClick={onSettings}>
            Configuraci贸n
          </Button>
          <Button variant="secondary" size="sm" icon={<Users size={16} />} onClick={onParticipants}>
            Participantes
          </Button>
        </div>

        {/* Divider */}
        <div className={styles.dividerWrapper}>
          <Divider />
        </div>

        {/* Other orgs label */}
        <div className={styles.sectionLabel}>Otras organizaciones</div>

        {/* Org list */}
        <div className={styles.orgList}>
          {organizations.map((org) => (
            <div
              key={org.name}
              className={styles.orgItem}
              onClick={() => onOrgChange?.(org.name)}
            >
              <Avatar size="xs" initials={org.name.slice(0, 2).toUpperCase()} />
              <div>
                <div className={styles.orgItemName}>{org.name}</div>
                <div className={styles.orgItemRole}>{org.role}</div>
              </div>
            </div>
          ))}
        </div>

        {/* Footer */}
        <Button
          variant="ghost"
          size="sm"
          icon={<Plus size={16} />}
          fullWidth
          onClick={onCreateOrganization}
        >
          Nueva organizaci贸n
        </Button>
      </div>
    );

    return (
      <div
        ref={ref}
        className={[className].filter(Boolean).join(" ") || undefined}
        {...rest}
      >
        <Popover
          trigger={trigger}
          content={content}
          onOpenChange={setIsOpen}
          placement="bottom-start"
          showArrow={false}
          fullWidth={fullWidth}
        />
      </div>
    );
  },
);

WorkspaceSwitcher.displayName = "WorkspaceSwitcher";

import type { HTMLAttributes } from "react";

export interface WorkspaceOrg {
  /** Organization display name */
  name: string;
  /** User's role in this organization */
  role: string;
}

export interface CurrentWorkspaceOrg extends WorkspaceOrg {
  /** Number of members */
  memberCount: number;
}

export interface WorkspaceSwitcherProps
  extends Omit<HTMLAttributes<HTMLDivElement>, "children"> {
  /** The currently active organization */
  currentOrg: CurrentWorkspaceOrg;
  /** List of other organizations to switch to */
  organizations: WorkspaceOrg[];
  /** Called when Settings button is clicked */
  onSettings?: () => void;
  /** Called when Participants button is clicked */
  onParticipants?: () => void;
  /** Called when "Nueva organizaci贸n" button is clicked */
  onCreateOrganization?: () => void;
  /** Called when user clicks another organization to switch to */
  onOrgChange?: (orgName: string) => void;
  /**
   * Whether the trigger stretches to full width.
   * @default true
   */
  fullWidth?: boolean;
}

/* Trigger button */
.trigger {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-3);
  border: none;
  border-radius: var(--radius-control);
  background: transparent;
  color: var(--text-primary);
  font-family: var(--font-secondary);
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-medium);
  cursor: pointer;
  width: 100%;
  box-sizing: border-box;
  text-align: left;
  transition: background-color var(--mode-hover-duration);
}

.trigger:hover {
  background: var(--surface-subtle);
}

.triggerName {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

/* Chevron animation */
.chevron {
  flex-shrink: 0;
  color: var(--text-tertiary);
  transition: transform var(--mode-hover-duration);
}

.chevronOpen {
  transform: rotate(180deg);
}

/* Popover content */
.content {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-3);
  min-width: 260px;
}

/* Active workspace header */
.activeWorkspace {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--spacing-2);
  padding-top: var(--spacing-3);
  padding-bottom: var(--spacing-3);
  text-align: center;
}

.orgName {
  font-weight: var(--font-weight-semibold);
  font-size: var(--font-size-16);
}

.orgMeta {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
}

/* Action buttons */
.actionButtons {
  display: flex;
  gap: var(--spacing-2);
}

/* Divider with reduced spacing */
.dividerWrapper {
  margin: calc(-1 * var(--spacing-1)) calc(-1 * var(--spacing-4));
}

/* Section label */
.sectionLabel {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
  font-weight: var(--font-weight-medium);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

/* Org list */
.orgList {
  max-height: 300px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.orgItem {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2);
  border-radius: var(--radius-control);
  cursor: pointer;
  transition: background-color var(--transition-fast);
}

.orgItem:hover {
  background-color: var(--surface-layer);
}

.orgItemName {
  font-size: var(--font-size-14);
  font-weight: var(--font-weight-medium);
}

.orgItemRole {
  font-size: var(--font-size-12);
  color: var(--text-tertiary);
}

export { WorkspaceSwitcher } from "./WorkspaceSwitcher";
export type {
  WorkspaceSwitcherProps,
  WorkspaceOrg,
  CurrentWorkspaceOrg,
} from "./WorkspaceSwitcher.types";
