// Fonts load automatically - no FontLoader needed!
import { ThemeProvider } from '@orion-ds/react';

function App() {
  return (
    <ThemeProvider>
      <YourComponents />
    </ThemeProvider>
  );
}

// Only use if you disabled auto-loading
<ThemeProvider disableAutoFontLoading>
  <FontLoader
    onLoad={() => console.log('Fonts ready!')}
    onError={(err) => console.error(err)}
  />
  <App />
</ThemeProvider>
